css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

a{
  color: #11E !important;
}

body{
 background: #EEE none !important;
 color: #333 !important;
}
a:not(:hover) .numeric,
#side .user_icon a:not(:hover),
#new_results_notification a:hover {
 color: #333 !important;
}
#new_results_notification a:not(:hover){
 color: #444 !important;
}
.meta a {
 color: inherit !important;
}

.list-header, .list-controls {
 background-color: #E0FFB3 !important;
 border-bottom: 2px solid transparent !important;
}

#logo, #footer, #currently, div.content-bubble-arrow, #content .bar .doing .thumb, #content .bar .doing, #side > .promotion, .shared-content
{ display:none !important; }

/* header */
#chars_left_notice{ /* char count */
 left: 0.5em !important;
 top: -0.5em !important;
}
#header > ul.top-navigation{
 position: absolute;
 top: -0.5em !important;
 right: 0;
 min-width: 40ex;
 min-height: 0.75em;
 margin-top: 0;
 background: white !important;
 z-index: 1;
}
#header:not(:hover) > ul.top-navigation > li{
 display: none !important;
}
#header:hover > ul.top-navigation> li{
 display: inline !important;
 z-index: 2;
}

/* statuses */
.statuses li{
 padding: 0 !important;
}
.statuses .entry-meta{
 display: inline !important;
}
.hentry:not(:hover) .entry-meta{
 visibility: hidden !important;
}
.hentry .author:not(.thumb-inline) .photo {
 float: right !important;
 height: 36px !important;
 width:  36px !important;
}
.hentry .shared-by-avatar-tiles {
 height: auto !important;
 line-height: 100% !important;
}
.hentry .author.thumb-inline .photo {
 height: 16px !important;
 width:  16px !important;
}
.hentry:hover .entry-meta,
.hentry:hover .photo{
 visibility: visible !important;
}
.statuses .thumb{
 padding: 0 !important;
}
.statuses .actions > div{
 top: 0 !important;
}
.statuses .shared-by-avatar-tiles {
 text-align: right;
}

/* side */
#side_base{
 background-color: #E0FFB3 !important;
}
#side_base *, #side_base {
 border-color: #87BC44 !important;
}
#side > hr {
 background-color: #87BC44 !important;
}
.sidebar-menu > li > a:hover, .sidebar-menu > li.active > a {
 background: white !important;
}
}
