body {
    margin-top: 0 !important;
    padding-top: 0 !important;
    min-width: 800px !important;
}
body {
    color: #333;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/www-refreshbg-vflC3wnbM.png) 0 0;
    background-color: #ebebeb;
    background-repeat: repeat
}
body,
button,
input,
textarea,
select {
    font: 12px arial, sans-serif;
}
#masthead-logo img,
#yt-masthead #logo-container .logo,
.footer-logo img,
#footer-logo .footer-logo-icon,
span.fg-eh-hj.a-b-c.fg-ph-Qc-Wd {
    background: url(http://vignette1.wikia.nocookie.net/logopedia/images/9/98/YouTube_Logo.svg/revision/latest/scale-to-width-down/250?cb=20130406142412) 0 no-repeat!important;
    background-size: contain!important;
    opacity: 1 !important;
    transition: opacity .3s !important;
}
.yt-subscription-button .subscribe-label,
.yt-subscription-button .subscribed-label,
.yt-subscription-button .unsubscribe-label {
    display: block;


}
.video-extras-sparkbar-likes {
    float: left;
    height: 4px;
    border-right: 1px solid #fff;
    background: #060;
}
.video-extras-sparkbars {
    height: 4px;
    margin: 2px 0;
    border: 1px solid #ccc;
    overflow: hidden;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
}
.video-extras-sparkbar-dislikes {
    float: right;
    height: 4px;
    margin-right: -1px;
    background: #c00;
}





.yt-card {
    margin: 0 0 10px;
    border: 0;
    background: transparent;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0);
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}
#yt-masthead-container {
    background: transparent;
    border-bottom: 0px solid #e8e8e8;
    min-width: 0;
    padding-bottom: 8px;
    padding-top: 7px;
    position: relative;
}
#masthead-search .search-btn-component {
    height: 29px;
}
#masthead-search .search-btn-compontent,
#masthead-search .search-btn-compontent .start {
    border-left: 0;
    -moz-border-radius-topleft: 0;
    -webkit-border-top-left-radius: 0;
    border-top-left-radius: 0;
    -moz-border-radius-bottomleft: 0;
    -webkit-border-bottom-left-radius: 0;
    border-bottom-left-radius: 0;

}

.appbar-guide-menu-layout {
    position: fixed;
    width: 230px;
    top: 0;
    visibility: visible;
    left: -230px;
    opacity: 0;
    -moz-transition: opacity .1s, visibility 0s .1s;
    -webkit-transition: opacity .1s, visibility 0s .1s;
    transition: opacity .1s, visibility 0s .1s;
}
.show-guide .appbar-guide-menu-layout {
    visibility: visible !important;
    left: 0;
    opacity: 1;
    -moz-transition: opacity .1s;
    -webkit-transition: opacity .1s;
    transition: opacity .1s;
}


#watch-headline h1 .long-title {
    font-size: .9166em;
    letter-spacing: -0.5px;
}
.yt-thumb-clip img,
.yt-thumb-clip .vertical-align {
    visibility: visible;
}
.yt-thumb-clip {
    visibility: hidden;
}
#yt-masthead #logo-container .logo {
    width: 100px;
    height: 40px;


}
#masthead-container {
    position: relative;
    z-index: 1;
    margin: 0;
    border-bottom: none;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/www-refreshbg-vflC3wnbM.png) 0 0;
    background-color: #ebebeb;
    background-repeat: repeat;
}
#masthead {
    position: relative;
    overflow: hidden;
    padding: 10px 0;
    margin: 0 auto;
    height: 40px;
    font-size: 13px;
}
#masthead,
#masthead-subnav {
    width: 970px;
}
#masthead-search-terms {
    position: relative;
    overflow: hidden;
    height: 32px;
    margin: 0 0 2px;
    _padding-left: 5px;
    _padding-right: 5px;
    font-size: 14px;
    line-height: 30px;
    background-color: #fff;
    -moz-box-sizing: border-box;
    -ms-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    -moz-border-radius-topleft: 3px;
    -webkit-border-top-left-radius: 3px;
    border-top-left-radius: 3px;
    -moz-border-radius-bottomleft: 3px;
    -webkit-border-bottom-left-radius: 3px;
    border-bottom-left-radius: 3px;
    -moz-transition: border-color .2s ease;
    -ms-transition: border-color .2s ease;
    -o-transition: border-color .2s ease;
    -webkit-transition: border-color .2s ease;
    transition: border-color .2s ease;
}
#masthead-search .search-btn-compontent {
    float: right;
    height: 32px;
    padding: 0 .91em;
    opacity: 1;
    filter: none;
    _margin-left: -3px;
}
#masthead-search .search-btn-component {
    height: 32px;
}
#masthead-search .search-btn-component .yt-uix-button-content {
    border: none;
    display: block;
    opacity: .6;
    padding: 0;
    text-indent: -10000px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/www-refresh-vflMLqC23.png) -285px -105px;
    background-size: auto;
    width: 14px;
    height: 14px;
    -moz-box-shadow: none;
    -ms-box-shadow: none;
    -webkit-box-shadow: none;
    box-shadow: none;
}
#masthead-search-terms input {
	width: 379px;
    height: 30px;
}
#masthead-search-terms .masthead-search-terms-border {
	width: 379px;
	height: 30px;                                      
}
#appbar-nav {
    visibility: hidden;
}
#appbar-content {
    visibility: hidden;
}
.page-loaded #masthead-appbar {
    visibility: hidden;
}
.yt-lockup .yt-uix-button-action-menu, .yt-lockup .yt-uix-button-lockup-action-menu, .video-list-item .related-item-action-menu .yt-uix-button {
    visibility: visible;
}
