css:
@-moz-document domain("schema.mah.se") {
 body {
     background-color: #1A1A1A;
 }
 .schemaTabell td {
     border-color: #353434;
 }
 .data-white,
 input,
 select,
 #loginform input {
     background-color: #212020;
 }
 .data-grey,
 .box-bakgrund,
 #visaschema_knapp span,
 #login_button {
     background-color: #101010;
 }
 td,
 a:link,
 a:visited,
 a:hover,
 a:active,
 div {
     color: #2D95FF;
 }
 img,
 th {
     display: none;
 }
 .vecka {
     color: #3298FF;
 }
}
