css:
@-moz-document domain("boyfriend.dk") {
/*basic style for boyfriend.dk*/
:root {
 	--grid-point: 8px;  
    font-size: 14px;
}
:root {
    --root-color: rgb(241, 241, 241);
    --text-color: #414042;
    --primary-color: #8DA1B9;
    --primary-text: white;
    --shade-one: #c0cad3;
    --shade-two: #EEF3F6;
    --color-red: #c70000;
}
:root {/*online indicator colors*/
    --color-online: #29a329;
    --color-away: #999900;
    --color-offline: #b3b3b3;
}


body,
.body {
    background-color: var(--root-color) !important;
    font-size: 14px;
}
body #toprowcontainer .toprow {
    background-color: var(--primary-color);
}
textarea {
    box-sizing: border-box;
    border: 1px solid !important;
}
input[type="text"],
input[type="password"] {
    height: 20px !important;
    padding: 1px 4px;
    border: 1px solid var(--shade-one) !important;
}
select[size="1"] {
    height: 24px;
}
tr td,
body div,
body p {
    font-size: 1em;
}
body .m1,
body .m2,
body .m3,
body .line_m1,
body .line_m2,
body .line_m3{
    line-height: initial;
}
body .m1,
body .line_m1,
table td[style*="background-color: #EEF3F6"],
div[style*="background-color: #EEF3F6"] {
    background-color: var(--shade-two) !important;
    background-image: none;
}
body .m3 {
    background-color: var(--shade-one);
}
body #report_container {
    background-color: var(--shade-one) !important;
    background-image: none;
}
td[style*="background-color: #C5D2DA"],
div[style*="background-color: #C5D2DA"],
.lysbg{
    background-color: var(--shade-one) !important;
}
div[style*="boyfriend.dk/static/themes/1/profil/home/lysbg.gif)"],
td[style*="boyfriend.dk/static/themes/1/profil/home/lysbg.gif)"]{
    background-color: var(--shade-two) !important;
    background-image: none !important;
}

}

@-moz-document domain("boyfriend.dk") {
/*Top banner and right side further down*/
/*Top banner*/
#toprowcontainer,
.condiv {
    width: calc(100% - 8*var(--grid-point));
}
.content iframe {
    width: 100%;
}
#toprowcontainer .toprow {
    background-image: none;
    height: 64px;
    width: 100%;
}
#toprowcontainer .toprow a[target="_top"] {
    color: var(--primary-text);
    text-decoration: none;
}
#toprowcontainer .toprow a[target="_top"]:after {
    content: 'Boyfriend.dk';
    font-size: x-large;
}
#toprowcontainer .toprow img {
    display: none;
}
#menu {
    background-color: var(--shade-one);
    height: 40px;
    width: 100%
}
#menu ul {
    top: 64px;
}
body #menu ul li { 
    font-size: inherit;
}
#menu>ul>li {
    font-size: 1em;
    padding: 12px 0 !important;
}
body #menu>ul>li:hover {
    background-image: none !important;
    border-top: 4px solid white;
    padding: 8px 0 !important;
}
#menu>ul>li>div {
    height: initial;
    padding: initial !important;
}
#menu ul li ul {
    top: 32px;
}
body #menu ul li:hover ul li {
    background-color: var(--shade-two);
    top: 8px !important;
}
body #menu ul li ul li:hover {
    background-color: var(--shade-one);
}
#menu ul li a {
    font-size: 1em;
}
#menu .status_bar {
    background-color: var(--primary-color);
    border: 1px solid;
    font-size: 1em;
    line-height: 20px;
    height: 48px !important;
    width: 208px;
}
#menu .status_bar .status_search {
    margin-top: 4px;
    width: 174px;
}
form #search_suggest {
	left: -101px !important;
    top: 64px !important;
}
#searchResult td:nth-of-type(5) {
    width: 96px !important;
}
#searchResult td div.ll {
	width: 100% !important;
}

#menu .status_bar img {
    margin-top: 6px;
    margin-left: 4px;
}
#menu div#abonnementsboks.status_bar {
    width: initial;
}
#topmenu_right {
    left: inherit;
    right: 8px;
    top: 72px;
}
#newstickercontainer {
    left: inherit;
    right: 8px;
    width: 256px;
}
#newsticker {
    width: 100%;
}
/*Right side status bar*/
#status {
    background-color: var(--shade-one);
    color: var(--text-color);
    font-size: 1em;
    left: initial;
    right: 0;
    top: 0;
    width: 222px;
}

#status .status_bar_top {
    background-image: none;
    border-bottom: 1px solid white;
    color: inherit;
    font-size: inherit;
    padding-top: 12px;
    height: 28px;    
    width: 100%;
}
#statusbar_edit #statusbar_vis {
    width: 180px;
}
#statusbar_edit #statusbar_vis p,
#statusbar_edit #statusbar_vis input {
    font-size: 1em;
    height: inherit;
}
#status .status_bar {
    background-color: var(--shade-one);
    box-sizing: border-box;
    color: inherit;
    font-size: inherit;
    width: 100%;
}
#item_seneste {
    height: 96px;
}
#item_seneste span,
#item_seneste div {
    font-size: x-small !important;
}
#status .events_container_tekst a,
#status .events_container_link a,
#status .events_container_link {
    color: inherit;
    font-size: small;
}
#status .events_container div a::after {
    content: '<br>';
}
#status .pr {
    color: inherit;
    font-size: 1em;
}
#status .status_header {
    color: inherit;
    font-size: 1em;
    padding-left: 20px !important;
}
div#favorite_container,
div#guest_container {
    margin-top: 8px;
}

#status .status_online {
    background-color: var(--shade-one);
    border-bottom: 1px solid white;
    border-top: 1px solid white;
    box-sizing: border-box;
    font-size: inherit;
    width: 100%;
}
#status .status_overskrift {
    background-image: none;
    color: inherit;
    font-size: 1em;
    width: 100%;
    text-transform: uppercase;
}
#status .status_bar_chat {
    background-color: inherit;
    border-color: white;
    box-sizing: border-box;
    height: 32px;
    width: 100%;
}
#status .status_bar_chat div {
    display: inline !important;
    position: initial !important;
    padding-right: 8px;
}
#status .status_bar_chat a {
    color: inherit !important;
}
#status #status_chat_content {
    font-size: .85em !important;
}
#status #status_chat_content a {
    color: var(--text-color) !important;
}
div[style*="width: 140px"] {
    width: 100% !important;
}
img[src$="s.boyfriend.dk/static/themes/1/status/bund.gif"] {
    display: none;
}
}

@-moz-document domain("boyfriend.dk") {
/*content*/
#toprowcontainer+.condiv {
    top: 8px;
}
body .content {
	width: calc(100% - 222px);
}

#outer,
.innerdiv {
    background-color: white;
}
#menu_sub {
    height: 40px;
}
body #menu_sub ul {
    height: inherit;
}
body #menu_sub ul li {
    background-color: var(--shade-one);
}
#menu_sub ul li .text {
    font-size: 1em;
    margin: 0;
    padding: 10px 16px;
}
#menu_sub ul li .count {
    background-color: white;
    margin-top: 8px;
    margin-left: 0;
    border-radius: 20px;
    line-height: 24px;
    padding: 0;
    height: 24px;
    min-width: 24px;
}
#menu_sub li:first-child {
    border-radius: 0 !important;
}
/*hjem*/
body div[style*="width: 422px"]>div {
    background-color: var(--shade-one) !important;
}
div[style*="width: 422px"] h1+div div + div div {
    /*optag video*/
    font-size: x-small;
    margin-top: 4px;
    padding-top: 4px !important;
}
div[style="height: 290px"]+div .mailheader div {
    padding: 0 !important;
    padding-right: 8px !important;
    width: initial !important;
}
/*guest paging*/
body div[style="height: 290px"] {
	position: relative;
}
body div[style="height: 290px"] div {
    position: initial !important;
}
body div[style="height: 290px"]>div+div>div>div  {
    display:none;
    position: absolute !important;
    right: 0;
}
body div[style="height: 290px"]>div+div>div>div div a {
	text-decoration: none;
}
body div[style="height: 290px"]>div+div>div>div div a::after {
    background-color: var(--shade-two);
    line-height: 20px;
    padding: 2px 4px;
}
body div[style="height: 290px"]>div+div>div>div div:first-of-type a::after {
    border-radius: 10px 0 0 10px;
    content: '←';
    padding: 2px 0 2px 8px;
}
body div[style="height: 290px"]>div+div>div>div div:last-of-type a::after {
    border-radius: 0 10px 10px 0;
    content: '→';
    padding: 2px 8px 2px 0;
}
body div[style="height: 290px"]>div+div>div>div div img {
    display: none;
}
body .innerdiv {
    padding: 16px !important;
}
body div#fact_container {
    background-color: white;
    height: 112px;
}
body #fact_container,
body #fact_container #boundingbox #left {
    margin: initial;
    width: initial;
}
body #fact_container #boundingbox_long {
    background-color: var(--shade-two);
    margin: 0 2px 4px 2px;
    width: calc(50% - 8px);
}
body #fact_container #boundingbox {
    background-color: var(--shade-two);
    margin: 0 2px 4px 2px;
    width: calc(25% - 8px);
}
/*profile text*/
div[style^="width: 727px; overflow:hidden; font-size: 10pt;"] {
    background-color: white;
    font-size: inherit !Important;
	width: 100% !important;
}
/*restyling of buttons*/
body div[style*="s.boyfriend.dk/static/themes/1/common/knap/middle.png)"] {
    background-color: var(--primary-color);    
    background-image: none !important;
    border-radius: 12px;
    color: var(--primary-text);
    font-size: 1em;
    padding: 4px 12px !important;
    width: inherit !important;
}
img[src$="common/knap/left.png"],
img[src$="common/knap/right.png"] {
    display: none;
}
body .mail_send_button {
    background-color: var(--primary-color);
    color: var(--primary-text);
    padding-top: 12px;
}
/*chat*/
#outer #menu_sub + script + div.innerdiv#chat_filter {
    padding-bottom: 0 !important;
}
div .home_link {
    float: left;
    margin-right: 8px;
}

div .qsort_optionbox {
    padding: 0;
}
.qsort_optionbox div:nth-of-type(2) {
    background-color: var(--shade-one);
    border-radius: 2px;
    color: var(--text-color);
    margin-right: 4px;
    padding: 4px 8px !important;
}
.qsort_optionbox .qsort_optionbox_options {
	background-color: var(--shade-two);
}

form[action^="/chat/"] div[style="padding: 5px; float: right"] {
    padding: 0 !important;
}
body .mailheader {
    background-color: var(--shade-one);
    border-bottom: 4px solid white;
    font-size: 1em !important;
}
.mailheader[style*="height: 47px"] {
    height: 64px !important;
}

div[style="width: 720px; overflow:hidden;"],
table[width="720"] {
	width: 100% !important;
}
table[width="720"] td:first-of-type,
div[style="word-wrap: break-word; width: 125px"] {
    width: 200px !important;
}
div[style="word-wrap: break-word; width: 565px"] {
	width: 100% !important;
}

iframe[src^="/chat/content.php?room=scor"]+script +style+div.innerdiv {
    padding-top: 0 !important;
}

/*input felter*/
/*
#outer div:nth-of-type(4) .innerdiv div:nth-of-type(1) {
    width: inherit !important;
}
#outer div:nth-of-type(4) .innerdiv div:nth-of-type(2) {
    float: right !important;
    width: inherit !important;
}
*/
.gradientbg.clear {
    background-image: none;
}
.gradientbg.clear .innerdiv {
    background-color: var(--shade-one);
    margin: 0 16px;
    padding: 8px;
}
.gradientbg.clear .innerdiv .bold {
    font-size: 1em;
    height: 24px;
}
/*pagers*/
body #pager_text {
    background-color: var(--shade-two);
    background-image: none;
    height: 24px;
    line-height: 20px;
    width: inherit;
}

body #pager_selection {
    background-color: var(--shade-two);
    height: initial;
    width: inherit;
    left: 120px;
	top: -96px;
}

.pager_container .pager,
.pager_container .pager_valgt {
    background-image: none;
    border: 1px solid grey;
    border-radius: 10px;
    cursor: default;
    height: 20px;
    line-height: 20px;
    padding: 0;
    width: 20px;
}
.pager_container .pager_valgt {
    background-color: var(--primary-color);
    color: white;
}
.pager_container div.pager[onclick] {
    cursor: pointer;
}
.pager_container div.pager[onclick] img { 
    display: none;
}
body .pager_container .pager[onclick]:first-of-type::after {
    content: '⇤';
}
body .pager_container .pager[onclick]:nth-of-type(2)::after {
    content: '←';
}
body .pager_container .pager[onclick]:nth-last-of-type(2)::after {
    content: '→';
}
body .pager_container .pager[onclick]:last-of-type::after {
    content: '⇥';
}

}

