css:
@-moz-document domain("www.romhacking.net") {
body, div#wrap, #footer_section, #footer, blockquote.bbc_standard_quote, #top, .topbar, .topbar2, #header, div.cat_bar, .table_grid tr.catbg, .table_grid tr.titlebg, div.title_barIC, div.title_bar, h3.catbg a:link, h3.catbg a:visited, h4.catbg a:link, h4.catbg a:visited, h3.catbg, .table_list tbody.header td, .table_list tbody.header td a, .newsitem, .news, .breadCrumb, .lastpost p, .side, #topforms ul, ul#topforms, .codeheader, .quoteheader, blockquote.bbc_alternate_quote, blockquote.bbc_standard_quote, .description, .description_board, .plainbox, span.upperframe, .roundframe, span.lowerframe, #search_form .input_text, #search_form select, #search_form .button_submit, .button_reset, textarea, input, .windowbg, #preview_body, #header .dropmenu li ul, .windowbg, .boardbg, .side, th, tbody tr td
{ background-color: #000; }
.entrymenu a, form.filter .submit input, form.general_submission .submit input, #btnG, #stylechange
{ background: #000; }
.entrymenu a:hover, form.filter .submit input:hover, form.general_submission .submit input:hover, #btnG:hover, #stylechange:hover, select, input[type="text"]
{ background: #203; }
.stickybg, .windowbg2, .dropmenu li li:hover, .adm_section .dropmenu li li:hover, tbody tr.odd  td, .entryinfo td
{ background-color: #203; }
.lockedbg
{ background-color: #302; }
hr, hr.hrcolor
{ background-color: #0F0; }
#top, .topbar, .topbar2, #header, div.cat_bar, .table_grid tr.catbg, .table_grid tr.titlebg, div.title_barIC, div.title_bar, h3.catbg a:link, h3.catbg a:visited, h4.catbg a:link, h4.catbg a:visited, h3.catbg, .table_list tbody.header td, .table_list tbody.header td a, a:hover, a:active, .dropmenu li a:hover, .dropmenu li a.active, #footer a:hover, #footer a:active, .poster h4, .poster h4 a, .breadCrumb ul li a:hover, .dropmenu li:hover a, caption
{ color: #B0F !important; }
.show-menu, #navbar a, .info div span a, .info .subject, .preview a, div.title_barIC h4.titlebg, a:link, a:visited, .dropmenu li a, #footer a:link, #footer a:visited, .breadCrumb ul li a, .tablecol th a:link, .tablecol th a:visited
{ color: #0F0; }
body, .newsitem, .news, .breadCrumb, .lastpost p, .side, #topforms ul, ul#topforms, .codeheader, .quoteheader, blockquote.bbc_alternate_quote, blockquote.bbc_standard_quote, .description, .description_board, .plainbox, span.upperframe, .roundframe, span.lowerframe, #search_form .input_text, #search_form select, #search_form .button_submit, .button_reset, textarea, input, p, .news p, .breadCrumb ul li.last a, .side, .stickybg, .windowbg2, .windowbg, #preview_body, #header .dropmenu li ul, .entryinfo tr td, .entryinfo th, table, th, td, select, input[type="text"]
{ color: #FFF; }
strong
{ color: inherit; }
.newsitem, .news, .breadCrumb, .lastpost p, .side, #topforms ul, ul#topforms, .codeheader, .quoteheader, blockquote.bbc_alternate_quote, blockquote.bbc_standard_quote, .description, .description_board, .plainbox, span.upperframe, .roundframe, span.lowerframe, #search_form .input_text, #search_form select, #search_form .button_submit, .button_reset, textarea, input, #top, #header, div.title_barIC, div.title_bar, #footer, .bbc_link:link, .bbc_link:visited, .boardbg, #footer_section, #header .dropmenu li ul
{ border-color: #0F0; }
#search_form .input_text, #search_form select, #search_form .button_submit, .button_reset, textarea, input, .entrymenu a, form.filter .submit input, form.general_submission .submit input, #btnG, #stylechange, select, input[type="text"]
{ border-color: #B0F; }

}
