  {
    background-color: /*[[barcolor]]*/ !important;
  }

input#keyword.text
  {
    border-radius: 9px !important;
    border-color: White !important;
  }

div.bigBarContainer
  {
    border-radius: 7px !important;
  }

div#subcontent
  {
    border-radius: 7px !important;
  }

div.rightBox
  {
    border-radius: 7px !important;
  }

div#tabmenucontainer > ul > li > a
  {
    border-radius: 15px !important;
