  {
    border-radius: 5px !important;
  }

a#currentTab
  {
    background-color: Grey !important;
  }

li#liHome > a
  {
    border-radius: 10px !important; 
  }

li#liReportError > a
  {
    border-radius: 10px !important; 
  }

li#liRequest > a
  {
    border-radius: 10px !important; 
  }

li#liDS > a
  {
    border-radius: 10px !important; 
  }

ul.pager > li
  {
    border-radius: 7px !important;
    background-color: Grey !important;
  }

