css:
@-moz-document domain("www.sweclockers.com") {
/* old swec / needs clean-up, (some of it is still in use!) */
div#siteHeader, .header, div.inner.clear, div#content .fluid {
    background: #56505A;
}.header>.main {
    background: #36303B;
}.mainMenu .menu li.isSelected a {
    background: #36303B !important;
    color: #E3E3E3;
}.mainMenu .menu li a {
    background: #2A252E !important;
    color: #E0E0E0;
}.mainMenu .shadow {
    background-image: -webkit-linear-gradient(top, #57505D, #57505D 65%, #676070 90%, #46404C);
}div.videoPush.sideBox, h5, h6 {
    color: #ECECF0;
}.videoPush h3 a, .popularGalleries h3 {
    color: #DDD;
}.popularGalleries .author a, .popularGalleries .cat a, .articleByline .author a, li.menuItem a.link {
    color: #ACABAB;
}li.menuItem a.link {
    color: #888;
}.popularGalleries .rating {
    color: #303333;
}.subMenu li.isSelected a {
    background: #56505A !important;
    color: #E3E3E3;
}.subMenu li a {
    background: #46404D !important;
    color: #CCC;
}div.mainContent {
    background: #BEBDBD !important;
}div.top.clear {
    background: #BEBDBD;
}.articleHeader h1 {
    color: #e07020;
    text-shadow: 0.65px 0.35px #b40;
}.articleHeader .meta {
    color: #e37320;
}.articleHeader .preamble {
    color: #EDECEC;
}.pushListExternal, .quickReply h4, div.profileHeader .title, div.profileHeader .meta {
    color: #939393;
}div.profileHeader {
    color: #e07020;
}.profileFeed .date {
    color: #d2661c;
}h1 {
    color: #BBB;
}div.breadcrumbs a.link, div.pages a, div.profileHeader a, div.sideMenu.sideBox a.link {
    color: #67a3de;
}div.pages.threadPages, div.flowPanel .cell {
    background-color: #46404D;
}div.flowPanel > .cell ~ .cell {
    background-color: inherit;
}div.pages a.isCurrent {
    background-color: #36303B;
}

/* new swec: */
body {
    color: #222;
}

.nav-container {
    background: #36303B;
}.nav-main {
    border-top-color: hsl(0, 1%, 50%);
    border-bottom-color: hsl(0, 2%, 59%);
}.nav-main ul li a:hover, .nav-main ul li .menu-lock:hover {
    background: #46404D;
}.nav-main ul li a span, .nav-main ul li .menu-lock span {
    color: #E3E3E3;
}

.nav-top {
    background-image: -webkit-linear-gradient(top, #2A252E, #2A252E 70%, #3b3541 97%, #36303B);
}.nav-top .logo img {
    filter: invert(0.07);
}.nav-top .profile-wrapper a {
    background: #2A252E;
    color: #E0E0E0;
}

#search-result .ninja-search-result .nsrt-main {
    background-color: #cecaca;
}#search-result h6 {
    color: hsl(26, 90%, 40%);
}

#header,
#content, #content>.container>.columns {
    background: #56505A;
}

.content-primary {
    background: #c9c5c7;
}.bg-grey-primary {
    background-color: #c9c5c7 !important;
    color: #282828 !important;
}

h1 {
    color: #cdcfcc;
}.descr {
    color: #acacac;
}.card-body .meta a {
    color: hsl(29, 90%, 40%);
}
}
