css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("meteox.nl"), domain("meteox.com"), domain("meteox.co.uk"), domain("meteox.fr") {

/* Remove gray bar on top */
  table[height="19"] { display: none !important; }
  div#Div1,#Div3,#Div4,#Div5 { display: none !important; }
  div#DivZoek { display: none !important; }
  div#languageBanner { display: none !important; } 

/* Remove side images */
  td[width="20"] { display: none !important; }
  td[background="images/balk_l_02.gif"] { display: none !important; }

/* Center page */
  #Div1 + table { width: auto !important; margin: 0px auto !important; }

/* Remove footer */
  td[colspan="3"] { display: none !important; }
  form[id="aspnetForm"] div + div > p { display: none !important; }

/* Remove dot-images */
  img[src="include/images/rm.gif"] { display: none !important; }
  img[src="include/images/stip2.gif"] { display: none !important; }
  img[src="include/images/stip.gif"] { display: none !important; }

/* Remove banners */
  iframe[name="google_ads_frame"] { display: none !important; }
  td[width="120"] { display: none !important; }
  span[id="ctl00_ContentPlaceHolder1_updatePanel1"] > table + table > tbody > tr > td > div > table > tbody tr + tr { display: none !important; } 
  span[id="ctl00_ContentPlaceHolder1_updatePanel1"] > table + table > tbody > tr > td + td + td { display: none !important; }

/* Resizen banner on top of page */
  a[target="_top"][href="http://www.Meteox.nl"], a[target="_top"][href="http://www.meteox.com"], a[target="_top"][href="http://www.meteox.co.uk"], a[target="_top"][href="http://www.meteox.fr"] {
    width: 805px !important;
    overflow: hidden !important;
    display: block !important;
    margin-left: auto !important; 
    margin-right: auto !important; 
  }
}

@-moz-document domain("meteox.be") {

/* Weghalen grijze balk bovenin */
  table[height="19"] { display: none !important; }
  div#Div1,#Div3,#Div4,#Div5 { display: none !important; }
  div#DivZoek { display: none !important; }

/* Weghalen balken aan de zijkant */
  td[width="20"] { display: none !important; }

/* Centreren pagina */
  form[id="aspnetForm"] > table { width: auto !important; margin: 0px auto !important; } 

/* Weghalen banners */
  iframe[name="google_ads_frame"] { display: none !important; }
  div[id="ctl00_pnlAd468homepage"] { display: none !important; }
  div[id="ctl00_pnlAd160homepage"] { display: none !important; } 
  div[id="ctl00_ContentPlaceHolder1_pnlAd300homepage"] { display: none !important; }

  table[height="600"] { display: none !important; } 
  div[id="banner"] { display: none !important; }

/* Weghalen footer */
  td[width="900"] > table > tbody > tr + tr + tr { display: none !important; }
  form[id="aspnetForm"] > p { display: none !important; }

/* Menubalk links uitlijning wijzigen */
  td[width="100"][align="right"] { text-align: left !important; }

/* Resizen banner bovenin zodat hij matcht met de inhoud eronder qua grootte */
  a[target="_top"][href="http://www.meteox.be"] {
    height: 90px !important;
    width: 795px !important;
    overflow: hidden !important;
    display: block !important;
    margin-left: auto !important; 
    margin-right: auto !important; 
  }
}
