}
#body_inner {
		background:url("http://i.imgur.com/7pEmz0u.png");
	background-size: 1600px 800px;
    background-repeat: no-repeat;
	background-color: #cdd0d7;
font:10px verdana !important;
	background-attachment: fixed;
}
div.body_l,
div.body_r {
	background: url(http://i.imgur.com/Vd0vyXd.png);
}
#page-header {
	background: url(http://i.imgur.com/hYFX7Se.png) !important;
	margin: auto;
	background-repeat: no-repeat;
	max-width: 1024px;
	border-radius: 6px;
}
.headerbar {
	background: url(http://i.imgur.com/Vd0vyXd.png);
}
