-webkit-border-radius: 10px / 10px;

-moz-border-radius: 10px / 10px;

border-radius: 10px / 10px;
}
.widgetpanel td, .widgetpanel, #tabs a span, #tabs a, #tabs a:hover span, #tabs li, #tabs ul, #tabs .activetab a span {
background-color: #c1c6fd !important;	
}
.motd dd {
	background-color: #e1e3fe !important;
}
.liner .post {
	border: 1px dashed #48a5b7;
}
ul.topiclist dt, dl.icon dt {
	display: block;
}
a:link {
    color: #01617a;
	font-weight: lighter;
}
a:visited {
    color: #01617a;
	font-weight: lighter;
}
a:hover {
    color: #01617a;
	text-decoration: dashed underline;
	font-weight: lighter;
}
a:active {
    color: #01617a;
	font-weight: lighter;
}
#ul.forums li.row .panel a:hover {
	color: #353a3a;
	background-color: transparent;
	background-color: rgba(245, 245, 220, 0.2);  
}
body.edittrade .petsection .header {
	font:10px verdana !important;
background-color: #e1e3fe !important;
	color: #000000;
}
body.edittrade .rulessection {
	border: 2px dashed #01617a;
}
#main-menu a {
	box-shadow: none;
	-khtml-border-radius: 10px / 10px;

-webkit-border-radius: 10px / 10px;

-moz-border-radius: 10px / 10px;

border-radius: 10px / 10px;
}
img {
	border:1px dashed #01617a;
}
.forumsheader img, #main-menu > li > a > img, .postbody img {
	border: 0px dashed #BD6330;
}
body.edittrade .petsection .actions a {
	color: #000000;
	background-color: #48a5b7 !important;
}
body.archive .pettree th, body.archive #csbody .tabs {
		-khtml-border-radius: 10px / 10px;

-webkit-border-radius: 10px / 10px;

-moz-border-radius: 10px / 10px;

border-radius: 10px / 10px;
}
#tabs a, #tabs a span, body.archive .kinds .kind a {
	background: #deefff;
background: -moz-linear-gradient(top, #deefff 0%, #98bede 100%); 
background: -webkit-linear-gradient(top, #deefff 0%,#98bede 100%); 
background: linear-gradient(to bottom, #deefff 0%,#98bede 100%); 
}
ul.ui-tabs-nav {
	background-color: #e1e3fe !important;
}
.ui-tabs .ui-tabs-nav li a, body.stamp-collection .tabs li, .ui-widget-header {
	background: #deefff;
background: -moz-linear-gradient(top, #deefff 0%, #98bede 100%); 
background: -webkit-linear-gradient(top, #deefff 0%,#98bede 100%); 
background: linear-gradient(to bottom, #deefff 0%,#98bede 100%); 
}
ul.forums li.row  {
	background: #deefff; 
background: -moz-linear-gradient(top, #deefff 0%, #98bede 100%); 
background: -webkit-linear-gradient(top, #deefff 0%,#98bede 100%); 
background: linear-gradient(to bottom, #deefff 0%,#98bede 100%); 
}
table.accentbox.timeinfo {
		background-size:cover;
border-radius:10px;
	box-shadow: none;
font:10px verdana !important;
	border: 0px dashed #48a5b7;
background-color: transparent;
  background-color: rgba(245, 245, 220, 0.2); 
	font-weight: lighter;
}
.table, #accentbox .timeinfo, #pets_remaining, #countdown, .user_action {
		font:10px verdana !important;
	box-shadow: none;
	font-weight: lighter;
}
#main-menu > li > a > img {
border-radius:10px;
	box-shadow: none;
font:10px verdana !important;
	border: 0px dashed #48a5b7;
background-color: transparent;
  background-color: rgba(245, 245, 220, 0.2); 
	font-weight: lighter;	
}
body.editpet #dressedpetoutline img {
	border: 0px dashed #48a5b7;
}
.icon {
background-size: 1px 1px;
