css:
@-moz-document domain("edstem.org") {
.icon,
.dic-new-thread .icon,
.dtc-head-badge.pinned {
	fill: #fff;
}

.navbtn-text {
	color: #fff;	
}

a,
.notif-title .target {
	color: #fff;
}

.dash-course-unread-count {
	color: #fff;	
	border-color: #fff;
}

.dash-course-inner-aside {
	background-color: #fff !important;
    width: 4px;
}

html {
	background-color: #444444;
}

body {
	color: #bbbbbb;
	text-rendering: geometricPrecision;
}


.dash-courses {
	background-color: #444444;	
}

.cml {
	background-color: #444444;	
}

.snippet, .snip-tb {
	border-color: #555555;
}

.snippet {
	background-color: transparent;
}

.segmented-item {
	border-color: #dddddd;
	color: #dddddd;
}	

.csub-test-feedback {
	color: #dddddd;	
}

.stacktrace-content {
	background-color: #3a3a3a;
	color: #eeeeee;
}

.stacktrace-toggle.ed-btn-compact {
	box-shadow: none;
	color: #cccccc;
	border: 1px solid #dddddd;
	border-radius: 5px;
}

.segmented-item.active {
	background-color: #999999;
}

.snip-tb {
	background-color: #3a3a3a;	
}

.cm-s-light.CodeMirror {
	background-color: #444;
	color: #bbb;
}

.cml-arrow {
	fill: #444444;
}

.app {
	background-color: #444444;	
}

input.res-search-input {
	background-color: rgba(255, 255, 255, 0.1);
	color: #dddddd;
	box-shadow: 0 0 10px rgba(0, 0, 0, 0.5) inset; 
	padding: 10px;
	border-radius: 5px;
}

.ws-submission-container {
    background-color: #2a2a2a;
}

.csub-header-title,
.csub-header-aside {
    color: #eeeeee;
}

.ws-fixed-terminal-overlay {
	background-color: #2a2a2a;
}

button.ws-fixed-terminal-reset-btn {
	background-color: #222222;
    color: #eee;
    transition: opacity 0.1s linear
}

.ed-theme-dark .pane-tab.active .pane-tab-close {
    background-color: transparent;
    box-shadow: none;
}

.pane-tab-close .icon.pane-tab-close-icon  {
 	fill: #aaaaaa;
    display: inline !important;
}
.pane-tab-close:hover .icon.pane-tab-close-icon {
 	fill: #ffffff;   
}

h2.csub-header {
    color: #cccccc;
    font-weight: normal;
}

.csub-test-item {
 	color: #dddddd;   
}

.res-search-container {
	border-bottom-color: #444444;	
}

.pills-item {
	background-color: #3a3a3a;	
}

.res-row, .res-body.type-archive:hover {
	background-color: transparent;	
}

.res-row {
	box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
	border-radius: 5px;
	background-color: #4a4a4a;
	border: none;
	transition: background-color 0.1s linear,
				box-shadow 0.1s linear;
}


.res-type {
	border-radius: 5px;
}

.res-row:hover {
	background-color: #555555;
	box-shadow: 0 0 30px rgba(0, 0, 0, 0.1);	
}

.res-search-container.active {
	border-bottom-style: solid;
	border-bottom-color: #3a3a3a;
}

.dashboard, .prof-container {
	background-color: #444444;	
}

.ed-input {
	background-color: #3a3a3a;
	color: #ddd;
}

input[type=text] {
	box-shadow: 0 0 5px rgba(0, 0, 0, 0.1) inset;
	color: #ddd;	
}

input[type=password] {
	color: #ddd;	
}

.ed-input:focus {
	box-shadow: 0 0 5px rgba(0, 0, 0, 0.1) inset,
				0 0 10px rgba(255, 255, 255, 0.1);
	border: 1px solid rgba(0,0,0,.1);
}

.dash-course-inner-content {
	background-color: #3a3a3a;
	border-color: #555555;
}

.hbm-pane {
	background-color: #3a3a3a;	
}

.navbar.hbm-navbar,
.hbm-items-nav .hbm-item {
	border-bottom: none;
}

.hbm-items-user .hbm-item {
	border-top-color: #555555;	
}

.notifications.hbm-notif {
	background-color: #3a3a3a;
}

.hbm-item {
	background-color: #333333;
	cursor: pointer;
}

.hbm-item-text {
	color: #dddddd;	
}

.de-type {
	background-color: rgba(255, 255, 255, .1);
	color: #dddddd;
	transition: background-color 0.1s linear;
}

select.media-sm {
	background-color: #666666;
	border-radius: 3px;
	color: #dddddd;
}

div.check-btn {
	background-color: #444444;
}

.check.checked .check-btn {
	background-color: #0080ff;
}

.icon.icon-question,
.icon.icon-post {
	background-color: transparent;
	fill: #dddddd;
}

div.de-type>.icon {
	display: none;
}

.dcp-item {
	filter: brightness(200%);	
	-webkit-backface-visibility: hidden;
	transition: filter 0.1s linear;
}

.dcp-item.selected {
	filter: brightness(300%);
	box-shadow: none;
}

.dcp-item:hover {
	filter: brightness(250%);
}

.de-type.post.active .icon {
	background-color: transparent;
	fill: #dddddd;
}

.de-type.post.active,
.de-type.question.active {
	background-color: rgba(255, 255, 255, .3);
	color: #dddddd;
}

.navbar {
	background-color: #303030;	
}

.navbar-side.left {
	box-shadow: 10px 0 10px -4px rgba(0, 0, 0, 0.04) inset;
	-webkit-box-shadow: 10px 0 10px -4px rgba(0, 0, 0, 0.1) inset;
}

.chai-tab {
    background-color: #2a2a2a;
}

.chai-tab.router-link-exact-active.router-link-active {
    background-color: #3a3a3a !important;
}

table.chai-group tr {
	background-color: #3a3a3a;
}

.dic-split-panel-filters-container {
	background-color: #3a3a3a;	
}

.split-panel.dic-split-panel-filters {
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
    z-index: 10;
}

.de-actions {
    border-top: none;
}

input[type=text].dic-search-input {
    box-shadow: none;
}

.swm-header {
	background-color: #555555;	
}

table.chai-group, tr.chai-item {
	border: 1px solid #555555;
}

table.chai-group tr:hover {
	background-color: #4a4a4a;	
}

.swl-room {
	background-color: #4a4a4a;	
	border-color: #555555;
	transition: border-color 0.1s linear;
}

.swl-room:hover {
	border-color: #666666;	
}

.chai-item>td {
	border: 1px solid #555555 !important;	
	outline: #555555 !important;
}


div.dft-tabs {
	background-color: #3a3a3a !important;	
}

.dft-tab, .dft-tab .icon {
	color: #666666;
	stroke: #666666;
}

.dft-tab:after {
	background-color: #666666;
}

.dtc-comments-header {
	background-color: #444444;
	border-top: dashed #333333;
	border-bottom: dashed #333333;
}

.appbar, .navbar.appbar-small {
	background-color: #2a2a2a;	
}

.router-link-active {
	background-color: #333333 !important;	
}

.split-divider {
	display: none;
	background-color: #555555;
}


/* Threads */
.dsc-thread {
	background-color: #444444;
}

.dtc-head {
	background-color: #444444;	
}

.dic-content, .de-scroll {
 	background-color: #3a3a3a;
	box-shadow: 0 0 10px rgba(0, 0, 0, 0.4) inset; 
}


/* Thread Lists */
.dic-tl-container {
	background-color: #333333;	
}

.dic-tl-header-filter-btn-label {
	color: #8ff720;
}

/*.dic-tl-header-filter {
	border-right: 1px #555555 solid;	
}*/

.dtl-item.discuss-feed-thread-compact {
	background-color: #333333;	
	outline-color: #555555;
}

.dic-tl-header {
	border-color: #555555;	
	box-shadow: 0 0 10px rgba(0, 0, 0, 0.04)
}

.discuss-filters,
.dic-tl-filters,
.df-heading {
    margin: 0 !important;
	background-color: #333333;
}

.df-item {
	background-color: #3f3f3f;	
}

.df-course-unread {
	color: #dddddd;
}

.df-item:hover {
	background-color: rgba(255, 255, 255, 0.02);
}

.df-item.selected {
	background-color: #4a4a4a;
	color: #ffffff;
}

h2.df-heading:not(:first-of-type) {
	border: none;
}

/* Amber */

.split-panel:first-child {
 	background-color: #333333;
    z-index: 10;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
}

pre.amber-el {
 	background-color: #2a2a2a !important;   
}

.am-ui-barToolGroup>.am-ui-toolGroup-tools>.am-ui-tool.am-ui-tool-active,
.am-ui-barToolGroup>.am-ui-toolGroup-tools>.am-ui-tool.am-ui-tool-active:hover {
	background-color: rgba(255, 255, 255, 0.1);
}

.drc-inner {
	border: none;
	background-color: #444444;
}

.dtc-reply-placeholder {
	border-color: #5a5a5a;	
}

.dtc-reply-placeholder:hover {
	border-color: #666666;
}

.discuss-reply-placeholder {
	background-color: #3a3a3a;	
}

.amber, .am-ui-target {
	background-color: #3a3a3a;
	border-color: #222222;
}

.am-ui-widget, .am-ui-toolbar-bar {
	border-color: #222222;	
}

.am-ui-labelElement-label {
	color: #ccc;	
}

.am-ui-popupToolGroup>.am-ui-toolGroup-tools {
	background-color: #3a3a3a;
}

.am-ui-menuToolGroup .am-ui-tool:hover,
.am-ui-barToolGroup>.am-ui-toolGroup-tools>.am-ui-tool:hover,
.am-ui-popupToolGroup-handle:hover {
	background-color: #444444;	
}

.am-ui-menuToolGroup .am-ui-tool-id-blockquote .am-ui-tool-title {
	border-left-color: #666666;
}

.dl-layout, .discuss-landing {
	background-color: #3a3a3a;	
}

.dl-text {
	color: #ffffff;	
}

.url-segment {
	background-color: #aaaaaa;
	border-radius: 3px;
	color: #333333;
}

/* Sweet hack to change the colour of the Amber toolbar icons */
.am-ui-iconElement-icon,
.am-ui-indicator-down {
	-webkit-filter: invert(1);
   filter: invert(1);
	color: blue;
}

/* Notifications */
.app-pane {
	background-color: #444444;
	border-color: transparent;
	box-shadow: 0 0 20px rgba(0,0,0,.2)
}

.notif-title .name {
	color: white;	
}

.notif-item.unvisited {
	background-color: rgba(255, 255, 255, .04)
}

/* Sidebar */
.discuss-filters {
	background-color: #3c3c3c;	
}

.df-new-thread, .ed-btn {
	background-color: #555555;	
	color: #ffffff;
	border: none;
	box-shadow: 0 0 4px #333333;
	transition: background-color 0.2s;
}

.df-new-thread:hover, .ed-btn:hover:not(:disabled) {
	background-color: #505050;
}

.wsi-header {
	background-color: #3a3a3a;
}

.ai-header {
	color: #dddddd;	
}

.wsl-item-content {
	border: none;	
	border-radius: 5px;
	box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

.icon.icon-reload {
	fill: #dddddd;
}

.wsi-action {
	color: #dddddd;	
}

.wsl-item {
	background-color: #3a3a3a;
}

.wsl-item-gutter {
	background-color: #555555;	
}

.ed-theme-dark .pane-tab.active {
	background-color: #555555;	
}

.ed-theme-dark .pane-tab.active .pane-tab-title {
	color: #dddddd;
}

.appbar-btn.ed.router-link-exact-active.router-link-active {
	background-color: #2a2a2a !important;	
}

/* Scrollbars */
::-webkit-scrollbar-track
{
	background-color: #454545;
}

::-webkit-scrollbar
{
	width: 8px;
	background-color: #454545;
}

::-webkit-scrollbar-thumb
{
	border-radius: 10px;
	background-color: #555;
}
}
