css:
@-moz-document domain("wow.gameinfo.io") {
#app nav li._a {
    background: #111;
    position: relative;
    color: #51a955;
    border-right-color: #111;
}
#app nav li {
    background: #111;
    padding: 15px 18px;
    color: #bbb;
    border-right: 1px solid #222;
    border-bottom: 1px solid #222;
    cursor: pointer;
}
article {
    padding: 40px;
    background: #111;
    box-shadow: 0 1px 5px rgba(0, 0, 0, .1);
    margin: 0 0 20px;
}
body {
    font: 400 16px/1.5 'Roboto', sans-serif;
    color: #bbb;
}
#app article .date {
    margin-top: 15px;
    color: #bbb;
    font-size: 20px;
    font-weight: 400;
}
header {
    display: flex;
    justify-content: space-between;
    position: relative;
    z-index: 5;
    background: #111;
    height: 70px;
    line-height: 70px;
    padding: 0 24px;
}
header nav a.active,
header nav a:hover,
header nav div:hover a {
    background-color: rgba(255, 255, 255, 0.1);
}
header nav a {
    color: #ccc;
    text-decoration: none;
    display: inline-block;
    height: 70px;
    margin: 0;
    padding: 0 15px;
    position: relative;
}
#top .language .dropdown>a:hover {
    background: #111;
    cursor: pointer;
}
.table-links tr:hover td {
    background: #111;
}
#app nav li:hover {
    background: #111;
    color: #409844;
}
}
