css:
@-moz-document domain("steemit.com") {
/**
 * Style mods for Steemit.com,
 * A Friendly Community
 *
 * Author: https://steemit.com/@ura-soul
 *
 */
  
  body, .App__content, .theme-original {
    background: hsl(0, 36%, 44.7%);
    color: hsl(0, 0%, 99.6%);
  }

  a, a:focus, a:visited, a:active, .steemit a, table thead, .VotesAndComments__votes 
  {
        color: hsl(0, 100%, 90.4%) !important;
  }

  a:hover {
    color: hsl(213.8, 100%, 96.9%)!important;
  }
  
  hr {
    border-bottom: 1px solid hsla(0, 0%, 84.3%, 0.6);
    margin: 1rem auto 1.2rem;
  }
  
  label {
    color: hsl(30.9, 38.5%, 82.2%);
  }
  
  table thead, table tbody, table tfoot {
    background-color: transparent;
  }

  .Markdown blockquote > p, .ReplyEditor__body.rte blockquote > p, .theme-original .Markdown blockquote > p, .theme-original .ReplyEditor__body.rte blockquote > p, blockquote * {
    color: hsl(33.1, 100%, 82.9%);
  }
  
  .theme-original .Markdown blockquote, .theme-original .ReplyEditor__body.rte blockquote
  {
    border-color:   hsl(33.1, 100%, 82.9%);   
  }
  
  /* logo */
  
  #steem path:nth-of-type(2){
    fill:hsl(0, 100%, 88.6%) !important;
  }
  
  #steem path {
    fill: hsl(0, 69.8%, 61%);
  }
  
  .beta {
    color: hsl(0, 100%, 65.7%)!important;
  }
  
  .LoginForm .sign-up .button {
    background-color: hsl(0, 58.4%, 43.3%) !important;
  }
  
  /* header */
  
  .UserProfile__banner > div.column {
       text-shadow:1px 1px 2px hsla(0, 0%, 0%, 0.5) !important;
   }

  .Header {
    box-shadow: 0px 0 5px 0 hsla(0, 0%, 20%, 0.7);
  }
  
  .Header__top, .header {
    border-bottom: 1px solid hsl(0, 83.7%, 24.1%);
  }

  .Header .menu, .Header__top, .Header__sub-nav, .top-bar, .top-bar ul {
    background-color: hsl(0, 72.9%, 30.4%);
  }   
  
  .Header__sub-nav {
    border-bottom: 1px solid hsl(0, 62.4%, 41.8%);
  }
  
  li.Header__userpic .Userpic {
    border: 1px solid hsla(0, 100%, 59.4%, 0.6);
  }

  .Header .menu .hamburger::after {
     background: hsl(0, 92.6%, 94.7%);
     box-shadow: 0 7px 0 hsl(0, 92.6%, 94.7%),0 14px 0 hsl(0, 92.6%, 94.7%);
  }  
  
  .Header .menu .hamburger:hover::after{
     background: hsl(213.8, 100%, 96.9%);
     box-shadow: 0 7px 0 hsl(213.8, 100%, 96.9%),0 14px 0 hsl(213.8, 100%, 96.9%);
  }
  
  .Header__top ul > li.delim {
   color: hsl(0, 24.5%, 52.7%);
  }
  
  .submit-story a{
    background: hsl(0, 67.9%, 46.5%)!important;
    border-radius: 6px;
  }
    
  /* search icon */
  
  .Header svg > path {
    fill: hsl(0, 92.6%, 94.7%);
   }

  
  /* navigation menu */
  
  .Header__sub-nav li > a {
    color: hsl(213, 29%, 72.9%);
  }   
  
  .HorizontalMenu li > a:hover {
    color: hsl(242.2, 41.5%, 87.3%);
  }
  
  .Header__sub-nav li > a {
    background: transparent;
  }
  
  .UserProfile__top-nav .menu > li > a.active {
    background-color: hsl(0, 82.4%, 33.3%);
    color: hsl(0, 0%, 99.6%);
  }
  
  .HorizontalMenu .active > a {
    color: hsl(213.3, 39.1%, 82%);
  }
  
  .UserProfile__top-nav {
    background-color: hsl(0, 65%, 23.5%);
  }
  
  .UserProfile__top-nav .menu > li > a:hover, .UserProfile__top-menu > .shrink > .menu > li:hover {
    background-color: hsl(0, 82.4%, 33.3%);
  }
  
  .VerticalMenu svg path, .VerticalMenu svg polygon {
    fill: hsl(0, 100%, 88.2%);
  }
  
  .VerticalMenu > li.title {
    border-bottom: 1px solid hsla(0, 0.4%, 55.9%, 0.5);
  }
  
  .articles__icon--layout * {
    fill: white!important;
  }
  
  /* drop down menu */
  
  .Icon.dropdown-arrow polygon {
    fill: hsl(0, 82.9%, 77.1%);
  }
  
  .dropdown-pane, .gsc-control-cse .gsc-option-menu {
    background: hsl(0, 65.6%, 32%);
    border: 1px solid hsl(0, 59.8%, 45.9%);
  }
  
  .gsc-option-menu-item {
    color: hsl(240, 68.2%, 91.4%) !important;    
  }

  .VerticalMenu > li > a:hover, .gsc-option-menu-item-highlighted {
    background-color: hsl(0, 59.6%, 38.8%);
  }

  .DropdownMenu.show > .VerticalMenu {
    background-color: hsl(0, 73.2%, 24.9%);
    border-color: hsla(0, 62%, 46.5%, 0.6);
    color: hsl(0, 100%, 94.1%);
   }

  
  /* Sidebar menu */
  
  .SidePanel > div {
    background-color: hsl(0, 55.4%, 22%);
  }
  
  .SidePanel > div .menu > li > a {
    color: hsl(254.3, 70%, 88.2%);
    border-top: 1px solid hsla(0, 28.6%, 36.3%, 0.7);
   }

  .SidePanel > div .menu > li > a:hover {
    background-color: hsl(0, 48.4%, 35.7%);
  }
  
  .SidePanel .menu > li.last {
    border-bottom: 1px solid hsl(0, 38.7%, 55.9%);
  }
  
  .theme-original .c-sidebar__module, .c-sidebar__module {
    background-color: transparent;
    border-color: transparent;
  }

  /* notifications */
  
  .NotifiCounter {
    background-color: hsl(48.5, 100%, 42.9%);
    color: hsl(50.9, 29.7%, 21.8%);
  }
  
  /* user profile */
  
  .UserProfile__banner > div.column, .UserProfile__banner > div.columns {
     background-image: url('http://i.imgur.com/pxWfao8.jpg');
     background-size: cover;
  }
  
  .UserProfile__banner .UserProfile__stats,
  .UserProfile__banner .UserProfile__info 
  {
    font-weight:bold;
  }
  
  .UserProfile__banner .Userpic {
    border: 2px solid hsla(0, 100%, 59.4%, 0.6);
  }
  
  .UserProfile__banner .Icon svg{
    fill: hsl(338.7, 100%, 57.5%);
  }
  
  .UserProfile__banner .UserProfile__buttons label.button {
     background-color: hsla(0, 0%, 100%, 0.4);
     color: black !important;
  }

  
  /* user data */
  
  .Reputation {
    background-color: hsl(355.9, 100%, 28.8%);
    border: 1px solid hsla(355.3, 67.1%, 66.7%, 0.8)!important;
    border-radius: 50%;
    color: hsl(0, 0%, 100%)!important;
  }
  
  /* Posts */
  
  .articles__tag-link {
    color: white !important;
  }
  
  .layout-list .articles, .theme-original .articles {
    background-color: hsl(0, 36%, 44.7%)!important;
    border: 0px solid !important;
  }
  
  .layout-list .articles__summary, .theme-original .articles__summary {
    padding: 0.7rem;
    background-color: hsl(0, 50%, 25.9%);
    border: 1px solid hsla(0, 63.8%, 77.3%, 0.5);
    border-radius: 11px;
    box-shadow: 0px 0px 4px 0 hsla(0, 0%, 0%, 0.1);
    border-bottom:0px solid !important;
  }
  
  .entry-title > a, .PostSummary__header > h3 > a
  {
   color: hsl(0, 100%, 88.8%)!important;    
  }
  
  .PostSummary__body{
    color: hsl(240, 3%, 74.1%);
  }
  .PostSummary__image {
    background-color: transparent;
    border: 1px solid hsl(0, 62.3%, 20.8%);
    border-radius: 5px;
  }
  
  .PostSummary__reblogged_by, .articles__resteem-text {
    color: hsl(40.2, 100%, 61.4%)!important;
  }
  
  .PostSummary__reblogged_by .Icon path, .articles__resteem-icon path {
    fill: hsl(40.2, 100%, 61.4%);
  }

  .PostsList__post_overlay {
    background-color: hsla(0, 32.1%, 10.4%, 0.9);
  }

  .PostsList__post_container, .PostsList__post_container .PostFull, .PostsList__post_container {
    background: hsl(0, 49.7%, 37.5%) !important;
    color: hsl(0, 0%, 99.6%);
  }
  
  .PostFull{
    border-bottom: 1px solid hsl(0, 74.8%, 79.8%);
  }
  
  /* voting interface */

  .Voting__adjust_weight .rangeslider-horizontal .rangeslider__fill, 
  .Voting__adjust_weight_down .rangeslider-horizontal .rangeslider__fill
  {
      background: hsl(21.3, 85.6%, 62%);
  }

   .PostFull__reply {
      background: hsl(0, 49.7%, 37.5%) !important; 
   }

   .PostFull__footer {
      background: hsl(0, 66.9%, 27.3%) !important;
      border-bottom: 1px solid hsl(0, 62%, 23.7%) !important;
      z-index: 999;
   }
  
  .PostFull__footer .FoundationDropdownMenu__label, .PostFull__footer a, .PostFull__views, .Comment__footer a{
    color: hsl(30, 20.8%, 71.8%);    
  }

  .PostSummary__time_author_category,.PostFull__time_author_category_large, .PostFull__time_author_category, .Comment__header .updated{
    color: hsl(0, 0%, 90.6%)!important;
   }

  .FormattedAsset .decimal {
    opacity: .8;
    color: #6CA000;
   }

  .Voting__inner, .VotesAndComments__votes, .PostSummary__time_author_category .Reblog__button, .PostFull__time_author_category,.PostFull__footer .Reblog__button, .PostFull__reply, .PostFull__views {
    border-right: 1px solid hsl(0, 24.5%, 52.7%);
  }
  
  .PostSummary__time_author_category
  {
    border-left: 1px solid hsl(0, 24.5%, 52.7%);
  }
  
  
  #Chevron_Up_Circle path {
    fill: hsl(35.5, 63.3%, 76.5%);
  } 
  #Chevron_Up_Circle circle {
       stroke: hsl(0, 100%, 81.8%); 
  }
  .Voting__button--upvoted #Chevron_Up_Circle circle {
    fill: hsl(0, 100%, 36.9%)!important;
  }

  .Icon_1x #Chevron_Up_Circle circle {
    fill: hsl(0, 100%, 36.9%)!important;
    stroke: hsl(0, 47.2%, 75.5%)!important;
  }
 
  .theme-original .Voting__button-up > a:hover circle, .theme-original a.confirm_weight:hover circle{
    fill: hsl(0, 91.9%, 33.7%);
   stroke: hsl(0, 40.4%, 81.6%);
  }
  
  .Reblog__button-active path {
    fill: hsl(40.2, 100%, 61.4%) !important;
  }

  /* labels and markers */
  
  .theme-original .link--secondary, .theme-original .PlainLink, .theme-original .PostFull__header a, .PostFull__header .theme-original a, .theme-original .articles__resteem-username, .theme-original .articles__tag-link a, .articles__tag-link .theme-original a, .theme-original .user__username, .theme-original .user__reputation, .theme-original .timestamp__time, .theme-original .articles__resteem .username, .articles__resteem .theme-original .username {
    color: hsl(0, 3.8%, 89.8%);
  }
  
  .theme-original .link--secondary:hover, .theme-original .PlainLink:hover, .theme-original .PostFull__header a:hover, .PostFull__header .theme-original a:hover, .theme-original .articles__resteem-username:hover, .theme-original .articles__tag-link a:hover, .articles__tag-link .theme-original a:hover, .theme-original .user__username:hover, .theme-original .user__reputation:hover, .theme-original .timestamp__time:hover, .theme-original .articles__resteem .username:hover, .articles__resteem .theme-original .username:hover, .theme-original .link--secondary:focus, .theme-original .PlainLink:focus, .theme-original .PostFull__header a:focus, .PostFull__header .theme-original a:focus, .theme-original .articles__resteem-username:focus, .theme-original .articles__tag-link a:focus, .articles__tag-link .theme-original a:focus, .theme-original .user__username:focus, .theme-original .user__reputation:focus, .theme-original .timestamp__time:focus, .theme-original .articles__resteem .username:focus, .articles__resteem .theme-original .username:focus{
    color:hsl(0, 0%, 100%);
  }
  
  .theme-original .articles__summary-footer, .theme-original .articles__summary-header {
    border-color: hsl(0, 57.5%, 56.7%);
  }
  
  .PostFull__footer svg {
    fill: hsl(0, 75%, 92.2%);
  }
  
  .shareItems li > a:hover svg {
   fill: hsl(0, 100%, 85.1%);
  }
  
  .nsfw-flag {
    color: hsl(0, 95.1%, 75.9%);
    border: 1px solid hsl(0, 95.1%, 75.9%);
  }
  
  .Voting__button path {
    fill: hsl(0, 100%, 52.4%);
  }

  .Voting__button-down a:hover path {
    fill: hsl(0, 95.1%, 75.9%);
  }

  /* Post Index */
  
  .PostsIndex__topics {
    border-left: 1px solid hsl(0, 96%, 19.6%);
   }

  .Topics__title {
    color: hsl(0, 100%, 81.4%);
  }

  /* comments */
  
  .Comment__header-user, .Post__comments_sort_order {
    color: hsl(30, 20.8%, 71.8%);    
  }
  
  .callout {
    border: 1px solid hsla(0, 0%, 20%, 0.3);
    background-color: hsla(0, 0%, 100%, 0.1);
    color: hsl(0, 0%, 92.5%); 
  } 

  .Comment .highlighted {
    background: hsla(0, 40.6%, 19.8%, 0.4);
  }
  
  /* buttons */
  
  .close-button:focus, .close-button:hover {
    color: hsl(0, 86.4%, 79.8%)!important;
  }
  
  .button.disabled, .button[disabled] {
    opacity: .4;
  }
  
  .button {
     background-color: hsl(31.6, 67.6%, 42.4%);
  }

  .button:focus, .button:hover {
    background-color: hsl(262.7, 74%, 50.2%)!important;
    border-color: hsl(262.8, 75.5%, 36.9%)!important;
  }
  
  .button.hollow {
    border: 1px solid hsl(0, 100%, 74.9%)!important;
    color: hsl(0, 62.2%, 92.7%)!important;
  }
  
  .button.hollow:focus, .button.hollow:hover {
    border-color: hsl(0, 0%, 100%);
    color: hsl(0, 0%, 100%)!important;
   }
  
  .button.disabled, .button.disabled:focus, .button.disabled:hover, .button[disabled], .button[disabled]:focus, .button[disabled]:hover {
    background-color: hsl(31.6, 67.6%, 42.4%);
  }

  input.disabled[type="submit"] {
    background-color: hsl(0, 26%, 24.9%);
  }
  
  /* tags */
  
  .TagList__horizontal a {
    background: hsla(0, 63.6%, 28%, 1);
    color: hsl(39.7, 41.3%, 72%);
   }

  .TagList__horizontal a:hover {
      background: hsla(0, 55.3%, 57.1%, 0.6);
  }
  
  /* inputs */
  
  input:disabled, input[readonly], textarea:disabled, textarea[readonly] {
    background-color: hsl(0, 26%, 24.9%);
    color: hsl(0, 0%, 65.5%);
   }
  
  [type="color"]:focus, [type="date"]:focus, [type="datetime-local"]:focus, [type="datetime"]:focus, [type="email"]:focus, [type="month"]:focus, [type="number"]:focus,    [type="password"]:focus, [type="search"]:focus, [type="tel"]:focus, [type="text"]:focus, [type="time"]:focus, [type="url"]:focus, [type="week"]:focus, textarea:focus {
    outline: none;
     background: hsl(0, 26.4%, 34.1%);
     border: 1px solid hsl(0, 64.7%, 30%);
    box-shadow: 0 0 5px hsla(0, 88.7%, 79.2%, 0.6);
  }
  [type="color"], [type="date"], [type="datetime-local"], [type="datetime"], [type="email"], [type="month"], [type="number"], [type="password"], [type="search"], [type="tel"], [type="text"], [type="time"], [type="url"], [type="week"], textarea {
     background: hsl(0, 26.4%, 34.1%);
     border: 1px solid hsl(0, 64.7%, 30%);
    box-shadow: inset 0 1px 2px hsla(0, 53.4%, 22.7%, 0.8);
    color: hsl(0, 0%, 100%);
  }
  
  .drag-and-drop {
    color: hsl(0, 0%, 97.3%);
    background-color: hsl(0, 33.3%, 27.6%);
    border-color: hsl(0, 100%, 24.7%);
  }
  
  .upload-enabled{
    border-bottom: 1px dashed hsl(0, 72.4%, 55.9%);
  }
  
  select, select:focus {
    background-color: hsl(0, 35.3%, 27.3%);
    border: 1px solid hsl(0, 39.6%, 46.1%);
    box-shadow: 0 0 5px hsl(0, 60.9%, 25.1%);
    color: hsl(0, 0%, 86.7%);
  }
  
  form .error .help-text, form .error label, .error {
  color: hsl(0, 80.5%, 36.3%);
  }
  
  /* editor */
  
  .ReplyEditor .Preview .Markdown {
    border: 1px solid hsl(0, 79.7%, 76.9%);
    border-radius: 8px;
  }
  
  .ReplyEditor__draft {
    background: hsla(125.6, 34%, 18.4%, 0.9);
    color: hsl(0, 0%, 73.3%);
  }
  
  .RichTextEditor__root___33zoV{
     background: hsl(0, 26.4%, 34.1%);
     border: 1px solid hsl(0, 64.7%, 30%);
  }
  
  .RichTextEditor__editor___1VEsr {
    border-top: 1px solid hsl(0, 26.6%, 46.5%);
  }

  
  /* google search */
  
  .cse .gsc-control-cse, .gsc-control-cse {
    background: transparent;
    border: 0px solid;
  }
  .gsc-input, .gsc-completion-container table {
    color: black!important;
  }
  
  .gsc-result-info, .gsc-orderby-label {
    color: hsl(0, 0%, 81.2%);
  }
  
  .gsc-above-wrapper-area, .gsc-webResult.gsc-result {
    border-color: hsla(0, 0%, 91.4%, 0.5);
  }
  
  .gsc-preview-reviews, .gsc-control-cse .gs-snippet, .gsc-control-cse .gs-promotion em, .gsc-control-cse .gs-snippet, .gsc-control-cse .gs-promotion em {
    color: hsl(0, 0%, 84.7%);
  }

  .gs-result .gs-title, .gs-result .gs-title * {
    color: hsl(0, 100%, 16.9%);
  }

  .gsc-results .gsc-cursor-box .gsc-cursor-page {
    color: hsl(0, 19.6%, 82%);
  }
  
  .gs-result a.gs-visibleUrl, .gs-result .gs-visibleUrl{
   color: hsl(115.9, 100%, 8.6%);
  }
  
  /* wallet */
  
  .UserWallet__claimbox {
    background: hsla(102.6, 77.1%, 37.6%, 0.4);
    border: 1px solid hsl(93, 16.3%, 48.2%);
  }
  
  .button.hollow:focus, .button.hollow:hover, .button.hollow.secondary:hover, button.hollow.secondary:focus  {
    border-color: hsl(0, 0%, 100%);
    color: hsl(0, 0%, 100%);
  }
  

  .button.hollow, .button.hollow.secondary {
    border: 1px solid hsl(214.6, 64.8%, 82.2%);
    border-top-color: hsl(214.6, 64.8%, 82.2%);
    border-right-color: hsl(214.6, 64.8%, 82.2%);
    border-bottom-color: hsl(214.6, 64.8%, 82.2%);
    border-left-color: hsl(214.6, 64.8%, 82.2%);
    color: hsl(214.6, 64.8%, 82.2%);
  }
  .button:focus, .button:hover {
      background-color: hsla(336.6, 73.3%, 50%, 0.9) !important;
      border-color: hsla(308.6, 100%, 74.1%, 0.6) !important;
  }
  .UserWallet__claimbox .button {
    background: hsl(118.9, 39.7%, 26.7%)!important;
    border-color: hsl(106.4, 35.5%, 41.4%) !important;
    color: hsl(0, 0%, 99.6%);
  }
  .UserWallet__claimbox .button:hover {
    background: hsl(115.9, 52.2%, 77.8%);
    color: hsl(114.2, 54.4%, 33.5%);
    border-color: hsl(106.1, 74.9%, 51.6%);
  }
  .UserWallet__balance.UserWallet__balance.zebra, table tbody tr:nth-child(2n) {
    background: hsla(0, 0%, 94.9%, 0.1);
  }
  .theme-original .secondary {
    color: hsl(0, 0%, 81.2%);
  }
  .hoverBackground:hover {
    background-color: hsla(34.3, 77.8%, 91.2%, 0.2);
  }
  
  .FormattedAsset .prefix, .FormattedAsset .integer {
    color: #6CA000;
  }

  .UserWallet__balance:nth-child(7) > div:nth-child(2) {
    color: #6CA000;
    font-size: 24px;
  }

  /* market */
  
  .Market__ticker li b {
    border-right: 1px solid hsla(0, 0%, 90.2%, 0.2);
    background: hsla(0, 0%, 97.3%, 0.2);
  }

  .Market__ticker li {
    border: 1px solid hsla(0, 0%, 90.2%, 0.3);
  }
  
  .input-group-label {
    border: 1px solid hsla(0, 0%, 79.2%, 0.4);
    background: hsla(0, 0%, 90.2%, 0.2);
    color: hsl(41.5, 46.4%, 89%);
  }
  
   .buy-color,.Market__ticker-pct-up {
      color: hsl(120, 73%, 52.2%) !important;
   }

   .sell-color,.Market__ticker-pct-down {
      color: hsl(0, 100%, 21%) !important;
   }
  
  input.buy-color {
    background-color: hsl(120, 42.9%, 28.8%) !important;
  }
  
  input.sell-color {
    background-color:   hsl(0, 43.4%, 28.4%) !important;
  }

  /* dialog */
  
  .reveal {
    background-color: hsl(0, 29.3%, 22.7%);
    border: 1px solid hsl(0, 25.2%, 53.3%);
  }
  
  .Author__container{
   background-color: hsl(0, 38.5%, 35.7%);
   color: hsl(0, 0%, 84.3%);
   border: 1px solid hsla(0, 55.5%, 56.9%, 0.6);
  }

  /* firefox/chrome plugin compatibility */
  .smi-post-floating-footer .smi-post-footer-small .PostFull__footer > *, .smi-post-footer-small.smi-post-floating-footer-on-body .PostFull__footer > *,
  .PostFull__reply{
    background: transparent!important;
  }
  
  .DropdownMenu.Voting__voters_list ul li > span, .UserProfile__tab_content_VotesTab .VotesTab .vote .vote-weight {
    color: hsl(43.5, 87.5%, 78%);
  }
  
  .UserProfile__tab_content_VotesTab .VotesTab .vote .timeago {
    color: hsl(0, 100%, 90.4%) !important;
  }
  
   .UserProfile__tab_content_VotesTab .VotesTabLoadMore button {
    border: 1px solid hsl(0, 100%, 74.9%)!important;
