css:
@-moz-document url-prefix("http://codex.wordpress.org/"), domain("codex.wordpress.org"),url-prefix("http://developer.wordpress.org/"), domain("developer.wordpress.org") {
  .site-main .table-of-contents {
      background: #181818;
  }
  #wordpress-org {
      background: #212121;
  }
  #mw-content-text div {
      background-color: #212121 !important;
  }
  #bodyContent, body {
      color: #fff;
  }
  #toc {
      background: #242424;
  }
  #bodyContent h2 {
      color: #fff;
  }
  p, h1, h2,h3,h4,h5,h6 {
      color: #fff !important;
  }
  
  dt {
      color: #999;
  }
  
  
  #headline, #masthead {
      background-color: #282828;
  }
  #wporg-header,#wporg-header ul#wporg-header-menu{
    background-color:#303030;
  }
  
  code {
      color: #21759b;
  }
  #bodyContent h1, #bodyContent h2 {
  
    color: #fff;
  }
  .devhub-wrap #content table th, .devhub-wrap #content-area table th {
      background-color:#282828;
  }
  #wporg-footer {
      background-color: #101010;
  }
  div#localNotice > div {
      background-color: #492700 !important;
      color: #fff !important;
  }
  
  #bodyContent h2.pagetitle {
      color: white;
  }
  .devhub-wrap.single-wp-parser-function .comment-list li, .devhub-wrap.single-wp-parser-function #comment-preview, .devhub-wrap.single-wp-parser-method .comment-list li, .devhub-wrap.single-wp-parser-method #comment-preview, .devhub-wrap.single-wp-parser-hook .comment-list li, .devhub-wrap.single-wp-parser-hook #comment-preview, .devhub-wrap.single-wp-parser-class .comment-list li, .devhub-wrap.single-wp-parser-class #comment-preview {
      background: #212121;
  }
  
  .devhub-wrap.single-wp-parser-function .comment-meta, .devhub-wrap.single-wp-parser-function .comment-meta a{
      background-color:#215f82 !important;
      color:white;
  }
    a, #wporg-footer ul li a {
    color: #4ca6cf;
  }
    a:visited, #wporg-footer ul li a:visited
    {
      color: #21759b;
  }
  
  
  /* CODE CSS */
  .syntaxhighlighter {
    background-color: #181818 !important;
  }
  .syntaxhighlighter .line.alt1 {
    background-color: #181818 !important;
  }
  .syntaxhighlighter .line.alt2 {
    background-color: #181818 !important;
  }
  .syntaxhighlighter .plain, .syntaxhighlighter .plain a {
    color: white !important;
  }
  
  .syntaxhighlighter .string, .syntaxhighlighter .string a {
    color: #ffff47 !important;
  }
  
  /* COMMENTS */
  .devhub-wrap .single-wp-parser-function .comment-meta, .devhub-wrap .single-wp-parser-method .comment-meta, .devhub-wrap .single-wp-parser-hook .comment-meta, .devhub-wrap .single-wp-parser-class .comment-meta {
    padding: .5em 1em;
    background-color: #181818;
    overflow: auto;
  }
  
  .devhub-wrap .user-note-voting-up .dashicons, .devhub-wrap .user-note-voting-down .dashicons {
    font-size: 30px;
    height: 30px;
    width: 30px;
    color: #fff;
  }
  
  .devhub-wrap .single-wp-parser-function .comment-list li,
  .devhub-wrap .single-wp-parser-function #comment-preview, .devhub-wrap .single-wp-parser-method .comment-list li,
  .devhub-wrap .single-wp-parser-method #comment-preview, .devhub-wrap .single-wp-parser-hook .comment-list li,
  .devhub-wrap .single-wp-parser-hook #comment-preview, .devhub-wrap .single-wp-parser-class .comment-list li,
  .devhub-wrap .single-wp-parser-class #comment-preview {
    margin-top: 3rem;
    background: #181818;
    overflow: auto;
  }
  

}
