css:
@-moz-document domain("isis.tu-berlin.de") {
/* Course List */

#semester_category_header {
    display: none;
}
#myrooms_container {
    display: table;
    width: 100%;
    padding-bottom: 0;
}
.semester_category {
    display: table-footer-group !important;
}
#myroomstextfilter {
    display: none;
}
.block_myrooms,
.block_myrooms .content {
    padding-bottom: 0 !important;
    margin-bottom: 0 !important;
}
/* Clickable Course Boxes */

.teacher_info {
    margin-top: -30px;
    pointer-events: none;
}


.coursebox {
    padding: 0 !important;
    border: 0 !important;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
}
.coursebox .title a {
    border: 1px dotted #ddd;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    display: block;
    padding: 5px;
    padding-bottom: 25px;
    text-decoration: underline;
}
.block {
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
    border-radius: 0;
}
.main {
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
}
.endright {
    display: none;
}
.navbar {
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
}
.forumheaderlist {
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
}
.forumpost {
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
    border-radius: 0;
}
.deletable4 {
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
}
#rightheader {
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
}
.navmeta {
    box-shadow: 0 4px 0px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
}

.search-box {
    margin-right: -40px;
    background-color: #CCCCCC;
}



.layout {
    margin-left: 5px;
    margin-right: 5px;
}

#main {
    margin-bottom: 15px;
    background-color: #FFFFFF;
    box-shadow: 0 4px 0px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);

}

#page-footer{
    display: none;
}

.endleft {
	display: none;
}

.discussion {
    background-color: #CCCCCC;
}
.box {
    border-radius: 0;
}
.course_title {
    border-radius: 0;
}
.starter {
    background-color: #FFFFFF;
}
.picture {
    background-color: #FFFFFF;
}
.title {
    background-color: #FFFFFF;
}
/* Breadcrumbs */

.breadcrumb-button {
    opacity: 0.3;
}
.breadcrumb li:nth-child(2),
.breadcrumb li:nth-child(3) {
    display: none;
}
/* Top menus & Login */

.dropdown-toggle {
    display: none !important;
}
.globalnav {
    min-height: 0 !important;
    height: auto !important;
    margin-left: auto !important;
    margin-right: auto !important;
    width: 50% !important;
    float: none !important;
}
.row-fluid .globalnav:nth-child(2) {
    display: none;
}
.aalborg_nav_wrapper {
    display: none;
}
.carousel-indicators {
    display: none;
}
/* Calendar */

.minicalendar td.hasevent.today {
    background-color: #FF9E6E;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
}
.breadcrumb-nav {
    background-color: #CCCCCC
}
body {
    background-color: #CCCCCC!important;
}
/* Course */

.aalborg_title {
    display: none;
}
.summary a,
.section a {
    text-decoration: underline !important;
}
}
