css:
@namespace url(http://www.w3.org/1999/xhtml);
/*Seven Familiar*/
/* i really want this to be global */
/*Images - pictures*/

#pet-headerPortrait-base {
    background: url("http://img01.deviantart.net/8b31/i/2016/304/e/8/mystic_yoosung_by_redvelvetshock-damvp34.png") -15px top no-repeat !important;
    background-size: auto 125px !important;
}
#pet-portrait {
    background: url("http://orig09.deviantart.net/ac45/f/2016/251/e/e/tired_yoosung_by_kuromokonachan-dagz7r7.png") center no-repeat !important;
    background-size: auto 450px !important;
    height: 450px !important;
}
#pet-portrait > img[src*="/static/img/pet/icon/"] {
    display: none !important;
}
/*Infos*/

#pet-info-name:before {
    content: "Yoosung Kim" !important;
    font-size: 20px !important;
}
#pet-info-name {
    font-size: 0 !important;
}
#pet-info-evolve > .on {
    font-size: 0 !important;
}
.pet-info-evolve-icon {
    font-size: 13px !important;
}
.pet-info-evolve-icon:after {
    content: " Yoosung se a enamorado mas de ti";
}
#pet-info-racegender:before {
    content: "Humano" !important;
    font-size: 16px !important;
    line-height: 20px !important;
}
#pet-info-racegender {
    font-size: 0 !important;
    margin-top: 5px;
}
img[src*="/static/img/f.png"] {
    background: url("http://www.eldarya.es/static/img/m.png") !important;
    padding-left: 14px !important;
    padding-top: 0px !important;
    width: 0 !important;
    height: 0 !important;
    margin-bottom: 5px !important;
}
/*Comida - food*/

#pet-info-food > img {
    background: url("http://p1.pichost.me/640/21/1439865.jpg") !important;
    background-size: 60px 60px !important;
    margin-top: -5px !important;
    padding-left: 60px !important;
    padding-top: 60px !important;
    width: 0 !important;
    height: 0 !important;
}
#pet-info-food {
    font-size: 0 !important;
}
#pet-info-food:before {
    content: " 'Fast Food' " !important;
    font-size: 20px !important;
}
#pet-info-food > span {
    font-size: 16px !important;
}
/*Yoosung en tu perfil*/

#player-display-pet {
    background: url("https://vignette2.wikia.nocookie.net/mystic-messenger/images/e/e7/Yoosung_render.png/revision/latest/scale-to-width-down/160?cb=20160920004034") no-repeat;
    background-size: auto 100%;
    height: 532px !important;
    width: 200px !important;
}
#player-display-pet > img {
    display: none !important;
}

img[src="/i18n/static/fr/logo.png"] {
    width: 0;
    height: 0;
    background: url("https://fotos.subefotos.com/5b7ad569d7c30f935699498a780decc7o.png");
    padding-left: 500px;
    padding-top: 205px;
    position: absolute;
    top: -0px;
    left: -70px;
}
