css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.creationkit.com") {
  :root {
    --FriendsListBarColor: #262626;
    --GamesListTitleColor: #343434;
    --GamesListColor: #161616;
    --SearchBarColor: #292A2E;
    --TopNavBarColor:#20232B;
    --DarkButtonsColor: #5D5D5D;
    --InstallButtonColor: #438FAD;
    --SaleBoxColor: #4C6B22;
    --BuyGameColor: #6F8C05;
    --SyntaxType: #E5B759;
    --SyntaxGeneral: #ABB2B2;
    --SyntaxKeyword: #56B6A8;
    --SyntaxComment: Orange;
    --SyntaxText: #8D983E;
    --SyntaxIfWhile: #C678BD;
    
    /*EXAMPLE: background-color: var(--TopBarColor);*/
  }
  /*WIKI UI - Logo*/
  a.mw-wiki-logo{
    background: transparent url("http://i.imgur.com/7fWOYlE.png");
  }
  #p-logo{
    background: transparent;
  }
  
  /*WIKI UI - Splashcreen Background*/
  #mw-head, .mediawiki, .noresize, #bodyContent, #content{
    background: var(--GamesListColor);
    color: white;
    border: none;
  }
  #firstHeading{
    background: var(--GamesListColor);
    color: white;
    height: auto;
  }
  
  /*WIKI UI - Account*/
  a{
    color: lightblue;
  }
  
  /*WIKI UI - General Text Color*/
  h1, h2, h3, h4, h5, h6, h7, h8, h9{
    color: Orange;
  }
  p{
    color: white;
  }
  #toc{
    background-color: var(--GamesListTitleColor);
    color: lightblue;
    border: 2px, white;
  }
  div.mw-geshi.mw-code.mw-content-ltr{
    background-color: var(--GamesListTitleColor);
    border: none;
  }
  .catlinks{
    background-color: var(--GamesListTitleColor);
    border: none;
    color: white;
  }
  /*Papyrus Syntax Color*/
  .papyrus.source-papyrus .kw5{
    color: var(--SyntaxKeyword);
  }
  pre, .mw-code, .papyrus.source-papyrus .sy0, .papyrus.source-papyrus .br0, .papyrus.source-papyrus .kw1{
    color: var(--SyntaxGeneral);
  }
  .papyrus.source-papyrus .kw3, .papyrus.source-papyrus .nu0{
    color: var(--SyntaxType);
  }
  .papyrus.source-papyrus .co1{
    color: var(--SyntaxComment);
  }
  .papyrus.source-papyrus .st0{
    color: var(--SyntaxText);
  }
  .papyrus.source-papyrus .kw1{
    color: var(--SyntaxIfWhile);
  }

 
  /*WIKI UI - TABS*/
  div.vectorTabs{
    visibility: hidden;
  }

  div.vectorTabs{
    visibility: show;
  }
  /*WIKI UI - Footer*/
  #footer-info-lastmod, #footer-poweredbyico{
    visibility: hidden;
  }
}
