css:
@-moz-document domain("workflowy.com") {
body {
  background: #FFFFFF;
}

.page {
  border: none;
  background: none;
}

#logo {
  display: none;
}

#header {
  background: "#FFFFFF";
  background: linear-gradient(
    to bottom,
    rgba(255, 255, 255, 1) 50%,
    rgba(255, 255, 255, 0) 100%
    );
  border: none;
  padding: 12px 0;
}

#header #searchBox {
  background-image: url('data:image/png;base64,');
  background-repeat: no-repeat;
  background-position: 0 4px;
  border: none;
  border-radius: 0;
  padding: 4px 6px 4px 26px;
  margin-left: 15px;
}
#header #searchForm.clearPrompt #searchBox,
#header #searchBox:focus {
  border-bottom: 2px #333333 solid;
}
#header #searchCancel {
  display: none !important;
}

#header .button,
#header #buttonBar {
  background: none;
  border: none;
  color: #333333;
}
#header #buttonBar .button .menu-icon {
  background: #333333;
  width: 4px;
}
#header .showCompletedButton,
#header .saveButton {
  width: auto;
  padding: 8px 20px;
}

#header .button.saved {
  color: #65DA90;
}

#savedViewHUDButton,
#getMoreSpaceButtonTopRight,
#bottomLinks,
.footer,
.proPitch {
  display: none;
}

.menu-options {
  background: #FFFFFF;
  border: none;
  border-top: 2px solid #333333;
  border-radius: 0;
}
.menu-options > .button {
  padding: 10px 20px;
}

.selected {
  border: none;
}
}
