css:
@-moz-document domain("www.phimmoi.net") {
[class*="ad-center"],
.col-left,
.sidebar .ad-container,
.ad-topcomment-pc,
[id*="ads_zone"],
#media-player > div.vtlai-pause-ad,
[id*="uniad-zone"],
.uniad-zonetype-preload-overlay,
iframe[id^="cto_iframe"],
iframe[id^="criteo"]{
    display: none!important
}
.row.movie-update .col-right {
    width: 98%!important;
    margin-left: 0!important
}
#tabs-movie li.ui-corner-top {
    text-align: center;
    width: 33.333333%!important
}
/*dynamic search*/
header .header {
    position: fixed;
    z-index: 99999!important
}
nav {
    margin-top: 75px!important
}
/*max-size zoomed mode*/
#watch-block.large-player #media-player:not(.jw-flag-fullscreen) {
    position: fixed!important;
    top: 0!important;
    z-index: 99999!important;
    height: calc(100% - 2.5em)!important;
    left: 0%!important
}
}
