css:
@-moz-document domain("tacomaworld.com") {


html {
    color: #000;
    background: #fff;
    min-height: 100%;
    min-width: 240px;
    overflow-y: scroll!important
}

table {
    border-collapse: collapse;
    border-spacing: 0;
    font: 100%
}

q:after,q:before {
    content: ''
}

abbr,acronym {
    border: 0;
    font-variant: normal;
    text-decoration: none
}

sub,sup {
    vertical-align: baseline
}

#headerMover {
    background-repeat: repeat;
    background-position: top
}

.titleBar h1 em {
    color: #b0b0b0
}

#sitenav div {
    background-color: inherit
}

.discussionList .sectionHeaders {
    background-color: #777
}

.vehicleDesc {
    color: #999
}

.profilePage .tabs.mainTabs {
    background: inherit
}

#convoPlusNavigation {
    background-color: #444
}

.profilePage .tabs.mainTabs li.active a {
    background-color: #ffb700
}

.relProd {
    background-color: #222
}

.errorPanel:after,.memberList:after,.nodeDescriptionTip:after,.profilePage .mast .primaryContent:after,.profilePage .mast .secondaryContent:after,.resourceListSidebar .secondaryContent:after,.sectionMain:after,.sidebar .secondaryContent:after,.xengallerySideBar .secondaryContent:after {
    background-repeat: no-repeat;
    background-position: 50% 0
}

.dataTable tr.dataRow td {
    border-top: 1px solid #4e4e4e;
    border-bottom: 1px solid #343434;
    padding: 5px 10px;
    word-wrap: break-word
}

.dataTable tr.dataRow th {
    background: #ffb700;
    background-image: -webkit-linear-gradient(top,rgba(255,255,255,.3) 0%,rgba(255,255,255,0) 100%);
    background-image: linear-gradient(to bottom,rgba(255,255,255,.3) 0%,rgba(255,255,255,0) 100%);
    color: #fff;
    font-size: 11px;
    padding: 6px 10px
}

.dataTable tr.dataRow th a {
    color: inherit;
    text-decoration: underline
}

.baseHtml abbr,.baseHtml acronym {
    border-bottom: 1px dotted #000;
    cursor: help
}

.baseHtml em {
    font-style: italic
}

.baseHtml dl dd {
    margin-left: 1em
}

.resourceTabs ul.tabs {
    padding-left: 0
}

@font-face {
    font-family:'Droid Sans';font-style:normal;font-weight:400;src:local('Droid Sans'),local('DroidSans'),url(https://fonts.gstatic.com/s/droidsans/v6/s-BiyweUPV0v-yRb-cjciPk_vArhqVIZ0nv9q090hN8.woff2) format('woff2');unicode-range:U+0000-00FF,U+0131,U+0152-0153,U+02C6,U+02DA,U+02DC,U+2000-206F,U+2074,U+20AC,U+2212,U+2215,U+E0FF,U+EFFD,U+F000
}

@font-face {
    font-family:'Droid Sans';font-style:normal;font-weight:700;src:local('Droid Sans Bold'),local('DroidSans-Bold'),url(https://fonts.gstatic.com/s/droidsans/v6/EFpQQyG9GqCrobXxL-KRMYWiMMZ7xLd792ULpGE4W_Y.woff2) format('woff2');unicode-range:U+0000-00FF,U+0131,U+0152-0153,U+02C6,U+02DA,U+02DC,U+2000-206F,U+2074,U+20AC,U+2212,U+2215,U+E0FF,U+EFFD,U+F000
}

select.textCtrl {
    word-wrap: normal;
    -webkit-appearance: menulist
}

textarea.textCtrl {
    word-wrap: break-word;
    resize: vertical
}

.sidebar textarea.textCtrl.Elastic {
    max-width: 97%
}

html .taggingInput .tag>span:first-child+a:last-child:before {
    content: '';
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0
}

.ctrlUnit.fullWidth dt dfn {
    display: inline;
    margin: 0
}

.ctrlUnit>dd>*>li:first-child>.textCtrl:first-child {
    margin-top: -3px
}

.ctrlUnit>dd>*>li>ul>li:first-child {
    margin-top: 6px
}

ul.autoCompleteList img.autoCompleteAvatar {
    float: left;
    margin-right: 3px;
    width: 24px;
    height: 24px
}

.xenForm .ctrlUnit.fullWidth dd .bbCodeEditorContainer textarea {
    margin-left: 0;
    min-height: 200px
}

html[dir=rtl] #helper_birthday input,html[dir=rtl] #helper_birthday select {
    direction: rtl
}

.formPopup .primaryControls input.textCtrl {
    margin-bottom: 0
}

#navigationContainer {
    position: relative;
    margin: 0 auto;
    background-color: #404040;
    background-image: -webkit-linear-gradient(top,rgba(255,255,255,.15) 0%,rgba(255,255,255,0) 100%);
    background-image: linear-gradient(to bottom,rgba(255,255,255,.15) 0%,rgba(255,255,255,0) 100%)
}

.navTabs .navLink strong {
    display: inline;
    float: left;
    height: 30px
}

#AccountMenu .menuColumns ul {
    float: left;
    padding: 0;
    max-height: none;
    overflow: hidden
}

#AccountMenu .statusPoster textarea {
    width: 245px;
    margin: 0;
    resize: vertical;
    overflow: hidden
}

.navPopup .listPlaceholder ol.secondaryContent {
    padding: 0 10px
}

#QuickSearch input.button.primary {
    float: left;
    width: 110px
}

input[type=search]::-webkit-search-decoration,input[type=search]::-webkit-search-results-button,input[type=search]::-webkit-search-results-decoration {
    display: none
}

.NoResponsive body {
    min-width: 976px
}

.sidebar .section .primaryContent h3:after,.sidebar .section .secondaryContent h3:after {
    border-bottom: 1px solid #343434;
    content: '';
    display: block;
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    height: 0
}

a.callToAction span {
    font-weight: 700;
    font-size: 12px;
    font-family: 'Trebuchet MS',Verdana,Geneva,Arial,Helvetica,sans-serif;
    color: #303030;
    background-color: #ffb700;
    padding: 0 14px;
    border: 1px solid #ffb700;
    display: block;
    box-shadow: 0 0 0 1px rgba(0,0,0,.15) inset;
    line-height: 24px;
    background-image: -webkit-linear-gradient(top,rgba(255,255,255,.15) 0%,rgba(255,255,255,0) 100%),-webkit-linear-gradient(left,rgba(255,255,255,0) 0%,rgba(255,255,255,.1) 50%,rgba(255,255,255,0) 100%);
    background-image: linear-gradient(to bottom,rgba(255,255,255,.15) 0%,rgba(255,255,255,0) 100%),linear-gradient(to right,rgba(255,255,255,0) 0%,rgba(255,255,255,.1) 50%,rgba(255,255,255,0) 100%);
    min-height: 24px;
    box-sizing: content-box
}

del,ins {
    text-decoration: none
}

a.callToAction span small.minorText {
    display: block;
    line-height: 1.2em;
    padding-bottom: 10px
}

#StackAlerts .stackAlertContent a {
    color: inherit
}

blockquote,body,button,code,dd,div,dl,dt,fieldset,form,h1,h2,h3,h4,h5,h6,input,legend,li,ol,p,pre,td,textarea,th,ul {
    margin: 0;
    padding: 0
}

fieldset,img {
    border: 0
}

address,caption,cite,code,dfn,em,optgroup,option,strong,th,var {
    font-style: inherit;
    font-weight: inherit
}

li {
    list-style: none
}

[dir=auto],caption,th {
    text-align: left
}

h1,h2,h3,h4,h5,h6 {
    font-size: 100%;
    font-weight: 400
}

legend {
    color: #000
}

optgroup,option {
    font-family: inherit;
    font-size: inherit
}

button,input,select,textarea {
    *font-size: 100%;
    font: 99% arial,helvetica,clean,sans-serif
}

body {
    font: 13px/1.231 arial,helvetica,clean,sans-serif;
    *font: x-small;
    -webkit-text-size-adjust: 100%;
    -moz-text-size-adjust: 100%;
    -ms-text-size-adjust: 100%;
    text-size-adjust: 100%;
    font-family: 'Trebuchet MS',Helvetica,Arial,sans-serif;
    color: black;
    word-wrap: break-word;
    line-height: 1.28
}

code,kbd,pre,samp,tt {
    font-family: monospace;
    *font-size: 108%;
    line-height: 100%
}

img:-moz-broken,img:-moz-user-disabled {
    -moz-force-broken-image-icon: 1
}

img:-moz-broken:not([width]),img:-moz-user-disabled:not([width]) {
    width: 50px
}

img:-moz-broken:not([height]),img:-moz-user-disabled:not([height]) {
    height: 50px
}

#Mover {
    background-color: #2b2b2b;
    background-repeat: repeat;
    background-position: top
}

pre,textarea {
    word-wrap: normal
}

a:link,a:visited {
    color: #ccc;
    text-decoration: none
}

a[href]:hover {
    color: #ffb700;
    text-decoration: underline
}

a:hover {
    _color: #ffb700;
    _text-decoration: underline
}

a.noOutline {
    outline: 0
}

.emCtrl:focus,.emCtrl:hover,.ugc a:focus,.ugc a:hover {
    text-decoration: none;
    box-shadow: 5px 5px 7px rgba(0,0,0,.5);
    outline: 0
}

.emCtrl:active,.ugc a:active {
    position: relative;
    top: 1px;
    box-shadow: 5px 5px 7px rgba(0,0,0,.5);
    outline: 0
}

.ugc a:link,.ugc a:visited {
    color: #ffb700;
    padding: 0 3px;
    margin: 0 -3px
}

.ugc a:focus,.ugc a:hover {
    color: #7e5a00;
    background-color: #404040
}

.titleBar {
    margin-bottom: 10px;
    zoom: 1
}

#header .pageWidth .pageContent:after,.titleBar:after {
    content: '.';
    display: block;
    height: 0;
    clear: both;
    visibility: hidden
}

.titleBar h1 {
    font-size: 18pt;
    overflow: hidden;
    zoom: 1;
    text-shadow: 1px 1px 2px rgba(0,0,0,.5)
}

.titleBar h1 .Popup {
    float: left
}

#pageDescription {
    font-size: 11px;
    color: #888;
    margin-top: 2px
}

.topCtrl {
    float: right
}

.topCtrl h2 {
    font-size: 12pt
}

img {
    -ms-interpolation-mode: bicubic
}

a.avatar {
    *cursor: pointer
}

.avatar .img,.avatar img,.avatarCropper {
    background-color: #404040;
    padding: 2px;
    border: 1px solid #404040
}

.avatar.plainImage .img,.avatar.plainImage img {
    border: 0;
    border-radius: 0;
    padding: 0;
    background-color: transparent;
    background-position: left top
}

.avatar .img {
    display: block;
    background-repeat: no-repeat;
    background-position: 2px 2px;
    text-indent: 1000px;
    overflow: hidden;
    white-space: nowrap;
    word-wrap: normal
}

.avatar .img.s {
    width: 48px;
    height: 48px
}

.avatar .img.m {
    width: 96px;
    height: 96px
}

.avatar .img.l,.avatarCropper {
    width: 192px;
    height: 192px
}

.avatarCropper {
    direction: ltr
}

.avatarCropper a,.avatarCropper label,.avatarCropper span {
    overflow: hidden;
    position: relative;
    display: block;
    width: 192px;
    height: 192px
}

.avatarCropper img {
    padding: 0;
    border: 0;
    border-radius: 0;
    position: relative;
    display: block
}

.avatarScaler img {
    max-width: 192px;
    _width: 192px
}

.ctrlUnit>dd .explain b,.ctrlUnit>dd .explain strong,.ctrlUnit>dt dfn b,.ctrlUnit>dt dfn strong,body .dimmed,body .dimmed a,body a.dimmed {
    color: #b0b0b0
}

body .muted,body .muted a,body a.muted {
    color: #888
}

body .faint,body .faint a,body a.faint {
    color: #707070
}

.highlight {
    font-weight: 700
}

.cloaked,.cloaked a,.concealed,.concealed a {
    text-decoration: inherit!important;
    color: inherit!important
}

.concealed a:hover,a.concealed:hover {
    text-decoration: underline!important
}

.xenPreviewTooltip,.xenTooltip {
    display: none;
    z-index: 15000;
    cursor: default
}

.xenTooltip {
    font-size: 11px;
    color: #fff;
    background-color: #303030;
    padding: 5px 10px;
    border-radius: 0
}

.importantMessage a,.xenTooltip a,.xenTooltip a:hover {
    color: #fff;
    text-decoration: underline
}

.xenTooltip .arrow {
    border-top: 6px solid #303030;
    border-right: 6px solid transparent;
    border-bottom: 1px none #000;
    border-left: 6px solid transparent;
    position: absolute;
    bottom: -6px;
    line-height: 0;
    width: 0;
    height: 0;
    left: 9px;
    _display: none
}

.xenTooltip.flipped .arrow {
    left: auto;
    right: 9px
}

.xenTooltip.statusTip {
    padding: 5px 10px;
    line-height: 17px;
    width: 250px;
    height: auto
}

.xenTooltip.statusTip .arrow {
    border: 6px solid transparent;
    border-right-color: #303030;
    border-left: 1px none #000;
    top: 6px;
    left: -6px;
    bottom: auto;
    right: auto
}

.xenTooltip.iconTip {
    margin-left: -6px
}

.xenTooltip.iconTip.flipped {
    margin-left: 7px
}

#PreviewTooltip {
    display: none
}

.xenPreviewTooltip {
    border: 10px solid #606060;
    border-radius: 10px;
    position: relative;
    box-shadow: 0 12px 25px rgba(0,0,0,.75);
    width: 400px;
    border-color: #606060;
    border-color: rgba(96,96,96,.75);
    _border-color: #606060
}

.xenPreviewTooltip .arrow,.xenPreviewTooltip .arrow span {
    border-top: 15px solid #404040;
    border-right: 15px solid transparent;
    border-bottom: 1px none #606060;
    border-left: 15px solid transparent;
    position: absolute
}

.xenPreviewTooltip .arrow {
    bottom: -15px;
    _display: none;
    left: 22px
}

.xenPreviewTooltip .arrow span {
    top: -17px;
    left: -15px
}

.xenPreviewTooltip .primaryContent,.xenPreviewTooltip .secondaryContent,.xenPreviewTooltip .section,.xenPreviewTooltip .sectionMain {
    background: 0 0;
    padding: 0;
    border-width: 0;
    border-radius: 0;
    box-shadow: none!important;
    margin: 0
}

.xenPreviewTooltip .section {
    background-color: #404040;
    background-image: -webkit-linear-gradient(top,rgba(255,255,255,.03) 0%,rgba(255,255,255,0) 70%);
    background-image: linear-gradient(to bottom,rgba(255,255,255,.03) 0%,rgba(255,255,255,0) 70%)
}

.xenPreviewTooltip .previewContent {
    overflow: hidden;
    zoom: 1;
    min-height: 1em;
    padding: 10px
}

.importantMessage {
    position: relative;
    color: #282828;
    background-color: #ffb700;
    padding: 5px;
    margin: 10px 0 12px;
    border: 1px solid #ffb700;
    text-align: center;
    box-shadow: 0 0 0 1px rgba(0,0,0,.15) inset;
    background-image: -webkit-linear-gradient(top,rgba(255,255,255,.1) 0%,rgba(255,255,255,0) 100%),-webkit-linear-gradient(left,rgba(255,255,255,.1) 0%,rgba(255,255,255,0) 50%,rgba(255,255,255,.1) 100%);
    background-image: linear-gradient(to bottom,rgba(255,255,255,.1) 0%,rgba(255,255,255,0) 100%),linear-gradient(to right,rgba(255,255,255,.1) 0%,rgba(255,255,255,0) 50%,rgba(255,255,255,.1) 100%);
    -moz-osx-font-smoothing: grayscale
}

.importantMessage a {
    font-weight: 600;
    color: inherit!important
}

.section {
    margin: 10px auto
}

.tabs+.section {
    margin-top: 0
}

.find_new_posts_none .tabs+.section,.xengallery_find_new_no_response .tabs+.section {
    margin-top: 10px
}

.sectionMain>.section:first-child,.sectionMain>table.dataTable:first-child {
    margin-top: 0
}

.sectionMain>.section:last-child,.sectionMain>table.dataTable:last-child {
    margin-bottom: 0
}

.dataTable caption,.heading {
    color: #ccc
}

.dataTable caption,.heading,.xenForm .formHeader {
    font-weight: 700;
    font-size: 11pt;
    padding: 5px 10px;
    margin-bottom: 3px
}

.heading a {
    color: #ccc
}

.editorPopup .heading,.xenForm .formHeader,.xenOverlay .heading {
    background-color: #ffb700;
    color: #fff;
    border-bottom-width: 0
}

.subHeading {
    font-size: 11px;
    color: #888;
    padding: 5px 10px;
    margin: 3px auto 0
}

#customFooter .boxContent a,#customFooter .boxContent a:visited,.pairs dt,.pairsColumns dt,.pairsInline dt,.pairsJustified dt,.pairsRows dt,.subHeading a {
    color: #888
}

.primaryContent a,.secondaryContent a {
    color: #ccc
}

.sectionFooter {
    overflow: hidden;
    zoom: 1;
    font-size: 11px;
    color: #888;
    background: 0 0;
    padding: 4px 10px;
    border-top: 1px solid #4e4e4e;
    border-bottom: 1px solid #343434;
    line-height: 16px
}

.handleReportForm .sectionFooter {
    margin: 10px 0;
    background: #404040
}

.section .secondaryContent+.sectionFooter {
    background: 0 0;
    border-width: 0
}

.sectionFooter .left {
    float: left
}

.sectionFooter .right {
    float: right
}

.actionList,.formPopup .submitUnit dd {
    text-align: center
}

.opposedOptions {
    overflow: hidden;
    zoom: 1
}

.opposedOptions .left {
    float: left
}

.opposedOptions .right {
    float: right
}

.primaryContent,.secondaryContent,.sectionMain {
    background-color: #404040;
    border: 1px solid #404040;
    box-shadow: 0 0 0 1px #383838 inset;
    position: relative
}

.sectionMain {
    background-image: -webkit-linear-gradient(top,rgba(255,255,255,.04) 0%,rgba(255,255,255,0) 50%);
    background-image: linear-gradient(to bottom,rgba(255,255,255,.04) 0%,rgba(255,255,255,0)50 75%)
}

.primaryContent,.secondaryContent {
    padding: 9px;
    margin-bottom: 10px
}

#recentActivity,#warnings>div[class=""],.memberList,.primaryContent,.resourceList,.secondaryContent {
    background-image: -webkit-linear-gradient(top,rgba(255,255,255,.04) 0%,rgba(255,255,255,0) 50%);
    background-image: linear-gradient(to bottom,rgba(255,255,255,.04) 0%,rgba(255,255,255,0)50 75%)
}

#recentActivity,#warnings>div[class=""],.resourceList {
    padding: 9px;
    margin-bottom: 10px
}

#ProfilePanes #info,#recentActivity,#warnings>div[class=""],.discussionList,.memberList,.messageList .message.messageFullBlock,.resourceList {
    background-color: #404040;
    border: 1px solid #404040;
    box-shadow: 0 0 0 1px #383838 inset;
    position: relative
}

#ProfilePanes #info,.memberList,.messageList .message.messageFullBlock {
    padding: 9px;
    margin-bottom: 10px
}

.messageList li.message {
    background-color: #2b2b2b!important;
    border: 0
}

.FollowList.memberList,.sidebar .sectionMain .secondaryContent,.watch_forums .sectionMain,a.primaryContent,a.secondaryContent,form.sectionMain .secondaryContent {
    background: 0 0;
    padding: 0;
    margin: 0;
    border-width: 0;
    border-radius: 0;
    box-shadow: none!important
}

.errorPanel:after,.memberList:after,.nodeDescriptionTip:after,.profilePage .mast .primaryContent:after,.profilePage .mast .secondaryContent:after,.resourceListSidebar .secondaryContent:after,.sectionMain:after,.sidebar .secondaryContent:after,.xengallerySideBar .secondaryContent:after {
    background-size: 100% 30px;
    content: '';
    display: block;
    position: absolute;
    bottom: -11px;
    left: 3px;
    right: 3px;
    pointer-events: none;
    height: 10px
}

.nodeDescriptionTip:after,.resourceListSidebar .secondaryContent:after {
    bottom: -10px
}

.resourceListSidebar .secondaryContent:after {
    bottom: -11px
}

.FollowList.memberList:after,.sidebar .sectionMain .secondaryContent:after,.watch_forums .sectionMain:after,form.sectionMain .secondaryContent:after {
    display: none
}

#recentActivity:after,.importantMessage:after,.message .messageInfo:after,.messageList .message.messageFullBlock:after,.primaryContent.resourceHistory:after,.profilePage #info:after {
    content: '';
    display: block;
    position: absolute;
    left: 0;
    right: 0;
    bottom: -11px;
    height: 10px
}

.mainContentBlock:after {
    display: none
}

.discussionList,.sectionMain {
    padding: 3px 7px;
    margin: 10px auto 20px
}

.columns {
    overflow: hidden;
    zoom: 1
}

.columns .columnContainer,.tabs li {
    float: left
}

.columns .columnContainer .column {
    margin-left: 3px
}

.columns .columnContainer:first-child .column {
    margin-left: 0
}

.c50_50 .c1,.c50_50 .c2 {
    width: 49.99%
}

.c70_30 .c1 {
    width: 70%
}

.c70_30 .c2 {
    width: 29.99%
}

.c60_40 .c1 {
    width: 60%
}

.c60_40 .c2 {
    width: 39.99%
}

.c40_30_30 .c1 {
    width: 40%
}

.c40_30_30 .c2,.c40_30_30 .c3 {
    width: 29.99%
}

.c50_25_25 .c1 {
    width: 50%
}

.c50_25_25 .c2,.c50_25_25 .c3 {
    width: 25%
}

.tabs {
    font-size: 11px;
    word-wrap: normal;
    min-height: 23px;
    _height: 23px;
    display: table;
    width: 100%;
    *width: auto;
    box-sizing: border-box
}

.tabs li a,.tabs.noLinks li {
    color: #fff;
    text-decoration: none;
    background-color: #606060;
    padding: 0 8px;
    margin-right: 1px;
    border: 1px solid #606060;
    display: inline-block;
    line-height: 22px;
    cursor: pointer;
    outline: 0;
    white-space: nowrap;
    word-wrap: normal;
    background-image: -webkit-linear-gradient(top,rgba(255,255,255,.03) 0%,rgba(255,255,255,0) 100%);
    background-image: linear-gradient(to bottom,rgba(255,255,255,.03) 0%,rgba(255,255,255,0) 100%);
    height: 22px
}

.tabs li:hover a,.tabs.noLinks li:hover {
    color: #282828;
    text-decoration: none;
