    font-size: 20px;
    line-height: 1;
    color: #aeaeae;
    opacity: .75;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale
}

.slick-prev {
    left: -25px
}

[dir=rtl] .slick-prev {
    left: auto;
    right: -25px
}

.slick-prev:before {
    content: ""
}

.slick-next:before,[dir=rtl] .slick-prev:before {
    content: ""
}

.slick-next {
    right: -25px
}

[dir=rtl] .slick-next {
    left: -25px;
    right: auto
}

[dir=rtl] .slick-next:before {
    content: ""
}

