css:
/* ==UserStyle==
@name           4chan Minimal Dark Blue
@namespace      USO Archive
@author         mob101
@description    `Turns 4chan into a minimal dark theme with dark blue accents.`
@version        20201111.10.44
@license        CC0-1.0
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("4chan.org"),
domain("4channel.org") {
    html {
        background: #212121;
    }
    body {
        background: #212121;
        color: lightgrey;
        font-family: 'Roboto', 'Helvetica', 'Arial', sans-serif;
        font-size: 14px;
        font-weight: 400;
        line-height: 20px;
        padding: 0;
        margin: 0;
    }

    #threads div {
        border-top: 0;
        margin-top: 0;
        padding-top: 0;
    }

    #ctrl-top a,
    .btn-wrap a.button,
    .btn-wrap span.button {
        color: #fff;
        background: rgb(23, 23, 23);
        padding: 10px;
        border-radius: 10px;
    }

    #ctrl-top a:hover,
    .btn-wrap a.button:hover,
    .btn-wrap span.button:hover {
        background: #22313F;
    }

    select {
        border: 1px solid #999;
        font-size: 16px;
        color: #fff;
        background-color: #212121;
        border-radius: 5px;
    }

    body div.center,
    hr,
    #blotter,
    .thread-stats {
        visibility: hidden;
    }

    div#content {
        margin: 0 1rem;
    }

    div#ctrl {
        margin: 0 9rem;
    }

    #boardNavDesktop.desktop,
    #boardNavDesktopFoot.desktop,
    div#absbot {
        background: #212121;
    }
    .post.op,
    .post.reply,
    .summary.desktop {
        background-color: #22313F;
    }
    .post.reply {
        width: 100%;
    }
    span.quote {
        color: red;
    }
    .board {
        background: #212121;
        border-top: 1px solid #ccc;
        margin-bottom: 50px;
        padding-bottom: 25px;
    }
    .expanded-thumb {
        width: 100%;
        height: auto;
    }
    .is_index .board {
        padding-bottom: 50px
    }
    a,
    a:visited,
    .quoteLink,
    .quotelink,
    .deadlink,
    div#boardNavMobile .pageJump a,
    .persistentNav .pageJump a,
    .summary a.replylink,
    div.post div.postInfo span.postNum a:visited,
    div.post div.postInfo span.postNum a.replylink {
        color: lightgrey!important;
        text-decoration: none!important;
    }
    a:hover,
    .quoteLink:hover,
    .quotelink:hover,
    .deadlink:hover,
    .summary a.replylink:hover,
    .persistentNav .pageJump a:hover,
    div#boardNavMobile .pageJump a:hover,
    div.post div.postInfo span.postNum a:hover,
    .posteruid .hand:hover {
        color: rgb(83, 109, 254)!important;
        text-decoration: none!important;
    }
    .postInfo a.postMenuBtn,
    .postInfo a.postMenuBtn:hover {
        color: rgb(83, 109, 254)!important;
        text-decoration: none!important;
    }
    div.postContainer {
        display: block;
        width: 80%;
        margin: 15px auto 0 auto;
        overflow: hidden;
    }
    .thread {
        margin-top: 30px!important;
        padding-top: 15px;
        border-top: 1px solid #ccc;
    }
    .thread:first-child {
        padding-top: 0;
        border-top: none;
    }
    .md-plus-btn {
        border-radius: 50%;
        font-size: 24px;
        height: 56px;
        margin: auto;
        min-width: 56px;
        width: 56px;
        padding: 0;
        overflow: hidden;
        box-shadow: 0 1px 1.5px 0 rgba(0, 0, 0, .12), 0 1px 1px 0 rgba(0, 0, 0, .24);
        /*     position: relative; */
        line-height: normal;
        position: fixed;
        right: 25px;
        bottom: 25px;
    }
    div.boardBanner > div.boardTitle {
        margin-top: 40px;
        font-size: 36px;
        color: #676767;
        margin-bottom: 40px;
        font-family: 'Roboto', 'Helvetica', 'Arial', sans-serif;
        font-weight: normal;
        letter-spacing: 0;
    }
    :-ms-input-placeholder {
        color: rgba(0, 0, 0, .35)
    }
    ::-webkit-input-placeholder {
        color: rgba(0, 0, 0, .35)
    }
    :-moz-placeholder {
        color: rgba(0, 0, 0, .35)
    }
    ::-moz-placeholder {
        color: rgba(0, 0, 0, .35)
    }
    #togglePostFormLink,
    .ad-plea,
    #blotter tfoot td,
    #ctrl-top,
    div.post div.postInfo span.postNum,
    div.pagelist div.pages {
        color: transparent
    }
    table.postForm > tbody > tr > td:first-child,
    .thread-stats {
        color: #000
    }
    .postInfo .backlink a.quotelink,
    .postInfo .backlink a.quotelink:hover {
        color: rgb(83, 109, 254)!important
    }
    .ws input[type="text"],
    .nws input[type="text"] {
        border: none!important;
        border-bottom: 1px solid rgba(0, 0, 0, .12)!important;
        font-size: 14px!important;
        font-family: "Helvetica", "Arial", sans-serif;
        padding: 4px!important;
        -webkit-transition: border-bottom-color 0.2s;
        transition: border-bottom-color 0.2s;
    }
    .ws input[type="text"]:focus,
    .ws #quickReply input[type="submit"]:focus {
        border-bottom: 1px solid rgb(83, 109, 254)!important
    }
    .nws input[type="text"]:focus,
    .nws #quickReply input[type="submit"]:focus {
        border-bottom: 1px solid rgb(244, 67, 54)!important
    }
    .tomorrow .extPanel,
    .recaptcha_input_area #recaptcha_response_field {
        border: 0!important
    }
    .ws input[type="submit"],
    .nws input[type="submit"],
    button {
        border: none;
        border-radius: 2px;
        position: relative;
        height: 28px;
        min-width: 64px;
        display: inline-block;
        text-transform: uppercase;
        outline: none;
        cursor: pointer;
        background: rgb(83, 109, 254);
        color: #fff;
        font-size: 14px;
        font-family: 'Roboto', 'Helvetica', 'Arial', sans-serif;
        line-height: 28px;
        margin-left: 8px;
    }
    .nws input[type="submit"],
    .nws button {
        background: rgb(244, 67, 54)
    }
    #qrCaptchaContainerAlt {
        margin-bottom: 8px
    }
    #postForm input[type="text"] {
        margin: 4px 0
    }
    #postForm td:first-child {
        text-align: right;
        padding-right: 10px;
    }
    .ws textarea,
    .nws textarea {
        border: 0!important
    }
    .ws textarea:focus,
    .nws textarea:focus {
        border: 0!important
    }
    table#postForm td {
        background: transparent;
        border: 0;
        font-weight: normal;
    }
    #toggleMsgBtn {
        margin-left: 5px!important
    }
    .rules {
        text-align: center
    }
    #qrHeader {
        background: #676767!important;
        color: #fff!important;
    }
    #qrHeader a {
        color: #fff!important
    }
    #search-box {
        height: inherit;
        line-height: inherit;
        margin: 0;
        padding: inherit;
    }
    #ctrl-top {
        padding-bottom: 10px;
        border-top: 1px solid #ddd;
        padding-top: 10px;
        text-align: center;
    }
    .reply:target,
    .reply.highlight {
        background: #50728E !important;
        padding: 10px!important;
        border: 0!important;
    }
    .navLinks + hr,
    .open-qr-wrap,
    .board hr,
    #bannerCnt,
    #ctrl-top + hr,
    #ctrl-top > hr {
        display: none
    }
    .navLinks {
        padding-bottom: 8px;
        color: transparent;
        width: 80%;
        margin: auto;
    }
    .navLinks label {
        color: rgb(83, 109, 254)
    }
    .navLinks label + span {
        color: #000
    }
    .navLinksBot {
        margin-top: 30px;
        text-align: left;
    }
    div#boardNavDesktop,
    div#boardNavDesktopFoot,
    div#boardNavMobile {
        background: #fff;
        padding: 5px 10px;
        box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .08), 0 3px 1px -2px rgba(0, 0, 0, .06), 0 1px 5px 0 rgba(0, 0, 0, .04);
        margin-bottom: 25px;
        color: transparent!important;
        font-size: 14px;
    }
    div#boardNavMobile {
        color: #000;
        border: 0;
    }
    div#boardNavDesktopFoot {
        margin-bottom: 0;
        margin-top: 25px;
        padding-top: 25px;
        padding-bottom: 25px;
    }
    div#boardNavDesktop a,
    div#boardNavDesktopFoot a {
        margin-left: -3px;
        margin-right: -3px;
        padding: 0;
    }
    .persistentNav .pageJump a {
        margin: 0;
        padding-right: 5px;
    }
    div.pagelist {
        background: #1A2126;
        margin-top: 25px;
        margin-bottom: 25px;
        border: 0;
        box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
        margin-left: 25px;
        padding: 8px;
        border-radius: 2px;
    }
    div#absbot {
        background: #212121;
        color: #999;
    }
    div#absbot #footer-links a {
        text-decoration: none!important;
        color: rgb(83, 109, 254)!important;
    }
    div#absbot #footer-links a:hover {
        text-decoration: underline!important
    }
    .bottomCtrl {
        display: none
    }
    .ad-plea-bottom + hr {
        display: none
    }
    div#boardNavDesktopFoot {
        margin-bottom: 0
    }
    div.post,
    .extPanel,
    div.reply,
    .dd-menu ul {
        background: #fff;
        border-radius: 2px;
        box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
        border: none;
    }
    div.pagelist div.cataloglink {
        border: 0
    }
    div.post {
        width: 100%;
        margin: 0;
        padding: 10px;
    }
    .dd-menu li {
        padding: 6px 10px!important;
        border: 0!important;
    }
    .dd-menu li:hover {
        background-color: #eee!important
    }
    #quote-preview {
        border: 0!important;
        padding: 10px!important;
    }
    div.op {
        display: block;
        max-width: none;
    }
    .fileText {
        color: #999;
        font-size: 12px;
        margin-top: 2px;
    }
    .op .fileText {
        margin-top: -5px;
        margin-bottom: 5px;
    }
    .fileText a {
        text-decoration: none;
    }
    .posteruid,
    .dateTime {
        color: #999
    }
    .postInfo input[type="checkbox"] {
        display: none
    }
    div.sideArrows {
        display: none
    }
    hr {
        border: 0;
        border-bottom: 1px solid #ddd;
    }
    div.post:after {
        display: block;
        content: ' ';
        clear: both;
    }
    div.post div.file .fileThumb img {
        object-fit: cover;
        height: 90vh;
    }
    div.op div.file .fileThumb img:not(.expanded-thumb) {
        border-radius: 0px !important;
        width: 150px!important;
        height: 150px!important;
    }
    div.reply div.file .fileThumb img:not(.expanded-thumb) {
        border-radius: 0px !important;
        width: 75px!important;
        height: 75px!important;
    }

    .postMessage {
        margin-left: 20px;
        margin-top: 5px;
    }
    #boardNavDesktop::after {
        content: ' ';
        display: block;
        clear: both;
    }
    span.summary {
        border-radius: 0 0 2px 2px;
        background-color: #f5f5f5;
        width: 80%;
        display: block;
        margin: -1px auto 0 auto;
        box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
        border-top: 1px solid rgba(0, 0, 0, 0.1);
        text-indent: 10px;
        padding: 10px 0;
    }

    /* cyrillic-ext */
    @font-face {
        font-family: 'Roboto';
        font-style: normal;
        font-weight: 400;
        src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/ek4gzZ-GeXAPcSbHtCeQI_esZW2xOQ-xsNqO47m55DA.woff2) format('woff2');
        unicode-range: U+0460-052F, U+20B4, U+2DE0-2DFF, U+A640-A69F;
    }

    /* cyrillic */
    @font-face {
        font-family: 'Roboto';
        font-style: normal;
        font-weight: 400;
        src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/mErvLBYg_cXG3rLvUsKT_fesZW2xOQ-xsNqO47m55DA.woff2) format('woff2');
        unicode-range: U+0400-045F, U+0490-0491, U+04B0-04B1, U+2116;
    }

    /* greek-ext */
    @font-face {
        font-family: 'Roboto';
        font-style: normal;
        font-weight: 400;
        src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/-2n2p-_Y08sg57CNWQfKNvesZW2xOQ-xsNqO47m55DA.woff2) format('woff2');
        unicode-range: U+1F00-1FFF;
    }

    /* greek */
    @font-face {
        font-family: 'Roboto';
        font-style: normal;
        font-weight: 400;
        src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/u0TOpm082MNkS5K0Q4rhqvesZW2xOQ-xsNqO47m55DA.woff2) format('woff2');
        unicode-range: U+0370-03FF;
    }

    /* vietnamese */
    @font-face {
        font-family: 'Roboto';
        font-style: normal;
        font-weight: 400;
        src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/NdF9MtnOpLzo-noMoG0miPesZW2xOQ-xsNqO47m55DA.woff2) format('woff2');
        unicode-range: U+0102-0103, U+1EA0-1EF1, U+20AB;
    }

    /* latin-ext */
    @font-face {
        font-family: 'Roboto';
        font-style: normal;
        font-weight: 400;
        src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/Fcx7Wwv8OzT71A3E1XOAjvesZW2xOQ-xsNqO47m55DA.woff2) format('woff2');
        unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;
    }

    /* latin */
    @font-face {
        font-family: 'Roboto';
        font-style: normal;
        font-weight: 400;
        src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/CWB0XYA8bzo0kSThX0UTuA.woff2) format('woff2');
        unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
    }
}

@-moz-document domain("4chan.org"),
domain("4channel.org") {
    body div#doc div:nth-child(7) {
        display: none;
    }
    #site-stats .boxcontent {
        margin-top: 0;
        padding-bottom: 2em;
    }
    div.boxcontent {
        background-color: #22313F;
        padding-bottom: 0px;
    }
    div#boards,
    div#popular-threads,
    div#site-stats {
        padding-bottom: 0;
    }
    .boxbar {
        background-color: #22313F;
    }
    div#ft ul {
        display: none;
    }
    div#copyright {
        margin: 0;
    }
    div.post {
        margin: 0 auto;
    }
    .box-outer {
        padding-bottom: 0;
    }
    .boxcontent {
        margin-top: 0;
    }
    img {
        max-width: auto;
        max-height: 320px;
    }
    #logo {
        background: url('https://unsplash.it/300/120');
    }
    .extPanel.reply {
        background: #191919;
    }
    #wot-cnt > p {
        color: lightgray;
    }
    .bd {
        background-color: #2D404F;
    }
    /* .extended-large .thumb{
    height: 250px;
    width: 250px;
    overflow: hidden;
    object-fit: cover;
}
.extended-small .thumb{
    height: 150px;
    width: 150px;
    overflow: hidden;
    object-fit: cover;
} */
}

@-moz-document domain("4chan.org"),
domain("4channel.org") {
    div#boardNavDesktop,
    div#boardNavDesktopFoot,
    div#boardNavMobile {
        background: #2D404F;
    }
    #boardSelectMobile {
        color: lightgray;
    }
    div#theme {
        background: #22313F;
    }
    #theme kbd {
        color: black;
    }
    #theme-css {
        max-width: 320px;
    }
    .adg-rects {
        display: none;
    }
    @media only screen and (max-width: 480px) {
        div.postContainer {
            width: 100%;
        }
        div.opContainer {
            background: none !important;
            border: none !important;
        }
        div.post {
            max-width: none;
        }
        div.post div.postInfoM {
            border-bottom: none;
            background: none;
        }
        div.replyContainer {
            background: none;
        }
        div.postLink {
            background-color: #22313F !important;
        }
        .backlink.mobile {
            background: none;
        }
        .button {
            background-image: none;
            background: none;
            border: none;
            color: white !important;
        }
        div.postLink span.info {
            color: white;
        }
        div.post div.postInfoM span.dateTime {
            color: lightgray !important;
        }
        div.post div.postInfoM span.dateTime a {
            color: white !important;
        }
        div.postLink {
            margin: 0 auto;
        }
        div.postLink a {
            color: white !important;
        }
        div.boardBanner > div.boardTitle {
            margin-top: 55px;
        }
    }
}
