/* BY ZECHARIAH GOSSE */
@-moz-document domain("open.spotify.com")
{
/* Written by Zachary Gosse 2017 */
/* Version 1.1 */


/* Background layer */
._1YMukI_FrE7QRuUVSvq_Qe::after {
  
  /******     BACKGROUND FOR SPOTIFY JUST CHANGE URL :)   *********/
  background: url("https://raw.githubusercontent.com/cyberlapse/SpotifyStylishExtension/gh-pages/bokeh%20evergreen.jpg")!important;
    
  content: "";
  background-size: cover !important;
  opacity: 0.5;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  position: absolute;
  z-index: -1;   
}


/* Release Radar Cover Image */
.cover-art-image.cover-art-image-loaded[style="background-image: url(\"https://i.scdn.co/image/0e2df44fc11eac228d51b88e394771b713d363a0\");"]{
    
 	background-image:url("https://raw.githubusercontent.com/cyberlapse/SpotifyStylishExtension/gh-pages/dandelion_plant_flower_seeds_feathers_90323_1920x1080%20(2).jpg") !important; 
}

/* Discover Weekly Cover Image*/
.cover-art-image.cover-art-image-loaded[style="background-image: url(\"https://i.scdn.co/image/11258b6c69204820d79575e0587f415735db2350\");"] {
    
 	background-image:url("https://raw.githubusercontent.com/cyberlapse/SpotifyStylishExtension/gh-pages/rockettakeoff.jpg") !important; 
}

/* Import Font */
@font-face {
    font-family: caviarDreams;
    src: url(https://cyberlapse.github.io/SpotifyStylishExtension/CaviarDreams.woff) ;
}
 @font-face {
    font-family: caviarDreams;
    src: url(https://cyberlapse.github.io/SpotifyStylishExtension/CaviarDreams_Bold.ttf) format('truetype');
    font-weight:bold;
}

/*Fonts for links */
.mo-info-name{
 font-family:caviarDreams; 
    font-weight:normal!important;
}

h1, h2, .link-subtle{
 	font-family:caviarDreams !important;  
    font-weight:normal!important;
}

.contentSpacing h1{
  letter-spacing:6px;
}

.navBar-header {
 	visibility: hidden !important;   
}

/* Content Viewing */
.playlist {
    padding-top: 70px;
}

.contentSpacing {
    padding: 0 2em;
    margin: 0 auto;
    max-width: 1480px;
    padding-top: 70px;
}

.entity-info {
    margin: 0 auto 1.5em;
    text-align: center;
    max-width: 400px!important;
    
    position: relative;
    top: 70px;
}

.tracklist-row--active .position, .tracklist-row--active .tracklist-duration, .tracklist-row--active .tracklist-name, .tracklist-row--active .tracklist-play-pause {
    color: #d88282!important;
}

/* ADS BAR :'( */
#main .root .top-container .main-view-container--has-ads .ads-container{
    padding-bottom: 200px;
    visibility: hidden !important;
}


/* NOW PLAYING */
#main .root .nowPlayingBar-container {
    background-color: #181818;
    position: fixed;
    height: 47px;
    width: 100%;
    top: 0px;
    margin-bottom:60px;
    z-index: 101;
    box-shadow:5px 3px 4px rgba(20,20,20, 0.7);
}

/* DIV INSIDE NOW PLAYING */
.now-playing-bar {
    -ms-flex-align: center;
    align-items: center;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-direction: row;
    flex-direction: row;
    -ms-flex-pack: justify;
    justify-content: space-between;
    height: 47px;
    padding: 0 16px;
}


/* LEFT SIDE NAV BAR */
#main .root .top-container .nav-bar-container {
    width: 220px;
    position: fixed;
    top: 0px;
    left: 0;
    height: 100vh;
    z-index: 100;
    box-shadow:2px 3px 5px rgba(20,20,20, 0.6);
    background-color: rgba(40, 40, 40, 0.70);
    
    overflow: auto;
    min-height: 0!important;
    will-change: transform;
    margin-top: 70px !important;
}

.navBar-link--active, .navBar-link--active:focus, .navBar-link--active:hover {
    color: #cc7f7f;
}

.EYVkZXmTImKH4iH48N7Uj._1-aXXBO8bQU1WTFY-7B0m-:after {
    background-color: #6b4d4d!important;
}

.EYVkZXmTImKH4iH48N7Uj._1-aXXBO8bQU1WTFY-7B0m-:hover:after {
    background-color: #dddddd!important;
}

/* NOWPLAYING BUTTONS CONTAINER*/
.player-controls__buttons {
    margin-bottom: 0px;
    cursor: default;
    display: -ms-flexbox;
    display: flex;
    width: 224px;
    -ms-flex-pack: justify;
    justify-content: space-between;
    -ms-flex-flow: row nowrap;
    flex-flow: row nowrap;
}

/* Play button under album cover */
.btn.btn-green:focus, .btn.btn-green {
    background-color: #6b4d4d!important;
    opacity:0.7;
}
.btn.btn-green:focus, .btn.btn-green:hover {
    background-color: #bbbbbb!important;
    opacity:0.7;
}

.btn.btn-fg-green {
    color: #bbbbbb!important;
}

/* NOWPLAYING PLAY BUTTON */
.control-button {
    background-color: transparent;
    border: none;
    color: #6b4d4d;
    cursor: pointer;
    width: 26px;
    min-width: 26px;
    height: 26px;
    position: relative;
    transition: all 33ms cubic-bezier(.3,0,.7,1);
}


/* PROGRESS BAR */
.progress-bar__fg {
    background-color: #6b4d4d;
    border-radius: 2px;
    height: 4px;
    width: 0;
    transition: background-color .1s cubic-bezier(.3,0,.7,1);
}

.progress-bar:hover .progress-bar__fg {
    background-color: #dddddd!important;
    border-radius: 2px;
    height: 4px;
    width: 0;
    transition: background-color .1s cubic-bezier(.3,0,.7,1);
}

/* PLAYLISTS */
/*.JKKBXFILp09eIN4WugwgY { */
._1YMukI_FrE7QRuUVSvq_Qe{
    background-image: linear-gradient(rgb(66, 49, 60), rgb(8, 6, 8) 85%);
      
    
}




/* CONNECT DEVICES POPUP */
.connect-device-list-container {
    position: absolute;
    width: 280px;
    bottom: -97vh;
    right: -124px;
    display: visible;
    background: #282828;
    border-radius: 5px;
    padding: 5px;
    z-index: 1002;
    box-shadow: 0 6px 12px rgba(0,0,0,.175);
    transition: transform 333ms,opacity 333ms cubic-bezier(.3,0,.7,1);
}

/* small scroll window */
.col-xs-12 .col-lg-3 .col-xl-4 .col-sticky{
        top: 70px;
}

/* SCROLLBAR STYLES */
::-webkit-scrollbar {
  width: 2px;
  height: 2px;
}
::-webkit-scrollbar-button {
  width: 0px;
  height: 0px;
}
::-webkit-scrollbar-thumb {
  background: #755351;
  border: 0px none #ffffff;
  border-radius: 50px;
}
::-webkit-scrollbar-thumb:hover {
  background: #704343;
}
::-webkit-scrollbar-thumb:active {
  background: #4d3535;
}
::-webkit-scrollbar-track {
  background: #666666;
  border: 0px none #ffffff;
  border-radius: 50px;
}
::-webkit-scrollbar-track:hover {
  background: #666666;
}
::-webkit-scrollbar-track:active {
  background: #333333;
}
::-webkit-scrollbar-corner {
  background: transparent;
}
}
/* BY ZECHARIAH GOSSE */
