  .herobox .avatar-level.label,
  .label .glyphicon,
  .toolbar .glyphicon,
  h2.task-column_title,
  .nav.nav-pills .glyphicon,
  .nav > li > a
  /*.nav-tabs > li > a */ {
    color: rgba(255,255,255,0.7) !important;
