css:
@-moz-document domain("www.lynda.com") {
body,
.learn-more-banner,
#header_search_field {
    background: #444444 !important;
}
h2 {
    color: #ededed;
}
.section,
#primary,
.course-container,
#nmb_banner_container,
.panel-collapse {
    background-color: #000000 !important;
}
.main_subjects a:hover,
.subject_details,
.course-info,
.well,
.main_subjects li a:active,
.tabs .active #mostPopular,
.carousel-content,
.course-search,
#course-toc-outer,
.tab-content,
.course-tabs li h2 a,
.course-tabs,
.faq_subcategory_header,
.faq_question_wrapper,
.searchTop li a,
.category-header,
dd span a,
.course-page.page-main {
    background: #ffeded !important;
}
#ams3982 td,
.category-more-details,
.main_link,
.main_subjects,
.course-list-head,
#course-toc-outer li dl:hover,
.course-tabs li h2 a:hover,
.faq_helpfulness_answer,
.sub_menu li a:hover,
.searchTop li a:hover,
.detail-type {
    background: #444444 !important;
}
.well {
    box-shadow: none !important;
}
#breadcrumbs a {
    color: #008cc9;
}
.title-banner {
    background: #666666 !important;
}
#course-page {
    background-color: #444444 !important;
}
.main_subjects a:hover,
.subject_details,
.course-info,
.well,
.main_subjects li a:active,
.tabs .active #mostPopular,
.carousel-content,
.course-search,
#course-toc-outer,
.tab-content,
.course-tabs li h2 a,
.course-tabs,
.faq_subcategory_header,
.faq_question_wrapper,
.searchTop li a,
.category-header,
dd span a,
.course-page.page-main,
#toc-content,
.course-toc.toc-container,
#toc.tab-pane,
.tab-content,
#sidebar-nav li a,
.section-module,
#course-page .course-toc li {
    background-color: #666666 !important;
}
#footer:before {
    background-color: #222222 !important;
}
#course-tabs {
    display: block;
    background-color: #ebebeb;
    border-bottom: 2px solid #008cc9;
    border-radius: 5px 5px 0 0;
}
#homepage .right-rail .courses-section .header-container.fixed {
    background-color: #444;
}
#homepage .left-rail .goals #update-goal i {
    color: #000;
}
#homepage .left-rail .goals #update-goal:hover i {
    color: #333;
}
.exercise-tab,
.tab-container section {
    background-color: #666666;
}
#notes-content .note textarea {
    color: #ddd;
}
#sidebar-container #sidebar-nav li a {
    border-bottom: none;
    color: #fff;
}
.course-toc .chapter-row {
    color: #333;
    border-bottom: 1px solid #777;
}
#course-page .course-toc li {
    background-color: #666;
}
.exercise-tab .files.unlocked .project-files {
    color: #222;
}
.main-content-area.featured {
    background-color: #444444 !important;
}
.software-name a {
    color: #ebebeb;
    text-decoration: none;
}
.software-name a:hover,
.software-name a:hover span {
    color: #008cc9;
}
.grid_15 h3 {
    color: #8c8c8c;
}
.content h3 {
    border-bottom: 1px solid #777777;
}
#category-page .course-list,
.account-page:before {
    box-shadow: 0 0 4px rgba(0, 0, 0, .4);
    background: repeat-x #f7f7f7;
    background-image: -webkit-linear-gradient(top, #f7f7f7 0, #e5e5e5 100%);
    background-image: -o-linear-gradient(top, #f7f7f7 0, #e5e5e5 100%);
    background-image: linear-gradient(to bottom, #656565 0, #18001c 100%);
}
#category-page .show-more-row {
    background-color: transparent;
}
#homepage {
    background-color: #444444 !important;
}
#homepage .right-rail {
    background-color: #666666 !important;
    box-shadow: 0 0 9px rgba(0, 0, 0, 0.4);
}
.courses-section,
.module-body,
.module-items,
.card.card-list-style,
#goals-0.module.goals.row {
    background-color: #666666 !important;
}
.card.card-list-style {
    border-top: 1px solid #777777;
}
#homepage .left-rail .module .module-body .module-item {
    transition: .5s cubic-bezier(0.77, 0.55, 0.03, -0.13);
    transition-property: initial;
    transition-duration: 0.5s;
    transition-timing-function: cubic-bezier(0.77, 0.55, 0.03, -0.13);
    transition-delay: initial;
    background-color: #666666;
}
.module-item:hover,
.card {
    background-color: #777777 !important;
}
.copy h3 {
    color: #ededed;
}
.myaccount {
    color: #999999;
}
#contactinfo span,
#accountinfo span {
    color: #ededed;
}
#orientation-page .body .panel {
    box-shadow: 0 0 4px rgba(0, 0, 0, .4);
    background: repeat-x #f7f7f7;
    background-image: -webkit-linear-gradient(top, #f7f7f7 0, #e5e5e5 100%);
    background-image: -o-linear-gradient(top, #f7f7f7 0, #e5e5e5 100%);
    background-image: linear-gradient(to bottom, #656565 0, #18001c 100%);
    padding: 20px 30px;
}
.btn-link {
    color: #ffb900;
}
.standalone-recommended p {
    color: #ededed;
}
#devices,
#enterprise,
#home-page,
#top-courses {
    background-color: #666666 !important;
}
#devices {
    border-top: 1px solid #777777;
}
#home-page .btn.btn-complimentary.browse-all-cta {
    background-color: #008cc9;
}
.panel {
    background: #333;
    color: #999;
}
.btn-group>.dropdown-toggle:not(:first-child),
.course-actions.btn-group>.btn:first-child:not(:last-child):not(.dropdown-toggle),
.course-actions.btn-group>.btn:last-child:not(:first-child),
.course-actions.btn-group>.btn:not(:first-child):not(:last-child):not(.dropdown-toggle) {
    border-radius: 5px;
    color: #ddd;
}
}
