css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
/* AGENT_SHEET */

#titlebar-close:hover {
    background-color:#333 !important;
}
#titlebar-min:hover {
    background-color:#333 !important;
}
#titlebar-max:hover {
    background-color:#333 !important;
}
#main-menubar {
    background-color: #1c1c1c !important;
}
#new-tab-button,
.tabs-newtab-button {
 background:#000 !important;
}
#new-tab-button:hover,
.tabs-newtab-button:hover {
 background:#1a1a1a !important;
}
#navigator-toolbox::after {
    background:#050505 !important;
}
#nav-bar,
#TabsToolbar,
#PersonalToolbar {
    background: #1c1c1c !important;
    border: 0px !important;
}
.toolbarbutton-text {
    color: #6a6a6a !important;
}
findbar {
    -moz-appearance: none !important;
    background-color: #1c1c1c !important;
}
#bookmarks-view,
#historyTree,
#history-panel,
#sidebar-header {
    background-color: #1c1c1c!important;
    color: #ccc!important;
}
menuitem,
menu {
    background-color: #1c1c1c !important;
    color: #ccc !important;
    border: none !important;
}
menuseparator {
    display: none !important;
}
menupopup {
    background-color: #1c1c1c !important;
    -moz-appearance: none!important;
    border: 1px solid #1c1c1c !important;
}
menupopup > menu:hover {
    background-color: #222 !important;
}
menuitem:hover {
    background-color: #222 !important;
    color: #fff !important;
}
arrowscrollbox {
    background-color: #050505 !important;
    color: #ccc!important;
}
.statuspanel-label {
    color: #ccc !important;
    background: #1c1c1c !important;
    border: none !important;
}
#main-menubar,
#nav-bar {
    box-shadow: none !important;
}
#urlbar {
    background-color: #222 !important;
    border: none !important;
    color: #aaa !important;
}
#urlbar:hover,
.searchbar-textbox:hover {
    background-color: #2a2a2a !important;
}
.searchbar-textbox {
    background-color: #222 !important;
    border: none !important;
    color: #aaa !important;
}
menupopup>hbox {
    border: 0px !important;
}
tab[selected="true"] {
    color: #ccc !important;
    background: #1c1c1c !important;
}
tab[selected="true"]:hover {
    color: #fff !important;
    background: #2c2c2c !important;
}
tab:not([selected="true"]) {
    color: #6c6c6c !important;
    background: #050505 !important;
}
tab:not([selected="true"]):hover {
    color:#ccc !important;
    background: #161616 !important;
}
#PersonalToolbar {
    height: 24px!important
}
#personal-bookmarks toolbarbutton {
    margin: -1px 0 6px 0 !important
}
#personal-bookmarks .toolbarbutton-icon {
    margin: -3px 2px -3px 0 !important
}
#personal-bookmarks .toolbarbutton-text {
    margin: -2px 0 !important
}
