css:
@-moz-document domain('forum.blockland.us') {

/*@import url(https://fonts.googleapis.com/css?family=Roboto);*/
@font-face {
  src: local('Roboto'), url(https://fonts.googleapis.com/css?family=Roboto) format('woff');
}

* {
	background-image: none !important;
	border: none !important;
	font-family: 'Roboto', sans-serif;
}

body {
	background-color: #e0e0e0;
}

body > table:not(:last-of-type) > tbody > tr > td,
body > table:not(:last-of-type) > tbody > tr,
.windowbg,
.windowbg2,
.windowbg > td,
.windowbg2 > td{
  background-color: #f5f5f5 !important;
}

body > table > tbody > tr > td {
	padding: 0;
}


body > table:first-of-type {
	max-width: 1330px;
}

.titlebg,
.titlebg > td,
.titlebg a,
.titlebg2,
.titlebg2 > td,
.titlebg2 a,
.middletext,
.middletext > td,
.middletext > td > a{
	background-color: #aed581 !important;
	color: #000 !important;
	font-weight: lighter !important;
}

.bordercolor {
	border-spacing: 0 !important;
	border-collapse: collapse !important;
	width: 100%;
}
	
body > table:not(:last-of-type) {
    -webkit-box-shadow: 0px 1px 2px #1c2429 ;
	box-shadow: 0px 1px 2px #1c2429 ;
}

body > table:last-of-type tr {
	background-color: #e0e0e0 !important;
}

body > table td {
	padding: 5px 0 5px 0;
}

.windowbg,
.windowbg2,
.titlebg > td:first-of-type,
.catbg{
	padding: 5px 5px 5px 15px;
}

.nav {
	padding: 2px 0 2px 15px;
}

.nav {
	display: inline-block;
}

.nav br {
	display: none;
}
body > table:first-of-type {
	width: 100%;
	height: 100px;
}

body > table:first-of-type td:last-child {
	text-align: right;
	padding-right: 5%;
}

body > table:first-of-type td:first-child {
	vertical-align: top;
}

body > table:nth-of-type(2n) {
	max-width: 1366px;
}

.logolink {
	background-image: none !important;
}

center:first-of-type {
  display: block;
  width: 70%;
  margin: -53px auto 30px !important;
  text-align: left;
}

body,
td,
th,
.post {
  color: #222 !important;
  font-size: 11pt !important;
  font-family: 'Roboto', verdana, sans-serif !important;
}

.catbg a:visited, .catbg a:link, .catbg,
.catbg3 a:visited, .catbg3 a:link, .catbg3{
	font-weight: 100;
	color: #FFF;
}

.bordercolor{
	background-color: transparent !important;
}
  
.catbg,
.catbg td,
.catbg tr,
.catbg3,
.catbg3 td{
	font-size: 14pt !important;
	background-color: #689f38 !important;
	padding: 5px 10px 5px 10px;
	color: #FFF !important;
}
  
	
td.catbg > table td,
td.catbg > table tr {
  background-color: #689f38 !important;
}
}

@-moz-document url-prefix('https://forum.blockland.us/index.php?action=post') {
	body > table:nth-of-type(2n) {
	  max-width: 1000px !important;
  }
	
	.nav br {
	display: inline-block;
  }
}

@-moz-document url-prefix('https://forum.blockland.us/index.php?action=profile') {
	body > table:nth-of-type(2n) {
	  width: auto !important;
  }
	
	.bordercolor {
		width: 95% !important; 
	}
	
	body > table:nth-of-type(2n) > tbody > tr > td > table > tbody > tr > td:first-of-type {
		width: 250px !important;
	}
	
	body > table:nth-of-type(2n) > tbody > tr > td > table > tbody > tr > td:last-of-type {
		width: 650px !important;
	}
	
	.smalltext {
		padding-left: 10px;
	}
}

@-moz-document url-prefix('https://forum.blockland.us/index.php?action=pm') {
	body > table:nth-of-type(2n) > tbody > tr > td > table > tbody > tr > td:first-of-type {
		width: 150px !important;
		padding: 5px;
}
}

@-moz-document url('https://forum.blockland.us/index.php?action=search') {
	body > table:nth-of-type(2n) > tbody > tr > td > table > tbody > tr > td:first-of-type {
		width: 150px !important;
		padding: 5px;
}
}
