.content{
  background-color: transparent !important;
}
.home-friends .people-list{
  background-color: rgba(255,255,255,0.8) !important;
  border-radius: 15px !important;
  color: black !important;
}
.home-header-content{
  color: white !important;
}
.game-cards .game-card{
  background-color: rgba(255,255,255,0.8) !important;
  border-radius: 15px !important;
  white-space: pre-line !important;
}
.game-card-link .game-cards .game-tile{
  padding: 3.5px !important;
}
li.list-item.game-card.game-tile.ng-scope:Hover, li.list-item.game-card.game-tile:Hover{
  box-shadow: 5px 5px 10px -3px rgba(0,0,0,0.75) !important;
}
.game-tile-container .game-card-thumb{
  border-radius: 5px !important;
}
.game-card-container{
  text-align: center !important;
}
.bg-gray-4{
  background-color: transparent !important;
}
.game-card-link .game-card-thumb-container{
  padding: 7px !important;
}
.game-card-thumb-container:before{
  display: none !important; /*Remove Hover Over Darken on games*/
}
.game-card-container .game-card-thumb-container{
  background-color: transparent !important;
}
.tab-content, .left-wrapper-placeholder .left-wrapper .section-content{ /*for avatar page*/
  border-radius: 5px;
  background-color: white;
}
.col-xs-12.section-content.game-main-content {
  background-color: white;
  border-radius: 5px;
}
.carousel-inner{
  width: 95% !important;
  margin: auto !important;
  top: 5% !important;
  border-radius: 7px !important;
}
.game-main-content .game-calls-to-action .game-title-container {
  padding: 20px 12px 0 0 !important;
}
.gig-bar-container.gig-share-bar-container {
  visibility: hidden !important; /* hide social share buttons*/
}
.text.game-description.linkify {
  padding-left: 20px;
}
.games-list-container{
	background-color: #ffffff00 !important;
}


}
