css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("36kr.com") {
  .rightlib,.author-panel,.summary,.article-footer-label,.ad,.single-post-tags,.fav-wrapper,.related-articles,.single-post-comment,.high_projects,.report-code-section,.common-footer,.post-realtion-monographic-wrapper,.kr_article_list .mark,.article-footer-ad{
    display: none;
  }
  /*如果需要隐藏下拉菜单，请取消该注释
  header.common-header .pop-up-row{
    display: none;
  }*/
  .mainlib .center_content{
    margin-right: 0px;
  }
  .article-detail .post-detail-con-box{
    max-width: 800px;
  }
  .pagewrap{
    max-width: 800px;
  }
  .article-detail .content-wrapper section.headimg img{
    width: 60%;
    margin-left: 15%;
  }
  .kr_article_list .img_box{
    width: 8rem;
    height: 4.6rem;
  }
  .kr_article_list .intro{
    margin-left: 9rem;
  }
  .kr_article_list .info{
    padding-left: 9rem;
  }
  .ab-index-exp .kr_article_list .intro h3{
    font-size: 18px;
  }
  .kr_article_list ul li .inner_li{
    height: 100%;
  }
  .kr_article_list ul li .info-flow-monographic-wrapper.inner_li{
    height: 7rem;
  }
  .kr_article_list ul li .info-flow-monographic-wrapper.inner_li .mark{
    margin-left: 0.5rem;
    top: 1.5rem;
  }
  .kr_article_list ul li .info-flow-monographic-inner .info .title{
    font-size: 18px;
  }
  body{
    padding-bottom: 10px;
  }

}
