css:
@-moz-document domain("app.roll20.net"), domain("app.roll20dev.net") {
body {
	background: url("http://i.imgur.com/dnt3CUG.jpg");
	font-family: "Arial", sans-serif;
}
code,
pre {
	font-family: "Consolas", monospace;
}
p input,
button,
select,
textarea {
	font-family: "Arial", sans-serif;
}
.navbar-search .search-query {
	font-family: "Arial", sans-serif;
}
.textboxlist-bit-editable-input {
	font-family: "Consolas", monospace;
}
#page-toolbar {
	background-color: #2F4F4F !important;
	border-bottom: 0.5px solid #A9A9A9;
	border-radius: 0 !important;
	height: 160px !important;
	left: 0 !important;
	right: 0 !important;
	width: 100% !important;
}
#page-toolbar .availablepage {
	box-sizing: border-box !important;
}
#page-toolbar .availablepage .pagethumb {
	margin-top: 15px !important;
}
#page-toolbar .container {
	transition: opacity .4s ease-in-out !important;
}
#page-toolbar .handle {
	background: #2F4F4F !important;
	border-radius: 0 0 100% 100% !important;
	bottom: -31px !important;
	color: #eee !important;
	height: 29px !important;
	opacity: 1 !important;
	width: 100px !important;
	right: 30% !important;
	margin-left: -50px !important;
	padding: 0 !important;
	border: 0.5px solid #A9A9A9;
}
#page-toolbar .handle .pictos {
	display: none !important;
}
#page-toolbar .handle:after {
	content: 'PAGES' !important;
	font-size: 11px !important;
	letter-spacing: 2px !important;
	height: 100% !important;
	left: 0 !important;
	position: absolute !important;
	top: 0 !important;
	width: 100% !important;
}
#page-toolbar .playerbookmark {
	top: 10px !important;
}
#page-toolbar.closed .container {
	box-shadow: 0 0 50px rgba(0, 0, 0, .15) !important;
	opacity: 0 !important;
}
#sidebarcontrol {
	opacity: 1 !important;
	padding: 0 !important;
	-webkit-transform: translateX(0) rotate(-90deg) !important;
	-moz-transform: translateX(0) rotate(-90deg) !important;
	width: 100px !important;
	text-align: center !important;
	font-size: 12px !important;
	color: #FFFFFF !important;
	background-color: #696969 !important;
	border: 0.5px solid #A9A9A9;
	height: 30px !important;
	line-height: 30px !important;
	text-transform: uppercase !important;
	letter-spacing: 2px !important;
	-webkit-transform-origin: 100% 100% !important;
	-moz-transform-origin: 100% 100% !important;
	border-radius: 100% 100% 0 0 !important;
	top: 50% !important;
	margin-top: -75px !important;
}
#sidebarcontrol .fonticon-menu {
	display: none !important;
}
#sidebarcontrol:after {
	content: 'Menu' !important;
}
/*Scrollbar top*/

#page-toolbar .container::-webkit-scrollbar {
	-webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
	border-radius: 0px;
	background-color: #F5F5F5;
	height: 15px;
	width: 5px;
}
