/* Ui-Resize-Slider*/

.ui-slider-vertical .ui-slider-handle {
	left: -.4em;
	margin-left: 0;
	margin-bottom: -.6em;
}
.ui-slider .ui-slider-handle {
	position: absolute;
	z-index: 2;
	width: 1.5em;
	height: 1.5em;
	cursor: pointer;
}
/*Spell Description
div.sheet-container {
font-family: "Arial";
}*/

.sheet-rolltemplate-spell .sheet-container {
	width: 260px;
	font-family: "Georgia";
}
.sheet-rolltemplate-spell .sheet-title {
	text-transform: uppercase;
	font-weight: bold;
}
.charsheet .sheet-npc .sheet-display .sheet-title input {
	font-family: "Georgia";
	font-size: 24px;
	width: 100%;
	text-transform: capitalize;
	padding-bottom: 6px;
}

#secondary-toolbar {
    left: 90px !important;
	opacity: 0.90;
	background-color: rgba(255, 255, 255, 0.3);
	border: 0;
	box-shadow: none;
	border-radius: 5px;
}
#secondary-toolbar .btn {
    border: 0 !important;
    border-radius: 5 !important;
	margin: 2px;
}
#secondary-toolbar li {
    border: 0 !important;
}
}
