css:
@-moz-document domain("mks.mff.cuni.cz") {
html {
 background: #000 !important
}

.tex {
 filter: invert(100%) !important;  
}

.button_tex img {
 filter: invert(100%) !important;  
}

.header {
 background: #222 !important   
}

.header_submenu ul li a {
 color: #000 !important   
}
.header_submenu ul li.active a {
 color: #fff !important   
}
.header_submenu ul li a:hover {
  background-color: #e0a075 !important;   
  box-shadow: 0px 1px 2px 1px #e0a075 !important
}

.menu_nav ul li.active a {
 color: #000 !important  
}

img[src="images/texH.png"], img[src="images/motivacni_obr_7.png"], img[src="images/motivacni_obr_6.png"], img[src="images/latexH.png"]{
 filter: invert(100%)   
}


textarea {
 background: #000 !important;
 color: #ccc !important             
}

.galpopis, .popisek {
     color: #ccc !important;
}

.content {
 color: #ccc !important;
 background: #000 !important
}

.container, .boxik {
 background: #222 !important   
}

.framed {
 background: #222 !important;   
 border: 1px solid #111 !important             
}

div.topic {
 background-color : #000 !important;
 color: #ccc !important;
}

.page_box {
 background: #ccc !important;   
 border: #999999 1px solid !important;
 box-shadow: 4px 4px 5px #eb7b31 !important
}

input, button {
 background-image: none !important;
 background-color: #333 !important;
 color: #ccc !important;
 border-color: #333 !important;
 border-width: 1px !important
}

form.mksform select {
 background-color: #000 !important;
 color: #ccc !important             
}

.login {
 background: #222 !important   
}

.login input {
 background-color: #000 !important	
}

form.mksform td {
 background-color: #000 !important
}

.fbg td {
 background-color: #111 !important
}

.login td {
 background-color: #222 !important
}

.db_table {
 border: solid #2f2f2f 1px !important   
}

.db_table th {
 background-image: -webkit-linear-gradient(top, #636363, #000000) !important   
}

.db_table tr:hover td {
 color: #1ff9ff !important;
 text-shadow: 0px 0px 2px #19464a !important
}

.db_table tr.odd {
 background: #111 !important;
}

.fbg {
 background: #111 !important;
 box-shadow: 0px -3px 5px #111 !important
}

.fbg .c2 img {
 filter: invert(100%) !important    
}

.footer {
 background-color: #222 !important
}
}
