css:
@-moz-document domain("esa.io") {
/* general */
.body {
    font-family: "Noto", "Source Han Code JP", "Noto Sans";
    padding-left: 100px;
}
.btn-primary {
    padding: 3px 10px;
}

/* post list */

.post-list .post-title__name {
    font-size: 20px !important;
    font-weight: normal;
}
.post-list .post-title__btn {
    display: none;
}
.posts .category-heading__icon {
    display: none;
}
.posts .category-heading__button {
    font-size: 0;
    padding: 2px 10px;
    margin-top: 8px;
}
.posts .category-heading__button:before {
    font-size: 13px;
    content: "New";
}
.posts a.post-title__action:hover {
    color: #1e84eb !important;
}


/* post list - nabvar-side */

.navbar-side {
    background: #fafafb;
    width: 300px;
}
.navbar-side .title:before {
    color: #555;
}
.navbar-side__title-name {
    min-width: 300px !important;
}
.navbar-side>a {
    color: #000;
}
.navbar-side>ul>li>a {
    background: #fafafb;
    color: #000;
}
.navbar-side ul li>a {
    font-size: 13px;
    padding: 6px;
}
.navbar-side ul li>a {
    padding-left: 10px;
    padding-top: 4px;
    padding-bottom: 4px;
}
.navbar-side>ul>li>ul>li>a .title {
    padding-left: 0px;
}
.navbar-side>ul>li>ul>li>ul>li>a .title {
    padding-left: 10px;
}
.navbar-side ul li>a {
    font-size: 14px;
    color: #000;
    background-color: #fafafb;
}
.navbar-side ul li>a.active {
    color: #000;
    background-color: #fafafb;
}
.navbar-side__title-name {
    white-space: normal;
    text-overflow: initial;
    width: 200px;
}
.post-prev-next {
    display: none;
}
.post-aside {
    border-top: 1px solid #ccc;
}
.viewer-action .is-footer {
    display: none;
}


/* post */

.main-column {
    padding-left: 100px;
}

/* post - header */

.post-header__meta {
    width: 300px;
    font-size: 14px;
}
.post-title__icon-wip {
    margin: 0px;
}
.post-title__link {
    margin-left: 0px;
    padding-left: 0px;
}
.post-title__button {
    font-size: 0;
    padding: 2px 10px;
    margin-top: 8px;
}
.post-title__button:before {
    font-size: 15px;
    content: "New";
}
.post-title__name {
    font-size: 30px !important;
    font-weight: normal;
    color: #000;
}
.post-title__action {
    padding-right: 0px;
    font-size: 20px;
    margin-right: 0px;
}
.post-list__left {
    display: none;
}
.post-list__footer {
    display: none;
}
.post-list__item {
    padding: 6px 0;
}
.post-title__btn {
    margin: 0;
    padding: 0;
    height: 25px;
    width: 60px;
}
.post-title__link {
    color: #000;
}
a.post-title__link:hover {
    color: #ff0000;
}
.post-title__name {
    font-size: 20px;
    padding-left: 10px;
}
a.post-title__link:hover {
    background-color: #fff;
}


/* revisions */

.viewer-action__thumbnail-list {
    display: none;
}
.post-author__thumbnail {
    display: none;
}
.markdown h1 a {
    color: #1e84eb !important;
}
.post-header {
    border-bottom: 1px solid;
    border-color: #ccc;
}

/* post - header counter */

body {
    counter-reset: h1counter;
}
.main-column h1,
.main-column h2,
.main-column h3,
.main-column h4,
.main-column h5,
.main-column h6 {
    font-weight: bold;
    border: none;
    color: #111;
}
.main-column .markdown h1 {
    color: #111;
    font-size: 25px;
    margin-top: 60px;
    /*    border: none;*/
    
    border-bottom: 1px solid #ccc;
}
.main-column h2 {
    font-size: 22px;
    margin-top: 40px;
    margin-bottom: 30px;
}
.main-column h3 {
    font-size: 18px;
    margin-top: 30px;
    margin-bottom: 20px;
}
.categories-index .main-column-category {
    margin-left: 300px;
}
.post-body {
    width: 800px;
    margin: 0 auto;
}
.navbar-main {
    background-color: #fafafb;
    display: none;
}
ul.viwer-action {
    display: none;
}
div.post-header {
    border-bottom: 1px solid #bbb;
}
.navbar-main__link:after {
    display: none;
}
aside {
    display: none;
}
.navbar-sub {
    background-color: #fafafb;
}
.navbar-sub__search {
    background-color: #fafafb;
}
.container {
    margin-left: 0px;
}
.btn-new {
    width: 50px;
    display: none;
}
.btn-new__image {
    width: 50px;
}
.nav-main {
    width: 50px;
}
.nav-main__icon {
    width: 50px;
}
.nav-main__link {
    width: 50px;
}
.btn-primary {
    background-color: #999;
}
a.btn-primary:hover {
    background-color: #555;
}
a {
    color: #1e84eb;
}
a:hover {
    color: #347cdb;
}
.sorter li a i {
    color: #459efd;
}
span.count-num {
    display: none;
}
li.item {
    width: 300px;
}
p,
ul,
ol {
    color: #333;
    font-size: 14px;
}
.category-path__link {
    color: #111;
}
a.category-path__link {
    color: #1e84eb;
}
a.category-path__link:hover {
    color: #1e84eb;
}
a.post-title__button {
    text-align: right;
}

/* post - toc */
.fixed {
    position: fixed;
    margin-right: 72px !important;
}

/* comment */

.comment__markdown {
    font-size: 12px;
}
.comment__markdown > blockquote > p {
    font-size: 12px;
}
.comment__markdown > p {
    font-size: 12px;
}
.comment__markdown > ul {
    font-size: 12px;
}
.comment__markdown > ol {
    font-size: 12px;
}
.comment__markdown li {
    font-size: 12px;
}
.comment__thumbnail {
    height: 30px;
    width: 30px;
}
a.thumbnail__link>img.thumbnail__image {
    height: 30px;
    width: 30px;
}

/* refereces */

#references {
    display: none;
}

}
