css:
@-moz-document domain("ukrainians.co") {
body {
	color: #c2c2c2;
	background-color: #0f0f0f!important;
}

.navbar-site,
.messager-header,
.show-more-comments {
	background-color: #1e1e1e;
	border-color: #141414!important;
}
.navbar-logo,
.st-user-name,
.u-container-header-tab ul li a {
	color: #ffffff;
}
.navbar-logo img {
	filter: invert(100%);
}
.aside-container,
.profile-general-info,
.post-block,
.post-block-new,
.modal-content,
.general-container,
.u-aside-tab {
	background-color: #181818;
	border: 1px solid #141414;
}
.profile-general-info-header {
	border-bottom: 1px solid #141414;
}
.button-while,
.profile-show-all {
	background-color: #292a2b;
	color: #fff;
	border-color: #141414;
}
.button-while:hover,
.profile-show-all:hover {
	background-color: #3b3c3e;
	color: #fff;
}
a:focus,
a:hover {
	color: #c7c7c7;
	text-decoration: underline;
}
.profile-statistic-value,
.profile-statistic-title,
.u-aside-tab ul li a {
	color: #c2c2c2;
}
.u-circle-counter {
	background: rgba(45, 45, 45, .7);
	color: #fff;
}
.side-bar ul li:hover {
	background-color: rgba(45, 45, 45, .7);
}
.profile-user-info-value {
	color: #b5b5b5;
}
.profile-ava-img {
	border: 5px solid #2d2d2d;
}
.my-rating {
	border-color: #5a5959;
}
.xs-photo-user,
.small-photo-user {
	border: 2px solid #2d2d2d;
}
.post-submitet-footer,
.message-write-body {
	border-color: #131313;
}
.post-upload-content {
	width: 100%;
	padding: 15px 20px;
	background-color: #232323;
	border-top: 1px solid #131313;
}
.post-block-footer {
	border-bottom: 0;
}
.post-block-ava {
	border: 2px solid #2d2d2d;
}
.post-block-profile-name {
	color: #c7c7c7;
}
.u-dropdown-menu,
.friends-search-container input {
	background: #181818;
	border-color: #1f1f1f!important;
}
.u-dropdown-content ul li:hover,
.search-block-item:hover {
	background-color: #292929;
}
.button-gray {
	border-color: transparent;
	background-color: #424242;
}
.button-gray:hover {
	background-color: #3b3c3e;
}
.post-edit-body .post-block-write {
	border: 1px solid #2d2d2d;
}
.post-comments-container:hover,
.post-likes-container:hover,
.notif-block-item-active,
.notif-block-item:hover,
.nav-drop-menu ul li:hover,
.mess-item-select,
.mess-item:hover,
.search-show-more:hover {
	background-color: #292929;
}
.post-block.add-new-comments,
.posts-comment-counter,
.comment-item:before,
.post-block-footer:before,
.u-container-header,
.center-container-footer {
	border-top: 1px solid rgba(230, 230, 230, 0.25);
}
.nav-drop-menu,
.footer-page-wrapper {
	background: #181818;
	border: 1px solid;
	border-color: #1f1f1f!important;
}
.friends-name {
	color: #e6e5e5;
}
.navbar-right-content .dropdown-wrapper:hover,
.navbar-right-content .open,
.dialog-item:hover,
.u-container-header-tab,
.st-popup-header {
	background-color: #292929;
}
.input-standard {
	width: 330px;
	border: 1px solid rgba(230, 230, 230, 0.25);
	background-color: #1f1f1f;
}
.u-aside-tab .aside-tab-active,
.u-aside-tab .aside-tab-active:hover,
.u-aside-tab ul li:hover {
	border-left: 2px solid #333;
	background-color: #292929;
}
.sett-social-connecting,
.settings-butt-change {
	color: #eae7e7;
}
.friends-name:hover {
	color: #fff;
}
.messager-header-wrapper {
	border-color: #0F0F0F;
	background-color: #fff;
}
.message-write-wrapper,
.account-footer {
	border-color: #0F0F0F;
	background-color: #1e1e1e;
}
.dialog-back:hover {
	background: 13px 16px no-repeat, linear-gradient(90deg, #313131 50%, #1e1e1e);
}
.send-message {
	color: #737373;
}
.send-message:hover,
.send-message-fr {
	color: #cecece;
}
.post-block-profile-name:active,
.post-block-profile-name:focus,
.post-block-profile-name:hover {
	color: #ffffff;
}
.u-container-header-tab ul .friends-navbar-active a:before {
	border-bottom: 2px solid #fff;
}
.u-container-header-tab ul .friends-navbar-active a:hover:before {
	border-bottom: 2px solid #989898;
}
}
