css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("niederschlagsradar.de") {

/* Don't show quite a lot of things */
  td[width="670"] > table > tbody > tr > td[width="120"], 
  iframe[src="120x600.html"], 
  td[width="900"] > table > tbody> tr + tr + tr > td[colspan="6"], 
  td[background="include/images/balk_l_02.gif"], 
  span[id="ctl00_lblWaardeOudeRegio"], 
  iframe[name="google_ads_frame"], 
  iframe[width="468"],
  table[style="height: 19px;"],  /* gray bar at the top */
  img[src="include/images/rm.gif"],
  img[src="include/images/stip2.gif"],
  img[src="include/images/stip.gif"]  
  {display: none !important }

/* Center page */
  table[width="100%"] { width: auto !important; margin: 0px auto !important; }

/* Re-align text and tables */
  td[align="right"] { text-align: left !important; }

/* Resize banner */
 td[colspan="6"] > table > tbody > tr > td > a[target="_top"][href="http://www.niederschlagsradar.de"] {
    height: 78px !important;
    width: 825px !important;
    overflow: hidden !important;
    display: block !important; 
    margin: 0px 0px 0 -5px !important;
  }
}
