@-moz-document url-prefix("http://context.reverso.net") {
  
  #top-content {
    background-color: #202020 !important; 
    box-shadow: 0 0 15px #000000 !important; 
    border-bottom: 2px solid rgba(0,0,0,.7) !important;
  }
  
  #footer-main {
    background-color: #202020 !important; 
    box-shadow: 0 0 15px #101010 !important; 
    border-bottom: 2px solid rgba(0,0,0,.7) !important;
  }
  
  body, #wrapper, .results #extension{
    background-color: #181818 !important;
  }
  
  #latest-history-content .title, #pair-selector .selector.displayed .option, #dictionary-button, .banner .reverso-logo-content{
    background-color: transparent !important;
  }
  
  #latest-history-content, .banner, #suggested-searches-content, #translate-links, #blocked-results-banner .content, #register-popup-bottom{
    background: #202020 !important;
    border: 1px solid #333 !important;
    box-shadow: none !important;
    border-radius: 3px !important;    
  }
  
  body *{
    color: #888 !important;
  }
  
  .reverso-logo-content div span, #examples .example .options .icon:hover, #translations .more:hover, #pair-selector .swap-content:hover span, #search-input .icon:hover, #dictionary-button:hover span, #search-button:hover span, #share-content div:hover span, #top-bar a:hover span, #top-bar .about a:hover, #footer-main a:hover, #register-popup-bottom .close:hover, #suggested-searches-content a:hover, #search-tools .icons .icon:hover, #bottom-suggestions .suggestion a:hover, #bottom-suggestions .other-content .icon.exclude:hover{
    color: #ddd !important;
  }
  
  #search-input, #pair-selector .selector, #pair-selector .swap-content, #lang-selector{
    background-color: #151515 !important;
    border-color: #333 !important;
  }
  
  #search-input.active{
    box-shadow: 0 0 5px #000 inset !important;
  }
  
  #search-input:hover, #pair-selector .selector:hover, #pair-selector .swap-content:hover, #pair-selector .selector .option.front, .ui-autocomplete, #src-selector.displayed, #trg-selector.displayed, #more-menu, #lang-selector:hover {
    background-color: #222 !important;
    border-color: #555 !important;
  }
  
  .ui-autocomplete li:hover, .ui-autocomplete li.ui-state-focus, #src-selector.displayed .option:hover, #trg-selector.displayed .option:hover, #pair-selector .selector.displayed .option.selected, #search-button:hover, #latest-history-content .history-entry.even:hover, #extension.banner .button:hover, #connect-content .join:hover, .results #extension .button:hover, #examples .example .src .text em, #examples .example .trg .text em, #subtop-content .taille:hover, #blocked-results-banner .button:hover, #add-example-content .button:hover, .popup.add-example .button:hover, #latest-history-content .button:hover, #register-popup-bottom .bottom .button:hover, #lang-selector option:hover, #latest-history-content .history-entry:hover, #top-bar .facebook{
    background-color: #444 !important;
  }
  
  #translations .translation, #translations .other-content.filtered, #global-options .icon, #latest-history-content .encourage-content, #bottom-suggestions{
    background-color: #222 !important;
    border-color: #444 !important;
  }
  
  #translations .translation:hover, #latest-history-content .history-entry.even, #translations .other-content.filtered:hover, #global-options .icon:hover{
    background-color: #333 !important;
    border-color: #666 !important;
  }
  
  #translations .translation.indication{
    background-color: #151515 !important;
    border-color: #333 !important;
  }
  
  #translations .translation.indication:hover{
    background-color: #181818 !important;
    border-color: #444 !important;
  }
  
  #search-button, #share-content div, .banner .reverso-logo-content:hover, #translate-links ul li:hover a, #extension.banner .button, #connect-content .join, .results #extension .button, #subtop-content .taille, #blocked-results-banner .button, #register-popup-bottom .top, #register-popup-bottom .bottom .button{
    background-color: #333 !important;
  }
  
  #examples .example:hover, #examples .example .options {
    background-color: #222 !important;
  }
  
  #examples .example:not(.pba) .options, .banner .reverso-logo-content {
    box-shadow: none !important;
  }
  
  .banner .reverso-logo-content{
    border: 1px solid #ddd !important;
  }
  
  #share-content .facebook:hover{
    background-color: #3b5998 !important;
  }
  
  #share-content .twitter:hover{
    background-color: #0cf !important;
  }
  
  
  #share-content .google:hover {
    background-color: #ef3106 !important;
}
  
  #blocked-results-banner{
    background-color: rgba(0,0,0,.2) !important;
    border: none !important;
    box-shadow: none !important;
  }
  
  #add-example-content .button, .popup.add-example .button, #latest-history-content .button, .banner .stores a:hover, #suggested-searches-content .lang-title, #footer-main ul{
    border-color: #444 !important;
  }
  
  #suggested-searches-content a:hover, #bottom-suggestions .suggestion a:hover{
    border-color: transparent !important;
  }  

}
