    }

.rbx-upgrade-now #upgrade-now-button:hover, .rbx-btn-secondary-xs:hover, .rbx-btn-secondary-xs.btn-more:hover, #AjaxCommentsContainer > div > div.comment-form > form > button:hover {
        background: #313131 !important;
        border-color: #313131;
    }

