css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {
  
  .overlay_background .overlay_container,.overlay_background,.android_walking,.walking .footsteps,.walking .shadow,.walking .x_button
  {display: none;}
  
  .overlay_background
  {background-color: transparent;}
  
  .NotificationLine
  {background-color: #111; box-shadow: none;}
  
/* rgb(44,40,39) */
  
  .Button,.Button.Active, .ButtonGroup.Open .Button.Handle, .Button:focus, .Button:active,.MenuItems
  {background: #000;}
  
  .header-search .type_search .type_search_select .type_search_option:hover
  {background: #031c2c;}
  
  .us-stylecard--long,.personal-page .light-blue-button:hover,.personal-page .light-blue-button,.us-stylecard--short,div.token-input-dropdown,.choose_editor .boxesContainer .box
  {background-color: #000;}
  
  .choose_editor .boxesContainer .box .cta
  {background-color: #031c2c;}
  
  .choose_editor
  {background-color: #111;}
  
  .us-stylecard__button-container .us-button
  {background-color: #01406a;}
  
  .us-stylecard__button-container .us-button:hover
  {background-color: #042439;}
  
  .header-search .type_search .type_search_select
  {background-color: #000;}
  
  #nav-view-responsive,.navigation .links-box .navigation-link:hover,.header-search .search_input .input_text_search,.header-search .search_input.focused,.header-search .search_input,.navigation,.us-stylecard--short .us-stencil,
  ul.token-input-list,.header-search .type_search
  {background-color: transparent;}
  
  .personal-page .light-blue-button:hover
  {color: lightblue;}
  
  .header-search .type_search .type_search_select .type_search_option,ul.token-input-list,.choose_editor .boxesContainer .box .title
  {color: #ddd;}
  
  .navigation .links-box .navigation-link .navigation-title
  {color: #8fb4da;}
  
  :not(.notes_textarea):not(.text_style_page):not(.style_description_long):not(.us-username):not(.NotificationLine-Content) > a
  {color: #8fb4da;}  
  
  .MenuItems,.FlyoutMenu,ul.token-input-list,div.token-input-dropdown,.Button,.WarningMessage,.header-search .search_input .input_text_search,.TextBoxWrapper,input.DateBox, input.InputBox, input.SmallInput, textarea
  {border: 1px solid #2d2d2d !important;}
  
  .Button.Active, .ButtonGroup.Open .Button.Handle, .Button:focus, .Button:active
  {border-color: transparent;}
  
  .MenuItems hr
  {border-bottom: transparent;}
  
  .Sprite16
  {filter: invert(100%);}

  .navigation .links-box .navigation-link,.navigation .links-box,.us-stylecard__button-container .us-button:hover,.navigation,.us-stylecard--short .us-stencil,.header-search .type_search .type_search_select,
  .choose_editor .boxesContainer .box,.choose_editor .boxesContainer .box .cta
  {box-shadow: none;}
  
  .header-search .type_search .type_search_select
  {width: 87px;}

  body,.Attachment .FileHover
  {background-color: #111 !important; color: #5aa3ac;}
  
  #Head,.FlyoutMenu,table.PreferenceGroup tbody tr:hover td,.Info,.WarningMessage 
  {background: #111;}
  
  .header_button a:hover{
    color: cyan !important;
  }
  .Item.Read{
    background: #222 !important;
  }

#Head a,a .TextColor,.FlyoutMenu,.WarningMessage {
    color: #b7b7b7;
}
#Head a:hover {
    color: #fff;
    text-decoration: none;
}
.MenuItems a:hover{
   color: black !important;
   background-color: transparent !important;
}
/*.Sprite16,.BigButton,.OptionsTitle,.NavButton,.MenuItems,.Button{
    filter: invert(100%);
}*/
.OptionsTitle{
    filter: invert(50%);
}
.EmoticonBox {
    background-color: #111;
    border: transparent;
}
.EmoticonContainer{
    background: #111;
}
.EmotifyDropdownActive {
    background-color: transparent;
}
.PanelCategories li:first-child, .PanelCategories li:nth-last-child(2)
  {
    border-bottom: transparent;
  }
.style_card_long,.line_seperator {
    box-shadow: inset 0 -1px 0 0 #111;
}  
#footer,.edit-setting {
    background-color: #111 !important;
}
#copyrights,.navigation_title {
    color: #b4c0cc !important;
}
a,.FlyoutMenu a {
    color: #8ca7d1;
    text-decoration: none;
}
.FlyoutMenu a:hover {
    color: cyan;
}
.Pager .Highlight {
    color: #6cd4ff;
}
#overlay-info,#header {
    background-color: #111;
}
#nav-view,#navigation {
    background-color: #111;
}
.linksBox .navigation_link:last-child {
    box-shadow: 0 1px 0 0 #111, 0 0 0 0 #111;
}
.navigation_link:hover {
  background-color: #000;
  box-shadow: 0 -1px 0 0 #000, 0 1px 0 0 #000;
}
.div-arrow:hover {
    background-color: #2c6880;
  }
  .screenshot_wrapper {
    border-bottom: 1px solid #000;
}
#pages a:hover{
    color: lightblue !important;
}
  h4 a {
    color: #3ab8da !important;
  }
    h4 a:hover {
    color: #3ab8da !important;
  }

  h1{
    font-size: 25px;
    color: #ababab !important;
  }
  .style_card,.current,#style-settings select {
    background-color: #000;
  }
  .Message{
    color: #a2a2a2 !important;;
  }
  #search_input input{
    color: grey !important;
  }
  
  #infomation_value_left {
    color: #8cc5ff;
  }
  #headline {
    color: #5d53ff;
  }
  #information_key_left {
    color: #567ea5;
  }
  #information_value {
    color: #489e88;
  }
  #information_key {
    color: #8bc093;
  }
    .text_style_page {
    color: #567ea5;
  }
  #notes_textarea {
    background-color: #111;
    color: #567ea5;
  }
#share_div {
    color: #88939f;
  }
  #applies_link {
    color: #cff5fe;
  }
  #pages a, #pages a:active, #pages a:visited {
    color: #7a8fa3;
}
  .div-arrow {
    background-color: #000;
    box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.08);
    border: solid 1px #2577ff;
  }
#send_feedback_button {
    border: solid 1px #87c3ff;
    background-color: #000;
    box-shadow: 0 1px 2px 0 #000;
    color: #87c3ff;
  }
  #send_feedback_button:hover {
    border: solid 1px #87c3ff;
    background-color: #2c6880;
    box-shadow: 0 1px 2px 0 #87c3ff;
    color: #87c3ff;
  }
  .css_button {
    background-color: #000;
    border: solid 1px #87c3ff;
    color: #87c3ff;
  }
  .css_button:hover {
    background-color: #2c6880;
  }
  .advanced_button {
    background-color: #000;
    color: #87c3ff;
    border: solid 1px #87c3ff;
  }
  .advanced_button:hover,.customize_button:hover {
    background-color: #2c6880;
  }
  .advancedsettings_shown {
    background-color: #222;
  }
  .title_setting {
    color: #b0b9c2;
  }
  .white_button {
    background-color: #000;
    color: #87c3ff;
    border: solid 1px #87c3ff;
  }
   .white_button:hover {
   background-color: #2c6880;
  }
  #style_installed_button {
    background-color: #000;
    border: solid 1px #2ecc71;
    color: #2ecc71;
  }
  #install_style_button {
    background-color: #000;
    border: solid 1px #3498db;
  }
  #update_style_button,#install_button {
    background-color: #000;
    border: solid 1px #2ecc71;
    color: #2ecc71;
  }
  #update_style_button:hover,#install_button:hover {
    background-color: #2ecc71;
    border: solid 1px #2ecc71;
    color: white;
  }
  .see_more {
    background-color: #000;
    color: #87c3ff;
    border: solid 1px #87c3ff;
  }
    .see_more:hover {
 background-color: #2c6880;
  }
  .view_details {
    background-color: #000;
    color: #87c3ff;
    border: solid 1px #87c3ff;
  }
    .view_details:hover {
    background-color: #2c6880;
  }
  #title_long a {
    color: #3ab8da !important;
  }
  .style_category_card_wrapper {
    background-color: #000;
  }
  #category_style_card_title {
    color: #39cfe7;
  }
    #category_style_card_title:hover {
    color: white;
  }
  .errorExplanation{
    background-color: #000;
  }
   textarea {
    background-color: #111;
    color: #a2a2a2 !important;

}
   input {
    background-color: #111;
    color: #8c8c8c !important;
    border: solid 1px lightblue;
}
#title_long a:visited {
    color: #428277 !important;
}
 code, pre {
    background: #111;
    border: 1px solid #5aa3ac;
  }
  input.DateBox, input.InputBox, input.SmallInput, textarea {
    color: #9b9b9b;
    background: #111;
  }
  input.SmallInput:focus, input.InputBox:focus, textarea:focus {
    background: #111;
    border: solid 1px grey;
  }
  #search_input{
    background-color: #111 !important;
    border: solid 1px grey;
  }
  
    #search_input:hover{
    border: solid 1px #3498db;
  }

.option-user-url {
    background-color: #000;
    border: solid 1px #000;
}
  #single_discussion {
    box-shadow: 0 1px 0 0 #111, 0 0 0 0 #dee0e3;
  }

  .Tag{
    background: #000;
    color: #A6F0DE;
  }
  
  .image-shown,.screenshot_img{
    filter:opacity(70%);transition: 0.5s;
  }
  
  .image-shown:hover,.screenshot_img:hover
  {filter:opacity(100%);transition: 0.5s;}
  
  .navigation_link_active
  {border-left: transparent !important;}
  
  td,th{
    border: transparent !important;
  }
  
  .HasNew{
    background: #000;
    color: #edee2f;
  }
  #stylish-code{
    background-color: #222; border: transparent !important;
  }
  #style-settings select{
    border: transparent !important;color: lightgrey !important;
  }
  
  .Flyout::before, .Flyout::after
  {display: none !important;}

  .Preview{
    background: #222;
    color: #edee2f;  
  }
  
  #title_div
  {box-shadow: none !important;}
  
  #top_android_button,.android_button_banner
  {display: none !important;}
  
  .customize_button
  {background-color: #000;color: #87c3ff;border: solid 1px #87c3ff;}
  
  
}
