.movemenu button {
	float: left;
	display: block;
	width: 140px;
	margin-right: 0px;
	border-radius: 0px;
	height: 50px;
  	font-family:"bebas neue" !important;
	position: relative;
	padding:  6px 4px 0 4px;
    transform:skewX(-20deg);
}
.movemenu button {
	position: relative;
	outline: none;
	cursor: pointer;
	text-align: center;
	text-decoration: none;
	text-shadow: 0 1px 0 rgba(255,255,255,.4);
    right:-30px;
	border-radius: 0px;
	margin-top: 0px;
  	padding-top:13px;
	-webkit-box-shadow: 0 1px 2px rgba(0,0,0,0), inset 0 -1px 2px rgba(255,255,255,0);
	-moz-box-shadow: 0 1px 2px rgba(0,0,0,0), inset 0 -1px 2px rgba(255,255,255,0);
	box-shadow: 0 1px 2px rgba(0,0,0,0), inset 0 -1px 2px rgba(255,255,255,0);

	/* default colors */
	color: white;
  	font-size:16pt;
  	text-shadow:none;
	border: solid 3px #AAAAAA;
}
.movemenu button small {
	color: #777777;
}
.movemenu button small.type {
  	padding-top:3px;
	float: left;
	font-size: 14pt;
    transform:skewX(20deg) translate(15px,-57px);
}
.movemenu button small.pp {
	padding-top: 3px;
    font-weight: normal;
	float: right;
	font-size: 10pt;
