css:
@-moz-document regexp(".*\.atlassian\.net.*RapidBoard.*") {
 
  /* make top row as small as possible */
  
  span.subnav-container {
    visibility: hidden; 
  } 
  
  #ghx-board-name {
    font-weight: bold;
    color: deeppink;
  }
  
  #ghx-header, .pre-adg3 #ghx-header {
    padding: .5em 0px 0px 20px;
  }
 
  #ghx-header h1, #ghx-header h2 {
    font-size: 0px;
  }
  
  .ghx-controls-filters dt,
  .ghx-quick-content.aui-expander-content {
    margin: 0px;
    padding: 0px;
    min-height: 30px;
  }
  
  .ghx-controls-work,
  .ghx-controls-filters dt{
    min-height: 0px;
  }
  
  ul#ghx-column-headers, 
  li.ghx-column {
    padding: 0px !important;
  }
  
  .pre-adg3 #ghx-pool {
    padding: 20px 10px 0;
  }
  
}
