@-moz-document domain("barter.vg") {
@keyframes notification_flash {
    0% {
        background-color: #5c7e10;
    }
    20% {
        background-color: #5c7e10;
    }
    50% {
        background-color: #86b71a;
    }
    80% {
        background-color: #5c7e10;
    }
    100% {
        background-color: #5c7e10;
    }
}
