body,
header,
footer,
#logolink,
#bc,
#listEdit,
#q,
textarea {
    background-color: #212121;
}
body {
    font-family: 'Open Sans', sans-serif;
}
aside strong {
    color: #cacaca;
}
h2.bborder {
    background: none;
    border: 1px solid white;
}
h2.pborder {
    background: none;
    border: 1px solid white;
}
.navs abbr {
    background-color: #5c7e10;
    animation: notification_flash 3s ease-in-out infinite
}
.pborder sup,
.pborder span,
#menu .pborder,
#bc.pborder li:last-child:first-letter,
#bc.pborder li:first-child:first-letter {
    color: #777;
}
.icon {
    border: none;
    border-radius: 5px;
}
aside div {
    background-color: #3e3e3e;
    width: 560px;
    padding-left: 10px;
    border-radius: 10px;
    color: #929292;
}
h1 a {
    font: 300 30px 'Open Sans', sans-serif
}
input[type=submit],
button[type=submit] {
    border: 2px solid white;
    background: white;
    border-radius: 10px;
    border-bottom-left-radius: 0px;
    border-top-left-radius: 0px;
    margin-left: 0px!important;
    padding: .22em 0;
}
.nav-sel {
    background: rgba(195, 195, 195, 0.2);
    border: none;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
}
input[type=text],
input[type=url],
#q {
    border: 2px solid white;
    background: #c1c1c1;
    border-radius: 10px;
    border-right: 0px;
    border-top-right-radius: 0px;
    border-bottom-right-radius: 0px;
    padding-left: 10px;
}
#q:focus {
    outline: 0;
    border-radius: 10px;
    border-right: 0px;
    border-top-right-radius: 0px;
    border-bottom-right-radius: 0px;
    padding-left: 10px;
    border: 2px solid white;
}
#search:focus,
#search:active {
    background-color: black;
}
.statusCurrent {
    background-color: mediumseagreen;
    border: 1px solid white;
    border-radius: 3px;
}
#offerStatus {
    border: none;
}
#offerStatus div {
    width: 24.9%;
}
h2.pborder {
    border-left: none;
    border-right: none;
    border-bottom: none;
}
.addTo {
    background: none!important;
    border: none!important;
}
select {
    border: 2px solid #6d6b6b;
    padding: .22em 0;
    border-radius: 15px;
}
a {
    color: #b5fdae;
}
a:hover {
    color: #7bca6e
}
a:visited {
    color: #7bca6e
}
#offers th {
    color: #9e9e9e;
    border-bottom: .08em solid #585858;
}
abbr[title] {
    border: none;
}
.center {
    border: none;
}
h2.oborder {
    background-color: rgba(1, 1, 1, 0);
    border-top: 1px solid #e50;
}
h2.gborder {
    background-color: rgba(1, 1, 1, 0);
    border-top: 1px solid #aa8fff;
}
h2.bborder {
    background-color: rgba(1, 1, 1, 0);
    border: none;
    border-top: 1px solid white;
}
.tag.tag_notices {
    border-radius: 10px;
    border-width: 1px;
}
.tag.tag_givenaway {
    color: #fff;
    border: 1px solid white;
    border-radius: 10px;
}
.tag.tag_warn_h {
    border: 1px solid #f00;
    border-radius: 10px;
}
.tag,
.tag.tag_warn_l,
.tag.tag_warn_m {
    border: 1px solid #4e4e4e;
    border-radius: 10px;
}
.tradables {
    border: .07em solid #888;
}
input[type="radio"] {
    background-color: #7b7272;
    border-radius: 10px;
    cursor: pointer;
    display: inline-block;
    height: 15px;
    margin-right: 0px;
    position: relative;
    width: 15px;
    -webkit-appearance: none;
    margin-bottom: 25px;
}
input[type="radio"]:checked {
    border: 3px solid white;
    background-color: black;
}
.tradables_items_list label img {
    border: none;
}
.notincluded > img {
    filter: blur(2px);
    transition: 0.5s;
    border: none;
}
#offer input:checked + label > img {
    filter: blur(0px);
    border: none;
    transition: 0.5s;
    cursor: pointer;
}
#exchanges input[type=checkbox]:disabled:after,
#exchanges input[type=radio]:disabled:after {
    padding-left: 2.5px;
}
.tradables li {
    padding-left: 10px;
}
::-webkit-input-placeholder {
    color: #676767;
}
#search {
    border: 2px solid #aaa;
    border-left: none;
}
.offerHistory span {
    border: 2px solid #7d7d7d;
    background-color: #464646;
}
input[type="checkbox"] {
    background-color: #7b7272;
    border-radius: 10px;
    cursor: pointer;
    display: inline-block;
    height: 15px;
    margin-right: 0px;
    position: relative;
    width: 15px;
    -webkit-appearance: none;
    margin-bottom: -2px;
}
input[type="checkbox"]:checked {
    border: 3px solid white;
    background-color: black;
}
li.bold {
    text-align: center;
}
.tradables_items_list label {
    cursor: pointer;
}
#exchanges input[type=radio],
#exchanges input[type=checkbox] {
    display: none;
}
input.showMoreToggle {
    display: none;
}
h1,
h2,
p,
h4,
footer,
.center {
    color: white;
}
#signinsteam {
    border: 4px solid floralwhite;
    box-shadow: none;
    border-radius: 7px;
}
a#logolink.pborder {
    background-image: url(http://i.imgur.com/RlYxUan.png);
    color: rgba(0, 0, 0, 0);
    height: 30px;
    width: 100px;
    background-size: 100px 30px;
}
.icon {
    vertical-align: middle;
}
body,
h1 a,
h1 a:visited,
nav a,
nav a:visited,
.actions a,
.actions a:visited,
.active,
#bc a,
#bc a:visited,
#profileStats a,
aside strong,
h2,
#q,
.addFrom input,
textarea,
.offer_message {
    color: #dcdcdc;
}
.collection tr:hover td,
#offers tr:hover td {
    background: rgba(255, 255, 255, 0.3);
    transition: 0.2s ease;
}
tr:nth-child(even) {
    background: none;
}
tr:nth-child(even) {
    background: #383838;
}
::-webkit-input-placeholder {
    color: white;
}
.bborder sup,
.bborder span,
th.filterTradable span,
#menu .bborder,
#bc.bborder li:last-child:first-letter,
#bc.bborder li:first-child:first-letter {
    color: #7d7d7d;
}
main,
#main,
#menu {
    max-width: 49em;
}
.collection td {
    padding-left: .5em;
    padding-right: .5em;
}
.bborder,
.platform.bborder th,
.collection th.filterTradable,
.mh {
    border-color: white;
}
.sortAsc {
    border-bottom: none;
}
}
