css:
@-moz-document url-prefix("http://llamalab.com/weblate/"), url-prefix("http://www.llamalab.com/weblate/"), domain("translations.vivaldi.com"), domain("hosted.weblate.org"), domain("demo.weblate.org") {
/* Dark Comfort for Weblate by R3gi, Czech Republic
Version: 2020.04.17.23.40 */

/* NO SHADOW */
.list-group,
.kbd-badge,
.badge,
.alert,
.btn-default, .btn-primary, .btn-success, .btn-info, .btn-warning, .btn-danger {
    box-shadow: none;
}

/* NO BORDER */
.list-group-item {
    border: none;
}

/* NO BORDER */
.panel-footer,
.table > tbody + tbody.zen-unit,
.table > tbody > tr > th,
.table > tbody > tr > td {
    border: none;
}

/* NO BORDER RADIUS */
.panel,
.panel-heading,
.panel-footer,
.breadcrumb,
.list-group-item,
.list-group-item:first-child,
.list-group-item:last-child,
.form-control,
.btn,
.pagination,
.pagination > li:first-child > a, .pagination > li:first-child > span,
.pagination > li:last-child > a, .pagination > li:last-child > span,
.nav-pills > li > a,
.dropdown-menu,
.progress,
pre,
.alert,
.kbd-badge,
.badge,
.btn-xs, .btn-group-xs>.btn,
.multi-wrapper {
	border-radius: 0;
}

/* REGULAR TEXT COLOR */
body,
a,
.pagination > li > a, .pagination > li > span,
.help-block,
.dropdown-menu > li > a,
legend,
.btn-link,
footer li, 
footer a,
.ct-label,
.navbar-inverse .navbar-nav > li > a,
.helptext,
a.list-group-item, button.list-group-item,
.fa-github,
.fa-google,
.ct-chart .ct-label, .ct-chart .ct-label.ct-vertical, .ct-chart .ct-label.ct-horizontal,
.text-primary {
    color: #E0E0E0;
}

/* LIGHT TEXT COLOR */
a:hover, a:focus,
.pagination > li > a:hover, .pagination > li > span:hover, .pagination > li > a:focus, .pagination > li > span:focus,
.btn-link:hover, .btn-link:focus,
.navbar-inverse .navbar-brand,
.badge,
.list-group-item.active > .badge, .nav-pills > .active > a > .badge,
input.search-input::placeholder,
.multi-wrapper .header,
.object-link, .object-link a,
.panel-default>.panel-heading,
.btn-group-settings a {
	color: #FAFAFA;
}

.breadcrumb a {
	color: #FAFAFA !important;
}

.btn-link path,
.green path, .btn-default path,
.panel-heading .doc-link path,
.state-empty path,
.state-icon path {
    fill: #FAFAFA;
}


/* NO TEXT SHADOW */
.btn-default {
    text-shadow: none;
}

/* DARK BORDER COLOR */
.form-control:focus,
.panel-primary,
.btn-primary, .btn-primary:hover, .btn-primary:active,
.breadcrumb,
.nav-pills>li>a:hover, .nav-pills>li>a:focus, .nav-pills .open>a, .nav-pills .open>a:hover, .nav-pills .open>a:focus,
.nav-pills>.active>a, .nav>.active>a:hover,
.btn-info,
.btn-info:hover,
.panel-default>.panel-heading,
.panel-default {
    border-color: #1F282D;
}

/* BORDER COLOR */
.panel-primary > .panel-heading,
.form-control,
.btn-default,
.pagination > li > a, .pagination > li > span,
.pagination > li > a:hover, .pagination > li > span:hover, .pagination > li > a:focus, .pagination > li > span:focus,
.pagination > .disabled > span, .pagination > .disabled > span:hover, .pagination > .disabled > span:focus, .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus,
.zen.table > thead > tr > th, .zen.table > tbody > tr > th, .zen.table > tfoot > tr > th, .zen.table > thead > tr > td, .zen.table > tbody > tr > td, .zen.table > tfoot > tr > td,
.table > thead > tr > th,  .table > tfoot > tr > th, .table > thead > tr > td, .table > tfoot > tr > td,
.dropdown-menu,
.form-control,
legend,
pre,
.table > tbody + tbody,
.navbar-inverse,
.panel>.panel-body + .table, .panel>.panel-body + .table-responsive, .panel>.table + .panel-body, .panel>.table-responsive + .panel-body,
.btn-success, .btn-warning, .btn-info,
.multi-wrapper,
.multi-wrapper .search-input,
.multi-wrapper .non-selected-wrapper,
.table-border,
.history-row + .history-row>div,
.table-bordered {
    border-color: #37474F;
}

.ct-grid {
    stroke: #546E7A;
}

/* DARKEST BACKGROUND COLOR */
body,
.zen-unit .translatetext,
.multi-wrapper .selected-wrapper,
.multi-wrapper .non-selected-wrapper {
    background-color: #263238;
}

/* DARKER BACKGROUND COLOR */
.table-striped > tbody > tr:nth-of-type(odd),
.dropdown-menu .divider,
.stripe-odd,
.list-group-item,
.comment-content {
    background-color: #37474F;
}

/* DARK BACKGROUND COLOR */
.panel,
code,
.translatetext,
.navbar-inverse,
.list-group-item.active > .badge, .nav-pills > .active > a > .badge,
.badge,
.panel-footer,
.zen-unit:nth-child(even),
.zen-unit:nth-child(even) .translatetext,
.badge.length, .badge.license,
.current_translation,
.btn-float {
    background-color: #455A64;
}

.navbar-inverse {
	background-image: linear-gradient(to bottom, #455A64 0%, #455A64 100%);
}

/* FRONT BACKGROUND COLOR */
.panel-primary > .panel-heading,
.breadcrumb,
.pagination > li > a, .pagination > li > span,
.dropdown-menu,
.multi-wrapper .search-input,
.panel-default>.panel-heading {
    background-color: #607D8B;
}

.panel-primary > .panel-heading,
.pagination > li > a, .pagination >.panel-default>.panel-heading  li > span {
	background-image: linear-gradient(to bottom, #607D8B 0%, #607D8B 100%);
}

/* HOVER and FOCUS BACKGROUND COLOR */
.nav > li > a:hover, .nav > li > a:focus,
.pagination > li > a:hover, .pagination > li > span:hover, .pagination > li > a:focus, .pagination > li > span:focus,
.dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus,
.nav-pills > li.active > a, .nav-pills > li.active > a:hover, .nav-pills > li.active > a:focus,
.dropdown-menu > .active > a, .dropdown-menu > .active > a:hover, .dropdown-menu > .active > a:focus,
.navbar-inverse .navbar-nav > .open > a, .navbar-inverse .navbar-nav > .open > a:hover, .navbar-inverse .navbar-nav > .open > a:focus,
a.list-group-item:hover, button.list-group-item:hover, a.list-group-item:focus, button.list-group-item:focus,
.nav .open > a, .nav .open > a:hover, .nav .open > a:focus,
.multi-wrapper .item:hover,
.clickable-row:hover,
.clickable-row:hover a {
    background-color: #78909C !important;
    background-image: linear-gradient(to bottom, #78909C 0%, #78909C 100%);
    color: #FFFFFF;
}

/* BUTTON DEFAULT */
.btn-default {
	background-color: #546E7A;
    background-image: linear-gradient(to bottom, #546E7A 0%, #546E7A 100%);
    border-color: #607D8B;
    color: #FAFAFA;
}

/* BUTTON HOVER and FOCUS */
.btn-default:hover, .btn-default:focus {
    background-color: #78909C;
    background-image: linear-gradient(to bottom, #78909C 0%, #78909C 100%);
    color: #FFFFFF;
    text-shadow: 0.5px 0.5px 0 #546E7A;
    border-color: #90A4AE;
    background-position: 0;
    outline: none;
}

/* BUTTON ACTIVE */
.btn-default:active,
.btn-default:active:hover, .btn-default.active:hover, .open > .dropdown-toggle.btn-default:hover, .btn-default:active:focus, .btn-default.active:focus, .open > .dropdown-toggle.btn-default:focus, .btn-default:active.focus, .btn-default.active.focus, .open > .dropdown-toggle.btn-default.focus
{
    background-color: #546E7A;
    background-image: linear-gradient(to bottom, #546E7A 0%, #546E7A 100%);
    color: #FFFFFF;
    border-color: #607D8B;
}

/* DISABLED ITEM */
.pagination > .disabled > span, .pagination > .disabled > span:hover, .pagination > .disabled > span:focus, .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus {
    color: #BDBDBD;
    background-color: #546E7A;
    background-image: linear-gradient(to bottom, #546E7A 0%, #546E7A 100%);
}

/* FORM BACKGROUND */
.form-control,
pre,
input:not(.search-input):not(.btn), button, select, textarea {
    background-color: #E0E0E0;
    color: #424242;
}

input, button, select, textarea {
    outline: none;
    border: 1px solid #37474F;
}

input:hover, select:hover, textarea:hover,
input:focus, select:focus, textarea:focus {
    background-color: #FAFAFA;
    color: #000000;
    transition-duration: 0.3s;
}

/* ORANGE */
a.list-group-item-warning, button.list-group-item-warning,
.list-group-item-text {
    color: #FFC107;
}

a.list-group-item-warning:hover, button.list-group-item-warning:hover, a.list-group-item-warning:focus, button.list-group-item-warning:focus,
.alert-info, .alert-info .alert-link,
.hlmatch {
    color:  #FFC107;
    background-color: #3E2E01;
    background-image: linear-gradient(to bottom, #3E2E01 0%, #3E2E01 100%);
}

.alert-info {
	border-color: #3E2E01;
}

/* YELLOW */
.alert-warning,
.warning {
    background-color: #FFEB3B;
    background-image: linear-gradient(to bottom, #FFEB3B 0%, #FFEB3B 100%);
    border-color: #FFEB3B;
    color: #000000;
}

/* RED */
a.list-group-item-danger, button.list-group-item-danger,
code,
.text-danger {
    color: #F44336;
}

a.list-group-item-danger:hover, button.list-group-item-danger:hover, a.list-group-item-danger:focus, button.list-group-item-danger:focus,
del {
    color: #B71C1C;
    background-color: #2B1817;
    background-image: linear-gradient(to bottom, #2B1817 0%, #2B1817 100%);
}

.alert-danger,
.panel-danger > .panel-heading {
	color: #FFFFFF;
    background-color: #B71C1C;
    background-image: linear-gradient(to bottom, #C62828 0%, #B71C1C 100%);
    border-color: #C62828;
    text-shadow: 0 0 1px #000000;
}

.panel-danger {
    border-color: #C62828;
}

/* BLUE */
a.list-group-item-info, button.list-group-item-info,
.text-info {
    color: #2196F3;
}

a.list-group-item-info:hover, button.list-group-item-info:hover, a.list-group-item-info:focus, button.list-group-item-info:focus,
.hlcheck {
    color: #2196F3;
    background-color: #022038;
    background-image: linear-gradient(to bottom, #022038 0%, #022038 100%);
}

.hlcheck {
    border-color: #2196F3;
}

/* GREEN */
a.list-group-item-success, button.list-group-item-success,
.text-success {
    color: #4CAF50;
}

a.list-group-item-success:hover, button.list-group-item-success:hover, a.list-group-item-success:focus, button.list-group-item-success:focus,
ins {
    color: #4CAF50;
    background-color: #023A04;
    background-image: linear-gradient(to bottom, #023A04 0%, #023A04 100%);
    
}

.alert-success {
	color: #FFFFFF;
    background-color: #4CAF50;
    background-image: linear-gradient(to bottom, #286F2B 0%, #1B5E20 100%);
    border-color: #2E7D32;
    text-shadow: 0 0 1px #000000;    
}

.ct-chart .ct-series.ct-series-a .ct-bar {
    stroke: #4CAF50
}

/* MISC FIXES */
.table>thead>tr>th {
    border-bottom-width: 1px;
}
}
