.dashboard .dashboard__menu {
	width: 75px;
	padding-top: 20px;
	background-color: #3e3f43
}

.dashboard .dashboard__menu ul li {
	position: relative
}

.dashboard .dashboard__content {
	padding-top: 30px;
	margin-left: 60px
}

.dashboard.open .dashboard__content {
	margin-left: 60px
}

.dashboard .dashboard__menu .menu__item>a {
	font-size: 14px
}

.dashboard .dashboard__menu .menu__ico {
	font-size: 35px;
	margin-bottom: 5px
}

.dashboard .dashboard__menu .menu__ico.jc-icon_crm {
	font-size: 35px
}

.dashboard .dashboard__menu .menu__ico.jc-icon_shop {
	font-size: 35px
}

.dashboard .dashboard__menu .menu__item .menu__item__sub {
	bottom: initial;
	opacity: .97;
	display: none;
	padding-top: 0
}

.dashboard .dashboard__menu .menu__close {
	display: none
}

.dashboard .dashboard__menu .menu__item:hover .menu__item__sub {
	display: block;
	position: absolute;
	-webkit-transition: 1s all;
	-o-transition: 1s all;
	transition: 1s all;
	left: 75px;
	right: initial;
	top: 0
}

.dashboard .dashboard__menu .menu__item .active {
	left: 75px;
	margin-right: 0
}

.dashboard .dashboard__menu .menu__item .menu__item__sub:hover {
	display: block;
	position: absolute;
	-webkit-transition: 1s all;
	-o-transition: 1s all;
	transition: 1s all
}

.dashboard .dashboard__menu .menu__item:nth-child(6):hover .menu__item__sub,
.dashboard .dashboard__menu .menu__item:nth-child(7):hover .menu__item__sub {
	top: initial;
	bottom: 0
}
/* Колонка "Партнеры" в разделе "Счета" всегда отображается */
td.col-partners {
    display: table-cell!important;
}
/* Делаем, чтобы кнопка для установки таймера работала нормально */
div.timer > a.disable {
    pointer-events: auto;
    cursor: pointer;
    opacity: 1;
}}
