css:
@-moz-document domain('www.speedsports.me'), url-prefix('http://www.speedsports.me/mlb/') {
.col-sm-3{ display: none; }
.col-sm-9 { width: 100%; }
.embed-responsive { max-width: 1200px; margin: 0 auto; border: 1px solid #151515; border-radius: 10px; }
body, .navbar-default .navbar-nav>.active>a, .navbar-default .navbar-nav>.active>a:hover, .navbar-default .navbar-nav>.active>a:focus { background-color: #000; color: #AAA; }
.navbar-default { background-color: #000; opacity: 0.75; }
.btn-primary { background-color: #333; border-color: #333; color: #AAA; text-shadow: -1px -1px 0px #000; }
.btn-primary:hover { background-color: #222; border-color: #222; color: #999; }
p.text-center { padding-top: 5px; }

@media screen and (max-width: 1300px) {
    .embed-responsive { max-width: 100%; }
    .container-fluid { padding-left: 0; padding-right: 0; }
}

@media screen and (min-width: 1301px) {
    .embed-responsive-16by9 {
    	padding-bottom: 680px;
	}
}
}

