css:
 @-moz-document domain("tweakers.net") {
  /*Maximale breedte opheffen van artikelen en koppen */
  #contentArea .centeredContent .article .center, #contentArea .centeredContent .article p, #contentArea .centeredContent .article > ol, #contentArea .centeredContent .article > ul, #contentArea .centeredContent .article > h1, #contentArea .centeredContent .article    > h2, #contentArea .centeredContent .article > h3, #contentArea .centeredContent .article > table, #contentArea .centeredContent .article > div, #contentArea .centeredContent .article > blockquote {
   max-width:none;
  }
  #contentArea .headingContent h1{
    max-width: none;
  }
  #contentArea .centeredContent h2.chapter{
    max-width: none;
  }
    #contentArea .headingContent{
    margin-left: 0px;
  }
  /* Maximale breedte opheffen van reacties */
  #commentColumn.centeredReactions #reacties{
    margin-left: 0px;
  }
  .reactieBody{
    max-width:none;
  }
  /*Maximale breedte reactieveld opheffen */
  #reactieForm{
    max-width: none;
  }
  /* Herplaats artikelopties naar rechterkant */
  #contentArea .centeredContent #articleMeta{
    float: right;
    position: relative;
  }
}
