css:
@-moz-document domain("ddl-warez.to") {
/*.navbar-nav {
    display: none;
}*/

body, html {
    color: #ffffff;
    background-color: #262626;
}

td {
    background-color: #262626;
}

td:hover {
    background-color: #404040;
}

hr {
    border-color: #404040;
}

.table>thead>tr>th, .table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>td, .table>tbody>tr>td, .table>tfoot>tr>td {
    border-color: #404040;
}

a {
    color: #3399ff;
}

a:hover {
    color: #4da6ff;
}

h1 {
    color: #ffffff;
}

.alert-warning {
    display: none;
}

.navbar {
	background-image: none;
    background-color: #47A4BE;
}

.list-group-item {
    background-color: #333333;
    border: 1px solid #404040;

}

a.list-group-item, .button.list-group-item {
   color: white;
}

.btn-xs {
    background-image: none;
    background-color: #333333;
    border-color: #404040;
    color: #ffffff;
}

.btn-xs:hover {
    background-color: #404040;
    color: #ffffff;
    border-color: #4d4d4d;
}

.panel, .panel-default, .panel-default>.panel-heading, table, .well {
    background-color: #262626;
    color: #ffffff;
    border-color: #404040;
}
}
