css:
@-moz-document domain("www.ixl.com") {
.site-nav-breadcrumb {
    background: #fff;
    font-size: 13px;
    line-height: 19px;
    padding: 3px 0 5px;
    text-indent: 10px;
}
.site-nav-header {
    border-bottom: 2px solid #fff;
    background-color: #4fc8ff;
    position: relative;
}
.header-tab-selected::before {
    content: "";
    height: 0;
    width: 0;
    position: absolute;
    border: 5px solid transparent;
    border-bottom-color: #fff;
    bottom: 0;
    left: 50%;
    -webkit-transform: translateX(-50%);
    transform: translateX(-50%);
}
.crisp-button {
    display: inline-block;
    zoom: 1;
    line-height: normal;
    white-space: nowrap;
    vertical-align: baseline;
    text-align: center;
    cursor: pointer;
    -webkit-user-drag: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    font-size: 15px;
    padding: .46em 1.5em .54em;
    box-sizing: border-box;
    -moz-box-sizing: border-box;
    color: #fff;
    border: 0 solid #999;
    border: none transparent;
    background-color: #4fc8ff;
    text-decoration: none;
    border-radius: 5px;
    -webkit-font-smoothing: antialiased;
    -webkit-transition: .1s linear -webkit-box-shadow;
    -moz-transition: .1s linear -moz-box-shadow;
    -ms-transition: .1s linear box-shadow;
    -o-transition: .1s linear box-shadow;
    transition: .1s linear box-shadow;
    min-width: 90px;
}
#header-tabs {
    display: -ms-inline-flexbox;
    display: -webkit-inline-flex;
    display: inline-flex;
    width: calc(100% - 20px);
    margin: 0 10px;
    border-top: 1px solid #4fc8ff;
}
.breadcrumb-link,
a.breadcrumb-link {
    color: #1fb2e4;
    font-size: 13px;
    text-decoration: none;
    position: relative;
}
.header-tab-secondary:not(:first-child)::after,
.header-tab-subject::after {
    content: "";
    height: 16px;
    width: 1px;
    background-color: #4fc8ff;
    position: absolute;
    top: calc((100% - 16px) / 2);
}
.crisp-button-hover,
.crisp-button:hover {
    color: #fff;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(transparent), color-stop(40%, rgba(0, 0, 0, .05)), to(rgba(0, 0, 0, .1)));
    background-image: -webkit-linear-gradient(transparent, rgba(0, 0, 0, .05) 40%, rgba(0, 0, 0, .1));
    background-image: -moz-linear-gradient(top, rgba(0, 0, 0, .05) 0, rgba(0, 0, 0, .1));
    background-image: -ms-linear-gradient(transparent, rgba(0, 0, 0, .05) 40%, rgba(0, 0, 0, .1));
    background-image: -o-linear-gradient(transparent, rgba(0, 0, 0, .05) 40%, rgba(0, 0, 0, .1));
    background-image: linear-gradient(transparent, rgba(0, 0, 0, .05) 40%, rgba(0, 0, 0, .1));
    background-color: #4fc8ff;
}
.header-tab:hover {
    color: #c2ecff
}
.ixl-home-page .header-tab::after {
    background-color: #4fc8ff;
}
.header-tab-secondary:not(:first-child)::after,
.he84ader-tab-subject::after {
    content: "";
    height: 16px;
    width: 1px;
    background-color: #4fc8ff;
    position: absolute;
    top: calc((100% - 16px) / 2);
}
.practice-subnav {
    height: 70px;
    background-color: #ffffff;
    border-bottom: 2px solid #ecf9ff;
}
.ixl-home-page .header-tab {
    color: #4fc8ff;
}
.skill-tree-skill-name {
    color: #38a6d8;
}
.skill-tree-body .arrow-link {
    color: #4fc8ff;
}
element.style {
    color: rgb(80, 134, 224);
}
.ixl-home-page .header-tab:hover {
    color: #c2ecff;
}
}
