css:
@-moz-document domain("beibq.cn") {
body, html, .book-container {
    background-color: white;
}
.book-container .book-content .title {
    margin: 0 -40px 25px;
    padding: 10px 40px 20px;
    border-bottom: 1px solid #e0e0e0;
}
blockquote {
    font-size: 16px;
    color: #999;
    text-shadow: 1px 1px 1px #bbb;
}
#book-catalogs,.html-content-title,.html-content, .header, .search-input-header, .search-input-header {
    font-family: "思源宋体","思源宋体 CN", "Hiragino Sans GB", "Heiti SC", "Microsoft YaHei", "WenQuanYi Micro Hei";
}
#book-catalogs {
	padding-top: 10px;
}
.html-content-title {
    font-size: 34px;
    color: #333;
    margin: 0;
}
.search-input-header {
	padding: 1px 27px 3px 12px;
}
.reader-catalog {
    padding: 5px 5px 5px 20px;
    color: #DDD;
}
.header-nav>li>a {
    font-size: 18px;
    line-height: inherit;
    max-width: 300px;
}
.btn-new-book {
    margin-left: 15px;
}
.book-header .header-nav .btn-new {
    padding: 5px 15px 7px;
}
.author-name {
    color: #DDD;
    font-family: 'SimSun', serif;
    font-weight: normal;
}
.book-container .book-catalog, .book-container .book-catalog .catalog-head {
	background-color: #666;
}
.html-content {
    color: #333;
    overflow: hidden;
    line-height: 2;
    word-wrap: break-word;
}
.book-content .content-body {
    border: none;
}
.book-title, .book-container .book-catalog .catalog-head {
    border: none;
}
.book-title .title {
    color: #fff;
    font-weight: bold;
    font-size: 120%;
}
}
